import { useState, useRef, useEffect } from "react";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Bot, Bold, Italic, Link as LinkIcon, Image, Wand2, Target, Palette, 
  Building2, MessageSquare, Megaphone, Sparkles, Type, ImagePlus, Video,
  Camera, Globe, Brush, Sun, Upload, Play, Trash2, Send, Clock, Film,
  Music, Mic, Aperture, Zap, Layers, Monitor, Eye, Copy, FileText, User as UserIcon
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import PlatformSelector from "../components/content/platform-selector";
import AiSuggestions from "../components/content/ai-suggestions";
import type { User } from "@shared/schema";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";

export default function CreateContent() {
  const [location, navigate] = useLocation();
  const urlParams = new URLSearchParams(location.split('?')[1] || '');
  const typeParam = urlParams.get('type');

  const [content, setContent] = useState("");
  const [selectedPlatforms, setSelectedPlatforms] = useState<string[]>(["Instagram", "Facebook"]);
  const [scheduleOption, setScheduleOption] = useState("approval");
  const [showAiSuggestions, setShowAiSuggestions] = useState(false);
  const [aiSuggestions, setAiSuggestions] = useState<string[]>([]);


  // Content type selection - initialize from URL param if present
  const [contentType, setContentType] = useState(typeParam || "text-image");

  // Business/Personal mode toggle
  const [isBusinessMode, setIsBusinessMode] = useState(true);

  // Enhanced AI input fields
  const [businessName, setBusinessName] = useState("");
  const [productName, setProductName] = useState("");
  const [targetAudience, setTargetAudience] = useState("");
  const [brandTone, setBrandTone] = useState("professional");
  const [keyMessages, setKeyMessages] = useState("");
  const [callToAction, setCallToAction] = useState("");
  const [isAdvertisement, setIsAdvertisement] = useState(true);
  const [additionalContext, setAdditionalContext] = useState("");

  // Image generation fields
  const [visualStyle, setVisualStyle] = useState("modern");
  const [colorScheme, setColorScheme] = useState("");
  const [imagePrompt, setImagePrompt] = useState("");
  const [cameraAngle, setCameraAngle] = useState("eye-level");
  const [environment, setEnvironment] = useState("studio");
  const [lighting, setLighting] = useState("natural");
  const [mood, setMood] = useState("bright");
  const [composition, setComposition] = useState("rule-of-thirds");
  const [objectFocus, setObjectFocus] = useState("");
  const [aspectRatio, setAspectRatio] = useState("16:9");
  const [imageResolution, setImageResolution] = useState("1080p");
  const [imageModel, setImageModel] = useState("gemini"); // Gemini Imagen 4 or OpenAI DALL-E 3

  // Video generation fields
  const videoDuration = "8"; // Fixed 8 seconds for Veo 3
  const [videoStyle, setVideoStyle] = useState("cinematic");
  const [videoTransitions, setVideoTransitions] = useState("smooth");
  const [videoMusic, setVideoMusic] = useState("upbeat");
  const [videoVoiceover, setVideoVoiceover] = useState("none");
  const [videoPacing, setVideoPacing] = useState("medium");
  const [videoEffects, setVideoEffects] = useState("minimal");
  const [videoFrameRate, setVideoFrameRate] = useState("30fps");
  const [videoResolution, setVideoResolution] = useState("1080p");
  const [videoAspectRatio, setVideoAspectRatio] = useState("16:9");
  const [videoScenes, setVideoScenes] = useState("");
  const [videoScript, setVideoScript] = useState("");
  const [videoModel, setVideoModel] = useState("gemini"); // Gemini Veo 3

  // Preview states
  const [generatedImage, setGeneratedImage] = useState<string | null>(null);
  const [generatedVideo, setGeneratedVideo] = useState<string | null>(null);
  const [uploadedImage, setUploadedImage] = useState<string | null>(null);
  const [uploadedVideo, setUploadedVideo] = useState<string | null>(null);
  const [generatedImageCaption, setGeneratedImageCaption] = useState<string>("");
  const [generatedVideoCaption, setGeneratedVideoCaption] = useState<string>("");

  // Caption control states
  const [autoGenerateImageCaption, setAutoGenerateImageCaption] = useState(true);
  const [autoGenerateVideoCaption, setAutoGenerateVideoCaption] = useState(true);
  const [manualImageCaption, setManualImageCaption] = useState("");
  const [manualVideoCaption, setManualVideoCaption] = useState("");
  const [imageCaptionStyle, setImageCaptionStyle] = useState("engaging");
  const [videoCaptionStyle, setVideoCaptionStyle] = useState("engaging");

  // File input refs
  const imageInputRef = useRef<HTMLInputElement>(null);
  const videoInputRef = useRef<HTMLInputElement>(null);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get current user data for video eligibility checking
  const { data: user } = useQuery<User>({
    queryKey: ["/api/user"],
  });

  // Video eligibility logic - bulletproof implementation
  const checkVideoEligibility = () => {
    if (!user) return { allowed: false, reason: "Loading user data..." };

    // Check if user is on Lite trial (nocard7) with insufficient credits
    if (user.trialVariant === "nocard7") {
      const hasTrialVideos = (user.trialVideosRemaining || 0) > 0;
      const hasSufficientCredits = (user.credits || 0) >= 20; // Video costs 20 credits

      if (!hasTrialVideos && !hasSufficientCredits) {
        return {
          allowed: false,
          reason: "Video generation requires 20 credits or upgrade to Pro trial",
          action: "upgrade"
        };
      }
    }

    // For paid users, check if they have enough credits
    if (user.tier !== "free_trial" && (user.credits || 0) < 20) {
      return {
        allowed: false,
        reason: "Video generation requires 20 credits",
        action: "buy_credits"
      };
    }

    return { allowed: true, reason: "" };
  };

  const videoEligibility = checkVideoEligibility();

  // Update content type when URL param changes
  useEffect(() => {
    if (typeParam && ['text', 'text-image', 'text-video'].includes(typeParam)) {
      setContentType(typeParam);
    }
  }, [typeParam]);


  const createPostMutation = useMutation({
    mutationFn: async (postData: any) => {
      return apiRequest("POST", "/api/posts", postData);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: scheduleOption === "approval" 
          ? "Your post has been sent to the approval queue!" 
          : "Your post has been created successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/posts/pending"] });
      // Reset form
      resetForm();
      // Reset to approval queue after successful submission
      setScheduleOption("approval");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create post. Please try again.",
        variant: "destructive",
      });
    },
  });

  const generateAiContentMutation = useMutation({
    mutationFn: async (params: any) => {
      const response = await apiRequest("POST", "/api/ai/text", {
        prompt: `Create a ${params.brandTone || 'professional'} social media post for ${params.platform || 'Instagram'} about: ${params.businessName} ${params.productName || ''} - ${params.callToAction || 'promotion'}. Include ${params.includeHashtags ? 'hashtags' : 'no hashtags'} and ${params.includeEmojis ? 'emojis' : 'no emojis'}.`,
        system: "You write punchy, on-brand social media captions.",
        temperature: 0.9,
        maxOutputTokens: 2048
      });
      return response.json();
    },
    onSuccess: (data) => {
      if (data.text) {
        setContent(data.text);
      }
      toast({
        title: "Content Generated",
        description: "AI has created optimized content for your platforms",
      });
    },
    onError: (error: any) => {
      console.error('AI generation error:', error);
      toast({
        title: "Error",
        description: error?.message || "Failed to generate AI content. Please try again.",
        variant: "destructive",
      });
    },
  });

  const generateImageMutation = useMutation({
    mutationFn: async (params: any) => {
      // Prioritize subject matter (productName) above all other parameters
      let prompt = params.imagePrompt;

      if (!prompt) {
        const subjectMatter = params.productName || '';
        const businessName = params.businessName || '';
        const visualStyle = params.visualStyle || 'modern';
        const environment = params.environment || '';
        const mood = params.mood || '';

        if (subjectMatter.trim()) {
          // Subject matter is the primary focus
          prompt = `${subjectMatter} in ${visualStyle} ${environment || 'setting'}, ${mood || 'bright'} mood`;

          // Add business context if provided, but as secondary
          if (businessName.trim()) {
            prompt += `, featuring ${businessName} branding`;
          }

          prompt += ', photoreal, professional quality';
        } else if (businessName.trim()) {
          // Fallback when only business name is provided
          prompt = `${businessName} ${visualStyle} ${environment || 'studio'} ${mood || 'bright'}, photoreal`;
        } else {
          // Default when neither is provided
          prompt = `${visualStyle} ${environment || 'studio'} ${mood || 'bright'} scene, photoreal`;
        }
      }

      const aspectRatioMap: Record<string, string> = {
        "Instagram": "1:1",
        "Facebook": "16:9",
        "X.com": "16:9",
        "TikTok": "9:16",
        "LinkedIn": "16:9"
      };

      // Show initial toast
      toast({
        title: "Generating Image",
        description: "Creating your custom image, this may take a few moments...",
      });

      // Add slight delay to show loading state
      await new Promise(r => setTimeout(r, 500));

      const response = await apiRequest("POST", "/api/ai/image", {
        prompt: prompt.trim(),
        aspectRatio: aspectRatioMap[selectedPlatforms[0]] || "1:1",
        count: 1,
        model: imageModel, // Add model selection (gemini or openai)
        // Send all context fields for caption generation
        businessName,
        productName,
        targetAudience,
        brandTone,
        keyMessages,
        callToAction,
        isAdvertisement,
        additionalContext,
        manualCaption: params.autoGenerateCaption === false ? params.manualCaption : undefined,
        captionStyle: params.captionStyle
      });

      const data = await response.json();

      // Wait a bit more to ensure image is fully generated
      await new Promise(r => setTimeout(r, 1000));

      return data;
    },
    onSuccess: (data) => {
      // Handle both single image response and array response
      if (data.url || data.id) {
        const imageUrl = data.url || data.id;
        // Ensure the URL starts with / for proper serving
        const finalUrl = imageUrl.startsWith('/') ? imageUrl : `/${imageUrl}`;
        setGeneratedImage(finalUrl);
      } else if (data.images && data.images[0]) {
        const imageUrl = data.images[0];
        const finalUrl = imageUrl.startsWith('/') ? imageUrl : `/${imageUrl}`;
        setGeneratedImage(finalUrl);
      }

      // Generate caption for the image only if auto-caption is enabled
      if (autoGenerateImageCaption) {
        if (!data.caption && (businessName || productName)) {
          // Generate a simple caption if not provided
          const simpleCaption = `Check out ${businessName ? businessName + "'s" : "our"} ${productName || "latest update"}! ${callToAction || ""}`;
          setGeneratedImageCaption(simpleCaption);
          if (!content) {
            setContent(simpleCaption);
          }
        } else if (data.caption) {
          setGeneratedImageCaption(data.caption);
          if (!content) {
            setContent(data.caption);
          }
        }
      } else {
        // Clear any existing caption when auto-generation is disabled
        setGeneratedImageCaption("");
      }

      toast({
        title: "Image Generated",
        description: "AI has created a custom image with caption",
      });
    },
    onError: (error: any) => {
      console.error('Image generation error:', error);
      toast({
        title: "Error",
        description: error?.message || "Failed to generate image. Please try again.",
        variant: "destructive",
      });
    },
  });

  const generateVideoMutation = useMutation({
    mutationFn: async (params: any) => {
      // Prioritize subject matter (productName) above all other parameters
      let prompt = params.videoScript;

      if (!prompt) {
        const subjectMatter = params.productName || '';
        const businessName = params.businessName || '';
        const videoStyle = params.videoStyle || 'professional';

        if (subjectMatter.trim()) {
          // Subject matter is the primary focus
          prompt = `${videoStyle} cinematic close-up of ${subjectMatter} in slow motion`;

          // Add business context if provided, but as secondary
          if (businessName.trim()) {
            prompt += `, featuring ${businessName}`;
          }
        } else if (businessName.trim()) {
          // Fallback when only business name is provided
          prompt = `${videoStyle} cinematic close-up of ${businessName} in slow motion`;
        } else {
          // Default when neither is provided
          prompt = `${videoStyle} cinematic scene in slow motion`;
        }
      }

      const aspectRatio = selectedPlatforms[0] === "TikTok" ? "9:16" : "16:9";

      // Start video generation with all context
      const startResponse = await apiRequest("POST", "/api/ai/video/start", {
        prompt: prompt.trim(),
        aspectRatio,
        fast: true, // Use Veo 3 Fast for quicker generation
        model: videoModel, // Add model selection (gemini for Veo 3)
        // Send all context fields for caption generation
        businessName,
        productName,
        targetAudience,
        brandTone,
        keyMessages,
        callToAction,
        isAdvertisement,
        additionalContext,
        manualCaption: params.autoGenerateCaption === false ? params.manualCaption : undefined,
        captionStyle: params.captionStyle
      });
      const { operationName, caption } = await startResponse.json();

      // Store caption immediately
      if (caption) {
        setGeneratedVideoCaption(caption);
        // If no manual content was entered, use the generated caption
        if (!content) {
          setContent(caption);
        }
      }

      // Show initial toast
      toast({
        title: "Video Generation Started",
        description: "Video generation can take up to 3 minutes. Please be patient...",
      });

      // Poll for completion with 3 minute timeout as requested
      let downloadUrl = "";
      let attempts = 0;
      const maxAttempts = 18; // Max 18 attempts (3 minutes with 10 second intervals)

      while (attempts < maxAttempts) {
        await new Promise(r => setTimeout(r, 10000)); // Wait 10 seconds between checks

        const statusResponse = await fetch(`/api/ai/video/status/${encodeURIComponent(operationName)}`);
        const status = await statusResponse.json();

        if (status.done) {
          downloadUrl = status.downloadUrl;
          break;
        }

        // Show progress updates every 30 seconds
        if (attempts > 0 && attempts % 3 === 0) {
          const minutesElapsed = Math.floor((attempts * 10) / 60);
          toast({
            title: "Still Generating...",
            description: `Video generation in progress (${minutesElapsed} minute${minutesElapsed !== 1 ? 's' : ''} elapsed). ${status.message || 'Please wait...'}`,
          });
        }

        attempts++;
      }

      if (!downloadUrl) {
        throw new Error("Video generation timed out");
      }

      return { videoUrl: downloadUrl };
    },
    onSuccess: (data) => {
      setGeneratedVideo(data.videoUrl);
      toast({
        title: "Video Generated",
        description: "AI has created a custom 8-second video with audio",
      });
      // Refresh user data to update credits and trial videos
      queryClient.invalidateQueries({ queryKey: ["/api/user"] });
    },
    onError: (error: any) => {
      console.error('Video generation error:', error);
      toast({
        title: "Error",
        description: error?.message || "Failed to generate video. Please try again.",
        variant: "destructive",
      });
    },
  });

  const sendToApprovalQueueMutation = useMutation({
    mutationFn: async (mediaData: any) => {
      return apiRequest("POST", "/api/posts/approval-queue", mediaData);
    },
    onSuccess: () => {
      toast({
        title: "Sent to Approval Queue",
        description: "Media has been sent for approval",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/posts/pending"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send to approval queue",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setContent("");
    setSelectedPlatforms(["Instagram", "Facebook"]);
    setScheduleOption("approval");
    setShowAiSuggestions(false);
    setAiSuggestions([]);
    setIsBusinessMode(true); // Reset to business mode
    setBusinessName("");
    setProductName("");
    setTargetAudience("");
    setKeyMessages("");
    setCallToAction("");
    setAdditionalContext("");
    setImagePrompt("");
    setColorScheme("");
    setObjectFocus("");
    setVideoScenes("");
    setVideoScript("");
    setGeneratedImage(null);
    setGeneratedVideo(null);
    setUploadedImage(null);
    setUploadedVideo(null);
    setGeneratedImageCaption("");
    setGeneratedVideoCaption("");
    setAutoGenerateImageCaption(true);
    setAutoGenerateVideoCaption(true);
    setManualImageCaption("");
    setManualVideoCaption("");
    setImageCaptionStyle("engaging");
    setVideoCaptionStyle("engaging");
  };

  const handleSubmit = () => {
    if (isBusinessMode) {
      if (!content.trim() && !businessName.trim()) {
        toast({
          title: "Error",
          description: "Please enter content or fill in business details for AI generation.",
          variant: "destructive",
        });
        return;
      }
    } else {
      // In personal mode, only require content if no other details are provided
      if (!content.trim() && !businessName.trim() && !productName.trim()) {
        toast({
          title: "Error",
          description: "Please enter content or fill in some personal details for AI generation.",
          variant: "destructive",
        });
        return;
      }
    }

    if (selectedPlatforms.length === 0) {
      toast({
        title: "Error",
        description: "Please select at least one platform.",
        variant: "destructive",
      });
      return;
    }

    const status = scheduleOption === "approval" ? "pending" : "scheduled";

    createPostMutation.mutate({
      content,
      platforms: selectedPlatforms,
      status,
      aiGenerated: true,
      contentType,
      imageUrl: uploadedImage || generatedImage,
      videoUrl: uploadedVideo || generatedVideo,
      scheduledFor: scheduleOption === "later" ? new Date(Date.now() + 24 * 60 * 60 * 1000) : null,
      metadata: {
        businessName,
        productName,
        targetAudience,
        brandTone,
        keyMessages,
        callToAction,
        isAdvertisement,
        visualStyle,
        colorScheme,
        imagePrompt,
        cameraAngle,
        environment,
        lighting,
        mood,
        composition,
        objectFocus,
        aspectRatio,
        imageResolution,
        videoDuration,
        videoStyle,
        videoTransitions,
        videoMusic,
        videoVoiceover,
        videoPacing,
        videoEffects,
        videoFrameRate,
        videoResolution,
        videoAspectRatio,
        videoScenes,
        videoScript,
      }
    });
  };

  const handleAiGenerate = () => {
    if (isBusinessMode && !businessName.trim()) {
      toast({
        title: "Missing Information",
        description: "Please enter at least your business name to generate content.",
        variant: "destructive",
      });
      return;
    }

    if (!isBusinessMode && !businessName.trim() && !productName.trim()) {
      toast({
        title: "Missing Information",
        description: "Please enter at least your name or subject/topic to generate content.",
        variant: "destructive",
      });
      return;
    }

    generateAiContentMutation.mutate({
      businessName,
      productName,
      targetAudience,
      brandTone,
      keyMessages: keyMessages.split(',').map(k => k.trim()).filter(k => k),
      callToAction,
      platform: selectedPlatforms[0] || 'Instagram',
      isAdvertisement,
      additionalContext,
      contentType,
    });
  };

  const handleGenerateImage = () => {
    generateImageMutation.mutate({
      businessName,
      productName,
      visualStyle,
      colorScheme,
      cameraAngle,
      environment,
      lighting,
      mood,
      composition,
      objectFocus,
      aspectRatio,
      resolution: imageResolution,
      imagePrompt,
      autoGenerateCaption: autoGenerateImageCaption,
      manualCaption: !autoGenerateImageCaption ? manualImageCaption : undefined,
      captionStyle: imageCaptionStyle,
    });
  };

  const handleGenerateVideo = () => {
    // Runtime guard to prevent bypassing disabled state
    if (!videoEligibility.allowed) {
      toast({
        title: "Video Generation Locked",
        description: videoEligibility.reason,
        variant: "destructive",
      });
      // Navigate to appropriate action
      if (videoEligibility.action === "upgrade") {
        setTimeout(() => navigate("/pricing"), 1500);
      } else if (videoEligibility.action === "buy_credits") {
        setTimeout(() => navigate("/billing"), 1500);
      }
      return;
    }

    generateVideoMutation.mutate({
      businessName,
      productName,
      videoDuration,
      videoStyle,
      videoTransitions,
      videoMusic,
      videoVoiceover,
      videoPacing,
      videoEffects,
      videoFrameRate,
      videoResolution,
      videoAspectRatio,
      videoScenes,
      videoScript,
      autoGenerateCaption: autoGenerateVideoCaption,
      manualCaption: !autoGenerateVideoCaption ? manualVideoCaption : undefined,
      captionStyle: videoCaptionStyle,
    });
  };

  const handleImageUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setUploadedImage(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleVideoUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setUploadedVideo(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSendImageToApproval = () => {
    const imageToSend = uploadedImage || generatedImage;
    if (imageToSend) {
      sendToApprovalQueueMutation.mutate({
        type: 'image',
        url: imageToSend,
        businessName,
        platforms: selectedPlatforms,
      });
    }
  };

  const handleSendVideoToApproval = () => {
    const videoToSend = uploadedVideo || generatedVideo;
    if (videoToSend) {
      sendToApprovalQueueMutation.mutate({
        type: 'video',
        url: videoToSend,
        businessName,
        platforms: selectedPlatforms,
      });
    }
  };

  const handleSelectSuggestion = (suggestion: string) => {
    setContent(suggestion);
    setShowAiSuggestions(false);
  };

  return (
    <div className="p-6 tech-grid">
      <div className="max-w-7xl mx-auto">
        <div className="mb-6 flex justify-between items-start">
          <div>
            <h1 className="text-3xl font-bold text-foreground font-display">Create Content</h1>
            <p className="text-muted-foreground mt-2">
              Generate AI-powered content optimized for your business and target audience
            </p>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Main Content Area - Left Side */}
          <div className="lg:col-span-2 space-y-6">
            {/* Target Platforms - Now at top */}
            <Card className="glass-morphism">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Target className="w-5 h-5 text-primary" />
                  Target Platforms
                </CardTitle>
                <CardDescription>
                  Select the social media platforms for your content
                </CardDescription>
              </CardHeader>
              <CardContent>
                <PlatformSelector 
                  selectedPlatforms={selectedPlatforms}
                  onPlatformsChange={setSelectedPlatforms}
                />
              </CardContent>
            </Card>

            {/* Business/Personal Mode Toggle */}
            <Card className="glass-morphism">
              <CardContent className="pt-6">
                <div className="flex items-center justify-center space-x-4">
                  <Label htmlFor="mode-toggle" className={`cursor-pointer transition-colors ${!isBusinessMode ? "text-primary font-medium" : "text-muted-foreground"}`}>
                    <UserIcon className="w-4 h-4 inline mr-1" />
                    Personal
                  </Label>
                  <Switch
                    id="mode-toggle"
                    checked={isBusinessMode}
                    onCheckedChange={setIsBusinessMode}
                    className="data-[state=checked]:bg-primary"
                    data-testid="switch-business-personal-mode"
                  />
                  <Label htmlFor="mode-toggle" className={`cursor-pointer transition-colors ${isBusinessMode ? "text-primary font-medium" : "text-muted-foreground"}`}>
                    <Building2 className="w-4 h-4 inline mr-1" />
                    Business
                  </Label>
                </div>
              </CardContent>
            </Card>

            {/* Business/Personal Information */}
            <Card className="glass-morphism">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  {isBusinessMode ? (
                    <Building2 className="w-5 h-5 text-primary" />
                  ) : (
                    <UserIcon className="w-5 h-5 text-primary" />
                  )}
                  {isBusinessMode ? "Business Information" : "Personal Information"}
                </CardTitle>
                <CardDescription>
                  {isBusinessMode 
                    ? "Provide details about your business for personalized content generation"
                    : "Provide details about yourself for personalized content generation"
                  }
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="businessName">
                      {isBusinessMode ? "Business Name *" : "Name"}
                    </Label>
                    <Input
                      id="businessName"
                      value={businessName}
                      onChange={(e) => setBusinessName(e.target.value)}
                      placeholder={isBusinessMode ? "e.g., Sarah's Coffee Shop" : "e.g., Sarah Johnson"}
                      className="mt-1"
                      data-testid="input-business-name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="productName">
                      {isBusinessMode ? "Product/Service" : "Subject/Topic"}
                    </Label>
                    <Input
                      id="productName"
                      value={productName}
                      onChange={(e) => setProductName(e.target.value)}
                      placeholder={isBusinessMode ? "e.g., Organic Coffee Blend" : "e.g., Travel photography, Cooking tips"}
                      className="mt-1"
                      data-testid="input-product-name"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="targetAudience">
                    {isBusinessMode ? "Target Audience" : "Intended Audience"}
                  </Label>
                  <Input
                    id="targetAudience"
                    value={targetAudience}
                    onChange={(e) => setTargetAudience(e.target.value)}
                    placeholder={isBusinessMode 
                      ? "e.g., Young professionals, coffee enthusiasts, eco-conscious consumers" 
                      : "e.g., Friends and family, photography enthusiasts, food lovers"
                    }
                    className="mt-1"
                    data-testid="input-target-audience"
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="brandTone">
                      {isBusinessMode ? "Brand Tone" : "Content Tone"}
                    </Label>
                    <Select value={brandTone} onValueChange={setBrandTone}>
                      <SelectTrigger className="mt-1" data-testid="select-brand-tone">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="professional">Professional</SelectItem>
                        <SelectItem value="friendly">Friendly & Casual</SelectItem>
                        <SelectItem value="luxurious">Luxurious & Premium</SelectItem>
                        <SelectItem value="playful">Playful & Fun</SelectItem>
                        <SelectItem value="inspirational">Inspirational</SelectItem>
                        <SelectItem value="educational">Educational</SelectItem>
                        <SelectItem value="urgent">Urgent & Action-Oriented</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div>
                    <Label htmlFor="callToAction">
                      {isBusinessMode ? "Call to Action" : "Action/Goal"}
                    </Label>
                    <Input
                      id="callToAction"
                      value={callToAction}
                      onChange={(e) => setCallToAction(e.target.value)}
                      placeholder={isBusinessMode 
                        ? "e.g., Shop Now, Learn More, Get 20% Off" 
                        : "e.g., Check out my photos, Share your thoughts, Let's connect"
                      }
                      className="mt-1"
                      data-testid="input-call-to-action"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="keyMessages">
                    {isBusinessMode ? "Key Messages (comma-separated)" : "Key Points (comma-separated)"}
                  </Label>
                  <Textarea
                    id="keyMessages"
                    value={keyMessages}
                    onChange={(e) => setKeyMessages(e.target.value)}
                    placeholder={isBusinessMode 
                      ? "e.g., Free shipping on orders over $50, Eco-friendly packaging, Award-winning quality" 
                      : "e.g., Taken with my new camera, Beautiful sunset view, Homemade recipe"
                    }
                    className="mt-1 h-20"
                    data-testid="textarea-key-messages"
                  />
                </div>

                {/* Advertisement toggle - only show in business mode */}
                {isBusinessMode && (
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <Switch
                        id="isAdvertisement"
                        checked={isAdvertisement}
                        onCheckedChange={setIsAdvertisement}
                        data-testid="switch-advertisement"
                      />
                      <Label htmlFor="isAdvertisement" className="cursor-pointer">
                        Structure as Advertisement
                      </Label>
                    </div>
                    <Badge variant={isAdvertisement ? "default" : "secondary"} className="pulse-glow">
                      {isAdvertisement ? "Ad Format" : "Organic Content"}
                    </Badge>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Content Type Selection */}
            <Card className="glass-morphism">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Wand2 className="w-5 h-5 text-primary" />
                  Content Type
                </CardTitle>
                <CardDescription>
                  Choose what type of content you want to create
                </CardDescription>
              </CardHeader>
              <CardContent>
