import { cn } from "@/lib/utils";
import type { User } from "@shared/schema";

interface UserAvatarProps {
  user: User | undefined;
  className?: string;
}

export function UserAvatar({ user, className }: UserAvatarProps) {
  const getTierColor = (role: string | undefined, tier: string | undefined) => {
    // Admin and employee accounts get special styling
    if (role === "admin" || role === "employee") {
      if (role === "admin") {
        return "bg-gradient-to-br from-amber-500 to-orange-600 border-amber-400 shadow-amber-500/30";
      }
      return "bg-gradient-to-br from-indigo-500 to-purple-600 border-indigo-400 shadow-indigo-500/30";
    }

    // Customer accounts colored by tier
    switch (tier) {
      case "enterprise":
        return "bg-gradient-to-br from-purple-500 to-pink-600 border-purple-400 shadow-purple-500/30";
      case "professional":
        return "bg-gradient-to-br from-blue-500 to-cyan-600 border-blue-400 shadow-blue-500/30";
      case "starter":
        return "bg-gradient-to-br from-green-500 to-emerald-600 border-green-400 shadow-green-500/30";
      case "free":
      default:
        return "bg-gradient-to-br from-gray-500 to-slate-600 border-gray-400 shadow-gray-500/30";
    }
  };

  const getInitials = (fullName: string) => {
    if (!fullName || fullName.trim() === '') {
      return 'U';
    }

    const names = fullName.trim().split(/\s+/);
    if (names.length >= 2) {
      // Get first letter of first name and first letter of last name
      return `${names[0][0]}${names[names.length - 1][0]}`.toUpperCase();
    }
    // If only one name, get first two letters
    return names[0].slice(0, 2).toUpperCase();
  };

  if (!user) {
    return (
      <div className={cn(
        "w-10 h-10 rounded-full bg-gray-300 animate-pulse",
        className
      )} />
    );
  }

  // Always show initials, never use image avatars for consistency
  const initials = getInitials(user.fullName || user.username || 'User');
  const isInternalAccount = user.role === 'admin' || user.role === 'employee';

  return (
    <div 
      className={cn(
        "w-10 h-10 rounded-full flex items-center justify-center text-white font-bold border-2 shadow-lg",
        getTierColor(user.role, user.tier),
        isInternalAccount && "ring-2 ring-offset-2 ring-offset-background",
        isInternalAccount && user.role === "admin" && "ring-amber-400",
        isInternalAccount && user.role === "employee" && "ring-indigo-400",
        className
      )}
      title={`${user.fullName}${isInternalAccount ? ` (${user.role === 'admin' ? 'Admin' : 'Staff'})` : ''}`}
    >
      <span className="text-sm font-bold tracking-wide">
        {initials}
      </span>
    </div>
  );
}

client/src/components/ui/logo.tsx.content:
import { cn } from "@/lib/utils";

interface LogoProps {
  className?: string;
  size?: "sm" | "md" | "lg";
  animated?: boolean;
}

export function Logo({ className, size = "md", animated = true }: LogoProps) {
  const sizeClasses = {
    sm: "h-8 w-8",
    md: "h-10 w-10",
    lg: "h-12 w-12"
  };

  const textSizes = {
    sm: "text-lg",
    md: "text-xl",
    lg: "text-2xl"
  };

  return (
    <div className={cn("flex items-center space-x-3", className)}>
      <div className="relative">
        {/* Outer hexagon container */}
        <svg
          viewBox="0 0 100 100"
          className={cn(
            sizeClasses[size],
            "relative z-10",
            animated && "animate-pulse"
          )}
        >
          {/* Gradient definitions */}
          <defs>
            <linearGradient id="logo-gradient" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="hsl(280, 100%, 60%)" />
              <stop offset="50%" stopColor="hsl(300, 100%, 50%)" />
              <stop offset="100%" stopColor="hsl(320, 100%, 60%)" />
            </linearGradient>
            <filter id="glow">
              <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
              <feMerge>
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="SourceGraphic"/>
              </feMerge>
            </filter>
          </defs>

          {/* Hexagon shape */}
          <path
            d="M50 5 L85 25 L85 75 L50 95 L15 75 L15 25 Z"
            fill="none"
            stroke="url(#logo-gradient)"
            strokeWidth="3"
            filter="url(#glow)"
          />

          {/* Inner AI symbol - stylized brain/circuit hybrid */}
          <g transform="translate(50, 50)">
            {/* Central node */}
            <circle cx="0" cy="0" r="4" fill="url(#logo-gradient)" />

            {/* Connection lines */}
            <path
              d="M0,0 L-15,-10 M0,0 L15,-10 M0,0 L-15,10 M0,0 L15,10"
              stroke="url(#logo-gradient)"
              strokeWidth="2"
              opacity="0.8"
            />

            {/* Outer nodes */}
            <circle cx="-15" cy="-10" r="3" fill="url(#logo-gradient)" opacity="0.8" />
            <circle cx="15" cy="-10" r="3" fill="url(#logo-gradient)" opacity="0.8" />
            <circle cx="-15" cy="10" r="3" fill="url(#logo-gradient)" opacity="0.8" />
            <circle cx="15" cy="10" r="3" fill="url(#logo-gradient)" opacity="0.8" />

            {/* Additional circuit lines */}
            <path
              d="M-15,-10 L0,-20 L15,-10 M-15,10 L0,20 L15,10"
              stroke="url(#logo-gradient)"
              strokeWidth="1.5"
              fill="none"
              opacity="0.6"
            />
          </g>
        </svg>

        {/* Animated glow effect */}
        {animated && (
          <div className="absolute inset-0 rounded-full bg-gradient-to-r from-purple-600 to-pink-600 opacity-30 blur-xl animate-pulse" />
        )}
      </div>

      <div className="flex flex-col">
        <h1 
          className={cn(
            textSizes[size],
