import React, { useMemo, useRef, useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import timeGridPlugin from "@fullcalendar/timegrid";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin, { Draggable } from "@fullcalendar/interaction";
import dayjs from "dayjs";
import utc from "dayjs/plugin/utc.js";
import timezone from "dayjs/plugin/timezone.js";
import { Sheet, SheetContent, SheetHeader, SheetTitle } from "@/components/ui/sheet";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { DraftsRail } from "@/components/schedule/DraftsRail";
import { ScheduleToolbar } from "@/components/schedule/ScheduleToolbar";
import { EventInspector } from "@/components/schedule/EventInspector";

dayjs.extend(utc);
dayjs.extend(timezone);

const PLATFORM_COLORS: Record<string, string> = {
  x: "#38bdf8",
  instagram: "#ec4899",
  facebook: "#3b82f6",
  tiktok: "#8b5cf6",
  linkedin: "#06b6d4"
};

interface ScheduleEvent {
  id: string;
  title: string;
  start: string;
  end: string;
  backgroundColor?: string;
  borderColor?: string;
  extendedProps: {
    postId: string;
    platform: string;
    status: string;
    caption: string;
    mediaUrls?: string[];
    tags?: string[];
    needsApproval?: boolean;
  };
}

export default function SchedulePage() {
  const { toast } = useToast();
  const [events, setEvents] = useState<ScheduleEvent[]>([]);
  const [filters, setFilters] = useState<string[]>([]);
  const [tz, setTz] = useState<string>("America/Chicago");
  const [selectedEvent, setSelectedEvent] = useState<ScheduleEvent | null>(null);
  const [view, setView] = useState<string>("timeGridWeek");
  const calendarRef = useRef<FullCalendar>(null);
  const [isLoading, setIsLoading] = useState(false);

  const visibleEvents = useMemo(
    () => filters.length ? events.filter(e => filters.includes(e.extendedProps.platform)) : events,
    [events, filters]
  );

  // Event content renderer with platform colors and status badges
  function eventContent(arg: any) {
    const props = arg.event.extendedProps;
    const color = PLATFORM_COLORS[props.platform.toLowerCase()] || "#a855f7";
    const title = arg.event.title || props.caption?.substring(0, 50) || props.platform.toUpperCase();

    return (
      <div 
        className="h-full w-full px-2 py-1 rounded-md cursor-pointer transition-all hover:shadow-lg"
        style={{ 
          backgroundColor: `${color}26`, 
          borderLeft: `3px solid ${color}`,
          borderColor: color
        }}
      >
        <div className="flex items-start justify-between gap-1">
          <span className="truncate text-[11px] font-medium text-zinc-100">{title}</span>
          {props.needsApproval && (
            <Badge className="text-[9px] px-1 py-0" variant="secondary">
              Review
            </Badge>
          )}
        </div>
        {props.status && (
          <div className="mt-1">
            <span className="text-[10px] text-zinc-400 capitalize">
              {props.status.replace("_", " ")}
            </span>
          </div>
        )}
      </div>
    );
  }

  // Fetch events when calendar view changes
  async function fetchEvents(info?: any) {
    setIsLoading(true);
    try {
      const calendarApi = calendarRef.current?.getApi();
      const activeView = info || calendarApi?.view;

      if (!activeView) return;

      const from = dayjs(activeView.currentStart).tz(tz).format("YYYY-MM-DD");
      const to = dayjs(activeView.currentEnd).tz(tz).format("YYYY-MM-DD");

      const response = await fetch(`/api/schedule?from=${from}&to=${to}&tz=${tz}`);
      if (!response.ok) throw new Error("Failed to fetch events");

      const data = await response.json();

      // Transform API response to FullCalendar events
      const transformedEvents: ScheduleEvent[] = (data.events || []).map((e: any) => {
        const color = PLATFORM_COLORS[e.platform?.toLowerCase()] || "#a855f7";
        return {
          id: e.id,
          title: e.title || e.caption?.substring(0, 50) || e.platform,
          start: e.scheduledAt || e.startsAt,
          end: e.endsAt || dayjs(e.scheduledAt || e.startsAt).add(30, "minutes").toISOString(),
          backgroundColor: `${color}40`,
          borderColor: color,
          extendedProps: {
            postId: e.postId || e.id,
            platform: e.platform || "instagram",
            status: e.status || "draft",
            caption: e.caption || e.content || "",
            mediaUrls: e.mediaUrls || [],
            tags: e.tags || [],
            needsApproval: e.needsApproval || e.status === "needs_approval"
          }
        };
      });

      setEvents(transformedEvents);
    } catch (error) {
      console.error("Error fetching events:", error);
      toast({
        title: "Error loading schedule",
        description: "Failed to load scheduled posts. Please refresh the page.",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  }

  // Create event from draft
  async function createFromDraft(dropInfo: any, draft: any) {
    try {
      const scheduledAt = dayjs(dropInfo.date).tz(tz).toISOString();

      const payload = {
        draftId: draft.id,
        platform: draft.platform,
        scheduledAt,
        caption: draft.caption || draft.content,
        mediaUrls: draft.mediaUrls || [],
        tags: draft.tags || []
      };

      const response = await fetch("/api/schedule", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload)
      });

      if (response.status === 409) {
        const { suggestion } = await response.json();
        dropInfo.revert();
        toast({
          title: "Time conflict detected",
          description: suggestion || "Try scheduling at a different time",
          variant: "destructive"
        });
        return;
      }

      if (!response.ok) {
        throw new Error("Failed to schedule post");
      }

      toast({
        title: "Post scheduled",
        description: `Scheduled for ${dayjs(scheduledAt).format("MMM D, h:mm A")}`,
      });

      // Refresh events
      await fetchEvents();
    } catch (error) {
      console.error("Error creating event:", error);
      toast({
        title: "Scheduling failed",
        description: "Could not schedule this post. Please try again.",
        variant: "destructive"
      });
    }
  }

  // Handle event drop (reschedule)
  async function handleEventDrop(info: any) {
    try {
      const newStart = dayjs(info.event.start).tz(tz).toISOString();

      const response = await fetch(`/api/schedule/${info.event.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ scheduledAt: newStart })
      });

      if (response.status === 409) {
        info.revert();
        const { suggestion } = await response.json();
        toast({
          title: "Time conflict",
          description: suggestion || "This time slot has conflicts",
          variant: "destructive"
        });
        return;
      }

      if (!response.ok) {
        info.revert();
        throw new Error("Failed to reschedule");
      }

      toast({
        title: "Post rescheduled",
        description: `Moved to ${dayjs(newStart).format("MMM D, h:mm A")}`,
      });
    } catch (error) {
      console.error("Error rescheduling:", error);
      info.revert();
      toast({
        title: "Rescheduling failed",
        description: "Could not move this post. Please try again.",
        variant: "destructive"
      });
    }
  }

  // Handle external drop (from drafts)
  async function handleExternalDrop(info: any) {
    const draft = JSON.parse(info.draggedEl.dataset.event || "{}");
    if (!draft.id) return;

    await createFromDraft(info, draft);
  }

  // Initialize draggable for drafts
  useEffect(() => {
    const draggableEl = document.getElementById("drafts-container");
    if (draggableEl) {
      new Draggable(draggableEl, {
        itemSelector: ".draft-card",
        eventData: (eventEl: any) => {
          const data = eventEl.dataset.event;
          return data ? JSON.parse(data) : {};
        }
      });
    }
  }, []);

  // Initial load
  useEffect(() => {
    fetchEvents();
  }, [tz]);

  return (
    <div className="flex h-[calc(100vh-64px)]">
      {/* Left: Drafts Rail */}
      <div className="w-[280px] border-r border-zinc-800 bg-zinc-950 overflow-hidden">
        <DraftsRail />
      </div>

      {/* Middle: Calendar */}
      <div className="flex-1 bg-zinc-900 p-6">
        <div className="h-full flex flex-col">
          <ScheduleToolbar
            tz={tz}
            onTzChange={setTz}
            filters={filters}
            onFiltersChange={setFilters}
            view={view}
            onViewChange={(v) => {
              setView(v);
              calendarRef.current?.getApi().changeView(v);
            }}
            onToday={() => calendarRef.current?.getApi().today()}
            onPrev={() => calendarRef.current?.getApi().prev()}
            onNext={() => calendarRef.current?.getApi().next()}
          />

          <div className="flex-1 mt-4 bg-zinc-950 rounded-2xl border border-zinc-800 p-4">
            <FullCalendar
              ref={calendarRef}
              plugins={[timeGridPlugin, dayGridPlugin, interactionPlugin]}
              initialView={view}
              headerToolbar={false}
              slotMinTime="06:00:00"
              slotMaxTime="22:00:00"
              slotDuration="00:30:00"
              slotLabelInterval="01:00:00"
              nowIndicator
              nowIndicatorClassNames="bg-fuchsia-500"
              height="100%"
              eventOverlap={false}
              eventContent={eventContent}
              events={visibleEvents}
              datesSet={fetchEvents}
              droppable
              editable
              eventDrop={handleEventDrop}
              drop={handleExternalDrop}
              eventClick={(info) => setSelectedEvent(info.event as any)}
              scrollTime={dayjs().format("HH:mm:ss")}
              dayMaxEvents={3}
              moreLinkClick="popover"
              weekends={true}
              slotLabelFormat={{
                hour: "numeric",
                minute: "2-digit",
                meridiem: "short"
              }}
              eventTimeFormat={{
                hour: "numeric",
                minute: "2-digit",
                meridiem: "short"
              }}
            />
          </div>
        </div>
      </div>

      {/* Right: Inspector Panel */}
      <Sheet open={!!selectedEvent} onOpenChange={(open) => !open && setSelectedEvent(null)}>
        <SheetContent side="right" className="w-[400px] bg-zinc-950 border-zinc-800">
          <SheetHeader>
            <SheetTitle className="text-zinc-100">Edit Post</SheetTitle>
          </SheetHeader>
          {selectedEvent && (
            <EventInspector
              event={selectedEvent}
              onSaved={() => {
                setSelectedEvent(null);
                fetchEvents();
              }}
              onDeleted={() => {
                setSelectedEvent(null);
                fetchEvents();
              }}
            />
          )}
        </SheetContent>
      </Sheet>
    </div>
  );
}

server/adminRoutes.ts.content:
import { Router, Request, Response, NextFunction } from "express";
import { storage } from "./storage";
import { hash } from "bcryptjs";
import { randomUUID } from "crypto";
import Stripe from "stripe";
import { isUserOnline } from "./middleware/activityTracker";

const stripe = process.env.STRIPE_SECRET_KEY 
  ? new Stripe(process.env.STRIPE_SECRET_KEY, { apiVersion: "2025-08-27.basil" as any })
  : null;

const router = Router();

// Admin authentication middleware that works with both session and Replit auth
const isAdmin = async (req: Request, res: Response, next: NextFunction) => {
  try {
    let userId: string | undefined;

    // Check for session-based auth first
    if (req.session?.userId) {
      userId = req.session.userId;
    }
    // Check for Replit auth
    else if ((req as any).user?.claims?.sub) {
      userId = (req as any).user.claims.sub;
    }

    if (!userId) {
      return res.status(401).json({ message: "Authentication required" });
    }

    const user = await storage.getUser(userId);
    if (!user || !user.isAdmin) {
      return res.status(403).json({ message: "Admin access required" });
    }

    // Store user info for route handlers
    (req as any).adminUser = user;
    (req as any).adminId = userId;

    next();
  } catch (error) {
    console.error("Admin auth error:", error);
    res.status(500).json({ message: "Authentication error" });
  }
};

// All admin routes require admin authentication
router.use(isAdmin);

// Create new user
router.post("/users", async (req, res) => {
  try {
    const adminId = (req as any).adminId;
    const {
      username,
      email,
      password,
      firstName,
      lastName,
      businessName,
      tier = "free",
      credits = 50,
      isAdmin = false,
    } = req.body;

    // Validate required fields
    if (!username || !email || !password) {
      return res.status(400).json({ 
        message: "Username, email, and password are required" 
      });
    }

    // Validate password length
    if (password.length < 6) {
      return res.status(400).json({ 
        message: "Password must be at least 6 characters" 
      });
    }

    // Validate email format
    if (!email.includes("@") || !email.includes(".")) {
      return res.status(400).json({ 
        message: "Invalid email format" 
      });
    }

    // Check if username is unique
    const existingUsername = await storage.getUserByUsername(username);
    if (existingUsername) {
      return res.status(400).json({ 
        message: "Username already exists" 
      });
    }

    // Check if email is unique
    const existingEmail = await storage.getUserByEmail(email);
    if (existingEmail) {
      return res.status(400).json({ 
        message: "Email already exists" 
      });
    }

    // Validate tier
    const validTiers = ["free", "starter", "professional", "business", "enterprise"];
    if (!validTiers.includes(tier)) {
      return res.status(400).json({ 
        message: "Invalid subscription tier" 
      });
    }

    // Hash the password
    const hashedPassword = await hash(password, 10);

    // Create the user
    const newUser = await storage.createUser({
      id: randomUUID(),
      username,
      email,
      password: hashedPassword,
      fullName: firstName && lastName ? `${firstName} ${lastName}` : firstName || lastName || username,
      firstName: firstName || null,
      lastName: lastName || null,
      businessName: businessName || null,
      tier,
      credits,
      isAdmin,
      emailVerified: true, // Admin-created users are pre-verified
      accountStatus: "active",
      trialType: null,
      trialStartDate: null,
      trialEndDate: null,
      createdAt: new Date(),
      updatedAt: new Date(),
    });

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: newUser.id,
      action: "create_user",
      details: {
        username,
        email,
        tier,
        credits,
        isAdmin,
      },
    });

    // Remove password from response
    const { password: _, ...userWithoutPassword } = newUser;

    res.status(201).json(userWithoutPassword);
  } catch (error: any) {
    console.error("Error creating user:", error);
    res.status(500).json({ 
      message: "Error creating user: " + error.message 
    });
  }
});

// Get all users with enhanced info
router.get("/users", async (req, res) => {
  try {
    const users = await storage.getAllUsers();

    // Enhance users with online status and trial info
    const enhancedUsers = await Promise.all(
      users.map(async (user) => {
        const isOnline = await isUserOnline(user.id);
        const now = new Date();
        const trialEndDate = user.trialEndDate || user.trialEndsAt;

        let trialDaysRemaining = null;
        let trialStatus = null;

        if (user.tier === 'free' && !user.isPaid && trialEndDate) {
          const daysRemaining = Math.ceil(
            (new Date(trialEndDate).getTime() - now.getTime()) / (1000 * 60 * 60 * 24)
          );
          trialDaysRemaining = Math.max(0, daysRemaining);
          trialStatus = trialDaysRemaining > 0 ? 'active' : 'expired';
        }

        return {
          ...user,
          isOnline,
          trialDaysRemaining,
          trialStatus,
        };
      })
    );

    res.json(enhancedUsers);
  } catch (error: any) {
    res.status(500).json({ message: "Error fetching users: " + error.message });
  }
});

// Get specific user
router.get("/users/:id", async (req, res) => {
  try {
    const user = await storage.getUser(req.params.id);
    if (!user) {
      return res.status(404).json({ message: "User not found" });
    }

    // Get user's credit transactions
    const transactions = await storage.getCreditTransactionsByUserId(user.id);
    const adminActions = await storage.getAdminActionsByTargetUser(user.id);

    res.json({ 
      user, 
      transactions,
      adminActions 
    });
  } catch (error: any) {
    res.status(500).json({ message: "Error fetching user: " + error.message });
  }
});

// Update user (admin can update any field)
router.patch("/users/:id", async (req, res) => {
  try {
    const adminUser = (req as any).adminUser;
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const updates = req.body;

    // Don't allow changing user ID
    delete updates.id;

    const updatedUser = await storage.updateUser(id, updates);
    if (!updatedUser) {
      return res.status(404).json({ message: "User not found" });
    }

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "update_user",
      details: updates,
    });

    res.json(updatedUser);
  } catch (error: any) {
    res.status(500).json({ message: "Error updating user: " + error.message });
  }
});

// Grant credits to user
router.post("/users/:id/grant-credits", async (req, res) => {
  try {
    const adminUser = (req as any).adminUser;
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { amount, reason } = req.body;

    if (!amount || amount <= 0) {
      return res.status(400).json({ message: "Invalid credit amount" });
    }

    // Create credit transaction
    const transaction = await storage.createCreditTransaction({
      userId: id,
      amount,
      type: "admin_grant",
      description: reason || `Admin granted ${amount} credits`,
      stripePaymentIntentId: null,
    });

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "grant_credits",
      details: { amount, reason },
    });

    res.json(transaction);
  } catch (error: any) {
    res.status(500).json({ message: "Error granting credits: " + error.message });
  }
});

// Deduct credits from user
router.post("/users/:id/deduct-credits", async (req, res) => {
  try {
    const adminUser = (req as any).adminUser;
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { amount, reason } = req.body;

    if (!amount || amount <= 0) {
      return res.status(400).json({ message: "Invalid credit amount" });
    }

    // Create negative credit transaction
    const transaction = await storage.createCreditTransaction({
      userId: id,
      amount: -amount,
      type: "admin_deduction",
      description: reason || `Admin deducted ${amount} credits`,
      stripePaymentIntentId: null,
    });

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "deduct_credits",
      details: { amount, reason },
    });

    res.json(transaction);
  } catch (error: any) {
    res.status(500).json({ message: "Error deducting credits: " + error.message });
  }
});

// Freeze/unfreeze user account
router.post("/users/:id/freeze", async (req, res) => {
  try {
    const adminUser = (req as any).adminUser;
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { frozen, reason } = req.body;

    const user = await storage.updateUser(id, { 
      accountStatus: frozen ? "frozen" : "active" 
    });
    if (!user) {
      return res.status(404).json({ message: "User not found" });
    }

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: frozen ? "freeze_account" : "unfreeze_account",
      details: { reason },
    });

    res.json({ message: `Account ${frozen ? "frozen" : "unfrozen"}`, user });
  } catch (error: any) {
    res.status(500).json({ message: "Error updating account status: " + error.message });
  }
});

// Change user subscription tier
router.post("/users/:id/change-tier", async (req, res) => {
  try {
    const adminUser = (req as any).adminUser;
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { tier, grantCredits } = req.body;

    const plan = await storage.getSubscriptionPlanByTier(tier);
    if (!plan) {
      return res.status(400).json({ message: "Invalid subscription tier" });
    }

    const user = await storage.updateUser(id, { tier });
    if (!user) {
      return res.status(404).json({ message: "User not found" });
    }

    // Optionally grant the plan's monthly credits
    if (grantCredits) {
      await storage.createCreditTransaction({
        userId: id,
        amount: plan.creditsPerMonth,
        type: "admin_grant",
        description: `Admin changed tier to ${plan.name} with credits`,
        stripePaymentIntentId: null,
      });
    }

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "change_tier",
      details: { tier, grantCredits },
    });

    res.json({ message: "Tier updated", user });
  } catch (error: any) {
    res.status(500).json({ message: "Error changing tier: " + error.message });
  }
});

// Delete user account (soft or permanent)
router.delete("/users/:id", async (req, res) => {
  try {
    const adminUser = (req as any).adminUser;
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { permanent } = req.query;

    // Prevent deleting admin accounts
    const user = await storage.getUser(id);
    if (!user) {
      return res.status(404).json({ message: "User not found" });
    }

    if (user.isAdmin) {
      return res.status(403).json({ message: "Cannot delete admin accounts" });
    }

    if (permanent === "true") {
      // Permanently delete user and all data
      const deleted = await storage.deleteUser(id);
      if (!deleted) {
        return res.status(500).json({ message: "Failed to delete user" });
      }

      // Log admin action
      await storage.logAdminAction({
        adminUserId: adminId || null,
        targetUserId: id,
        action: "permanent_delete",
        details: { permanent: true },
      });

      res.json({ message: "User permanently deleted" });
    } else {
      // Mark as deleted instead of actually deleting
      await storage.updateUser(id, { 
        accountStatus: "deleted"
      });

      // Log admin action
      await storage.logAdminAction({
        adminUserId: adminId || null,
        targetUserId: id,
        action: "delete_account",
        details: {},
      });

      res.json({ message: "User account deleted" });
    }
  } catch (error: any) {
    res.status(500).json({ message: "Error deleting user: " + error.message });
  }
});

// Process refund
router.post("/refund", async (req, res) => {
  try {
    if (!stripe) {
      return res.status(500).json({ message: "Stripe not configured" });
    }

    const adminUser = (req as any).adminUser;
    const adminId = (req as any).adminId;
    const { userId, amount, reason, stripePaymentIntentId } = req.body;

    if (!stripePaymentIntentId) {
      return res.status(400).json({ message: "Payment intent ID required for refund" });
    }

    // Create Stripe refund
    const refund = await stripe.refunds.create({
      payment_intent: stripePaymentIntentId,
      amount: amount ? Math.round(amount * 100) : undefined, // Convert to cents
      reason: "requested_by_customer",
      metadata: {
        adminId,
        userId,
        reason,
      },
    });

    // Deduct credits if refunding a credit purchase
    if (userId) {
      const creditAmount = Math.round(amount * 10); // $0.10 per credit
      await storage.createCreditTransaction({
        userId,
        amount: -creditAmount,
        type: "refund",
        description: `Refund processed: ${reason}`,
        stripePaymentIntentId,
      });

      // Log admin action
      await storage.logAdminAction({
        adminUserId: adminId || null,
        targetUserId: userId,
        action: "process_refund",
        details: { amount, reason, refundId: refund.id },
      });
    }

    res.json({ message: "Refund processed", refund });
  } catch (error: any) {
    res.status(500).json({ message: "Error processing refund: " + error.message });
  }
});

// Get all credit transactions (enhanced)
router.get("/transactions", async (req, res) => {
  try {
    const transactions = await storage.getAllTransactions();

    // Enhance transactions with user info
    const enhancedTransactions = await Promise.all(
      transactions.map(async (t) => {
        const user = await storage.getUser(t.userId);
        return {
          ...t,
          userName: user?.fullName || user?.username,
          userEmail: user?.email,
        };
      })
    );

    res.json(enhancedTransactions);
  } catch (error: any) {
    res.status(500).json({ message: "Error fetching transactions: " + error.message });
  }
});

// Get system stats (enhanced version)
router.get("/stats", async (req, res) => {
  try {
    const stats = await storage.getSystemStats();
    res.json(stats);
  } catch (error: any) {
    res.status(500).json({ message: "Error fetching stats: " + error.message });
  }
});

// Update user email
router.post("/users/:id/update-email", async (req, res) => {
  try {
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { email } = req.body;

    if (!email || !email.includes("@")) {
      return res.status(400).json({ message: "Invalid email address" });
    }

    const updatedUser = await storage.updateUserEmail(id, email);
    if (!updatedUser) {
      return res.status(404).json({ message: "User not found" });
    }

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "update_email",
      details: { email },
    });

    res.json(updatedUser);
  } catch (error: any) {
    res.status(500).json({ message: "Error updating email: " + error.message });
  }
});

// Update user password
router.post("/users/:id/update-password", async (req, res) => {
  try {
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { password } = req.body;

    if (!password || password.length < 6) {
      return res.status(400).json({ message: "Password must be at least 6 characters" });
    }

    // Hash the password
    const bcrypt = require("bcryptjs");
    const hashedPassword = await bcrypt.hash(password, 10);

    const updatedUser = await storage.updateUserPassword(id, hashedPassword);
    if (!updatedUser) {
      return res.status(404).json({ message: "User not found" });
    }

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "update_password",
      details: { passwordChanged: true },
    });

    res.json({ message: "Password updated successfully" });
  } catch (error: any) {
    res.status(500).json({ message: "Error updating password: " + error.message });
  }
});

// Toggle admin privileges
router.post("/users/:id/toggle-admin", async (req, res) => {
  try {
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { isAdmin } = req.body;

    // Prevent removing last admin
    if (!isAdmin) {
      const allUsers = await storage.getAllUsers();
      const adminCount = allUsers.filter(u => u.isAdmin).length;
      if (adminCount <= 1) {
        return res.status(400).json({ message: "Cannot remove last admin" });
      }
    }

    const updatedUser = await storage.setUserAdmin(id, isAdmin);
    if (!updatedUser) {
      return res.status(404).json({ message: "User not found" });
    }

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: isAdmin ? "grant_admin" : "revoke_admin",
      details: { isAdmin },
    });

    res.json(updatedUser);
  } catch (error: any) {
    res.status(500).json({ message: "Error toggling admin status: " + error.message });
  }
});

// Reset user credits to specific amount
router.post("/users/:id/reset-credits", async (req, res) => {
  try {
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { amount } = req.body;

    if (amount === undefined || amount < 0) {
      return res.status(400).json({ message: "Invalid credit amount" });
    }

    const updatedUser = await storage.resetUserCredits(id, amount);
    if (!updatedUser) {
      return res.status(404).json({ message: "User not found" });
    }

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "reset_credits",
      details: { amount },
    });

    res.json(updatedUser);
  } catch (error: any) {
    res.status(500).json({ message: "Error resetting credits: " + error.message });
  }
});

// Suspend user account
router.post("/users/:id/suspend", async (req, res) => {
  try {
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { reason } = req.body;

    const updatedUser = await storage.suspendUser(id, reason);
    if (!updatedUser) {
      return res.status(404).json({ message: "User not found" });
    }

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "suspend_account",
      details: { reason },
    });

    res.json(updatedUser);
  } catch (error: any) {
    res.status(500).json({ message: "Error suspending account: " + error.message });
  }
});

// Get user credit history
router.get("/users/:id/credit-history", async (req, res) => {
  try {
    const { id } = req.params;
    const history = await storage.getUserCreditHistory(id);
    res.json(history);
  } catch (error: any) {
    res.status(500).json({ message: "Error fetching credit history: " + error.message });
  }
});

// Pause user account
router.post("/users/:id/pause", async (req, res) => {
  try {
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { reason } = req.body;

    if (!reason) {
      return res.status(400).json({ message: "Reason is required for pausing account" });
    }

    const updatedUser = await storage.pauseUser(id, reason);
    if (!updatedUser) {
      return res.status(404).json({ message: "User not found" });
    }

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "pause_account",
      details: { reason },
    });

    res.json({ message: "User account paused", user: updatedUser });
  } catch (error: any) {
    res.status(500).json({ message: "Error pausing account: " + error.message });
  }
});

// Unpause user account
router.post("/users/:id/unpause", async (req, res) => {
  try {
    const adminId = (req as any).adminId;
    const { id } = req.params;

    const updatedUser = await storage.unpauseUser(id);
    if (!updatedUser) {
      return res.status(404).json({ message: "User not found" });
    }

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "unpause_account",
      details: {},
    });

    res.json({ message: "User account unpaused", user: updatedUser });
  } catch (error: any) {
    res.status(500).json({ message: "Error unpausing account: " + error.message });
  }
});

// Send message to user
router.post("/users/:id/message", async (req, res) => {
  try {
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { title, message, requiresPopup = true } = req.body;

    if (!title || !message) {
      return res.status(400).json({ message: "Title and message are required" });
    }

    const notification = await storage.sendMessageToUser(id, title, message, requiresPopup);

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "send_message",
      details: { title, message, requiresPopup },
    });

    res.json({ message: "Message sent", notification });
  } catch (error: any) {
    res.status(500).json({ message: "Error sending message: " + error.message });
  }
});

// Update trial period
router.patch("/users/:id/trial", async (req, res) => {
  try {
    const adminId = (req as any).adminId;
    const { id } = req.params;
    const { endDate, daysToAdd } = req.body;

    let newEndDate: Date;

    if (endDate) {
      newEndDate = new Date(endDate);
    } else if (daysToAdd) {
      const user = await storage.getUser(id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const currentEndDate = user.trialEndDate || user.trialEndsAt || new Date();
      newEndDate = new Date(currentEndDate);
      newEndDate.setDate(newEndDate.getDate() + daysToAdd);
    } else {
      return res.status(400).json({ message: "Either endDate or daysToAdd is required" });
    }

    const updatedUser = await storage.updateTrialPeriod(id, newEndDate);
    if (!updatedUser) {
      return res.status(404).json({ message: "User not found" });
    }

    // Log admin action
    await storage.logAdminAction({
      adminUserId: adminId || null,
      targetUserId: id,
      action: "update_trial",
      details: { newEndDate, daysToAdd },
    });

    res.json({ message: "Trial period updated", user: updatedUser });
  } catch (error: any) {
    res.status(500).json({ message: "Error updating trial: " + error.message });
  }
});

export default router;

