/**
 * X.com (Twitter) Integration
 * Uses OAuth 1.0a for posting tweets
 */

import crypto from 'crypto';
import axios from 'axios';

// X.com API credentials from environment
const credentials = {
  apiKey: process.env.X_API_KEY || '',
  apiKeySecret: process.env.X_API_KEY_SECRET || '',
  accessToken: process.env.X_ACCESS_TOKEN || '',
  accessTokenSecret: process.env.X_ACCESS_TOKEN_SECRET || '',
  bearerToken: process.env.X_BEARER_TOKEN || '',
};

// Validate credentials are present
function validateCredentials(): boolean {
  return !!(credentials.apiKey && credentials.apiKeySecret && 
           credentials.accessToken && credentials.accessTokenSecret);
}

interface OAuthParams {
  oauth_consumer_key: string;
  oauth_nonce: string;
  oauth_signature?: string;
  oauth_signature_method: string;
  oauth_timestamp: string;
  oauth_token: string;
  oauth_version: string;
}

function generateNonce(): string {
  return crypto.randomBytes(32).toString('base64').replace(/[^a-zA-Z0-9]/g, '');
}

function generateTimestamp(): string {
  return Math.floor(Date.now() / 1000).toString();
}

function percentEncode(str: string): string {
  return encodeURIComponent(str)
    .replace(/[!'()*]/g, (c) => '%' + c.charCodeAt(0).toString(16).toUpperCase());
}

function generateSignature(
  method: string,
  url: string,
  params: Record<string, string>,
  consumerSecret: string,
  tokenSecret: string
): string {
  // Sort parameters alphabetically
  const sortedParams = Object.keys(params)
    .sort()
    .map(key => `${percentEncode(key)}=${percentEncode(params[key])}`)
    .join('&');

  // Create signature base string
  const signatureBase = `${method.toUpperCase()}&${percentEncode(url)}&${percentEncode(sortedParams)}`;

  // Create signing key
  const signingKey = `${percentEncode(consumerSecret)}&${percentEncode(tokenSecret)}`;

  // Generate signature
  const signature = crypto
    .createHmac('sha1', signingKey)
    .update(signatureBase)
    .digest('base64');

  return signature;
}

function generateOAuthHeader(params: OAuthParams): string {
  const headerParams = Object.keys(params)
    .sort()
    .map(key => `${percentEncode(key)}="${percentEncode(params[key as keyof OAuthParams] || '')}"`)
    .join(', ');

  return `OAuth ${headerParams}`;
}

export async function postToX(content: string, mediaIds?: string[]): Promise<any> {
  if (!validateCredentials()) {
    return {
      success: false,
      error: 'X.com credentials not configured. Please set X_API_KEY, X_API_KEY_SECRET, X_ACCESS_TOKEN, and X_ACCESS_TOKEN_SECRET environment variables.'
    };
  }

  try {
    const url = 'https://api.twitter.com/2/tweets';
    const method = 'POST';

    // Generate OAuth parameters
    const oauthParams: OAuthParams = {
      oauth_consumer_key: credentials.apiKey,
      oauth_nonce: generateNonce(),
      oauth_signature_method: 'HMAC-SHA1',
      oauth_timestamp: generateTimestamp(),
      oauth_token: credentials.accessToken,
      oauth_version: '1.0',
    };

    // Create request body
    const requestBody: any = {
      text: content,
    };

    if (mediaIds && mediaIds.length > 0) {
      requestBody.media = {
        media_ids: mediaIds
      };
    }

    // For OAuth 1.0a with JSON body, we don't include body params in signature
    const signatureParams = { ...oauthParams };

    // Generate signature
    oauthParams.oauth_signature = generateSignature(
      method,
      url,
      signatureParams,
      credentials.apiKeySecret,
      credentials.accessTokenSecret
    );

    // Generate OAuth header
    const authHeader = generateOAuthHeader(oauthParams);

    // Make the request
    const response = await axios({
      method,
      url,
      headers: {
        'Authorization': authHeader,
        'Content-Type': 'application/json',
        'User-Agent': 'MyAiMediaMgr/1.0',
      },
      data: requestBody,
    });

    return {
      success: true,
      data: response.data,
      tweetId: response.data.data?.id,
      url: `https://x.com/i/web/status/${response.data.data?.id}`,
    };
  } catch (error: any) {
    console.error('X.com posting error:', error.response?.data || error.message);

    // Fallback to v1.1 API if v2 fails
    if (error.response?.status === 403 || error.response?.status === 401) {
      return postToXv1(content);
    }

    return {
      success: false,
      error: error.response?.data || error.message,
    };
  }
}

// Fallback to v1.1 API
async function postToXv1(content: string): Promise<any> {
  try {
    const url = 'https://api.twitter.com/1.1/statuses/update.json';
    const method = 'POST';

    const oauthParams: OAuthParams = {
      oauth_consumer_key: credentials.apiKey,
      oauth_nonce: generateNonce(),
      oauth_signature_method: 'HMAC-SHA1',
      oauth_timestamp: generateTimestamp(),
      oauth_token: credentials.accessToken,
      oauth_version: '1.0',
    };

    // For v1.1, include the status parameter in signature
    const requestParams = {
      status: content,
    };

    const signatureParams = { ...oauthParams, ...requestParams };

    // Generate signature
    oauthParams.oauth_signature = generateSignature(
      method,
      url,
      signatureParams,
      credentials.apiKeySecret,
      credentials.accessTokenSecret
    );

    // Generate OAuth header
    const authHeader = generateOAuthHeader(oauthParams);

    // Make the request with form-encoded body
    const response = await axios({
      method,
      url,
      headers: {
        'Authorization': authHeader,
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      data: `status=${percentEncode(content)}`,
    });

    return {
      success: true,
      data: response.data,
      tweetId: response.data.id_str,
      url: `https://x.com/i/web/status/${response.data.id_str}`,
    };
  } catch (error: any) {
    console.error('X.com v1.1 posting error:', error.response?.data || error.message);
    return {
      success: false,
      error: error.response?.data || error.message,
    };
  }
}

// Test connection by verifying credentials
export async function verifyXCredentials(): Promise<boolean> {
  if (!validateCredentials()) {
    console.error('X.com credentials not configured');
    return false;
  }

  try {
    const url = 'https://api.twitter.com/1.1/account/verify_credentials.json';

    const oauthParams: OAuthParams = {
      oauth_consumer_key: credentials.apiKey,
      oauth_nonce: generateNonce(),
      oauth_signature_method: 'HMAC-SHA1',
      oauth_timestamp: generateTimestamp(),
      oauth_token: credentials.accessToken,
      oauth_version: '1.0',
    };

    oauthParams.oauth_signature = generateSignature(
      'GET',
      url,
      oauthParams as unknown as Record<string, string>,
      credentials.apiKeySecret,
      credentials.accessTokenSecret
    );

    const authHeader = generateOAuthHeader(oauthParams);

    const response = await axios({
      method: 'GET',
      url,
      headers: {
        'Authorization': authHeader,
      },
    });

    console.log('X.com connected as @' + response.data.screen_name);
    return true;
  } catch (error: any) {
    console.error('X.com verification failed:', error.message);
    return false;
  }
}

server/x-oauth.ts.content:
/**
 * X.com OAuth 2.0 Implementation
 * Allows users to connect their X accounts to the platform
 */

import crypto from 'crypto';
import { storage } from './storage';

// OAuth 2.0 configuration
const domain = process.env.REPLIT_DOMAINS?.split(',')[0] || 'localhost:5000';
const protocol = domain.includes('localhost') ? 'http' : 'https';

const X_OAUTH_CONFIG = {
  clientId: process.env.X_CLIENT_ID || '',
  clientSecret: process.env.X_CLIENT_SECRET || '',
  redirectUri: process.env.X_REDIRECT_URI || `${protocol}://${domain}/api/auth/x/callback`,
  authorizationUrl: 'https://twitter.com/i/oauth2/authorize',
  tokenUrl: 'https://api.twitter.com/2/oauth2/token',
  scopes: ['tweet.read', 'tweet.write', 'users.read', 'offline.access'],
};

// Store OAuth states temporarily (in production, use Redis or database)
const oauthStates = new Map<string, { userId: string; timestamp: number }>();

// Generate PKCE challenge
function generatePKCEChallenge() {
  const verifier = crypto.randomBytes(32).toString('base64url');
  const challenge = crypto
    .createHash('sha256')
    .update(verifier)
    .digest('base64url');
  return { verifier, challenge };
}

/**
 * Generate OAuth authorization URL for X.com
 */
export function generateXAuthUrl(userId: string): { url: string; state: string; codeVerifier: string } {
  const state = crypto.randomBytes(32).toString('hex');
  const { verifier, challenge } = generatePKCEChallenge();

  // Store state for verification
  oauthStates.set(state, {
    userId,
    timestamp: Date.now(),
  });

  // Clean up old states (older than 10 minutes)
  for (const [key, value] of oauthStates.entries()) {
    if (Date.now() - value.timestamp > 600000) {
      oauthStates.delete(key);
    }
  }

  const params = new URLSearchParams({
    response_type: 'code',
    client_id: X_OAUTH_CONFIG.clientId,
    redirect_uri: X_OAUTH_CONFIG.redirectUri,
    scope: X_OAUTH_CONFIG.scopes.join(' '),
    state,
    code_challenge: challenge,
    code_challenge_method: 'S256',
  });

  return {
    url: `${X_OAUTH_CONFIG.authorizationUrl}?${params.toString()}`,
    state,
    codeVerifier: verifier,
  };
}

/**
 * Exchange authorization code for access tokens
 */
export async function exchangeCodeForTokens(
  code: string,
  codeVerifier: string
): Promise<{
  accessToken: string;
  refreshToken: string;
  expiresIn: number;
  scope: string;
}> {
  const params = new URLSearchParams({
    grant_type: 'authorization_code',
    code,
    redirect_uri: X_OAUTH_CONFIG.redirectUri,
    code_verifier: codeVerifier,
  });

  const authHeader = Buffer.from(
    `${X_OAUTH_CONFIG.clientId}:${X_OAUTH_CONFIG.clientSecret}`
  ).toString('base64');

  const response = await fetch(X_OAUTH_CONFIG.tokenUrl, {
    method: 'POST',
    headers: {
      'Authorization': `Basic ${authHeader}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    },
    body: params.toString(),
  });

  if (!response.ok) {
    const error = await response.text();
    throw new Error(`Failed to exchange code for tokens: ${error}`);
  }

  const data = await response.json();

  return {
    accessToken: data.access_token,
    refreshToken: data.refresh_token,
    expiresIn: data.expires_in,
    scope: data.scope,
  };
}

/**
 * Get user information from X.com
 */
export async function getXUserInfo(accessToken: string): Promise<{
  id: string;
  username: string;
  name: string;
}> {
  const response = await fetch('https://api.twitter.com/2/users/me', {
    headers: {
      'Authorization': `Bearer ${accessToken}`,
    },
  });

  if (!response.ok) {
    throw new Error('Failed to get user information from X');
  }

  const data = await response.json();

  return {
    id: data.data.id,
    username: data.data.username,
    name: data.data.name,
  };
}

/**
 * Refresh access token using refresh token
 */
export async function refreshXAccessToken(refreshToken: string): Promise<{
  accessToken: string;
  refreshToken: string;
  expiresIn: number;
}> {
  const params = new URLSearchParams({
    grant_type: 'refresh_token',
    refresh_token: refreshToken,
  });

  const authHeader = Buffer.from(
    `${X_OAUTH_CONFIG.clientId}:${X_OAUTH_CONFIG.clientSecret}`
  ).toString('base64');

  const response = await fetch(X_OAUTH_CONFIG.tokenUrl, {
    method: 'POST',
    headers: {
      'Authorization': `Basic ${authHeader}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    },
    body: params.toString(),
  });

  if (!response.ok) {
    throw new Error('Failed to refresh access token');
  }

  const data = await response.json();

  return {
    accessToken: data.access_token,
    refreshToken: data.refresh_token,
    expiresIn: data.expires_in,
  };
}

/**
 * Handle OAuth callback from X.com
 */
export async function handleXOAuthCallback(
  code: string,
  state: string,
  codeVerifier: string
): Promise<{ success: boolean; userId?: string; error?: string }> {
  try {
    // Verify state
    const stateData = oauthStates.get(state);
    if (!stateData) {
      return { success: false, error: 'Invalid state parameter' };
    }

    // Clean up state
    oauthStates.delete(state);

    // Exchange code for tokens
    const tokens = await exchangeCodeForTokens(code, codeVerifier);

    // Get user info
    const userInfo = await getXUserInfo(tokens.accessToken);

    // Store platform connection
    await storage.createPlatform({
      userId: stateData.userId,
      name: 'X (Twitter)',
      username: `@${userInfo.username}`,
      isConnected: true,
      accountId: userInfo.id,
      accessToken: tokens.accessToken,
      refreshToken: tokens.refreshToken,
      expiresAt: new Date(Date.now() + tokens.expiresIn * 1000),
    });

    return { success: true, userId: stateData.userId };
  } catch (error: any) {
    console.error('OAuth callback error:', error);
    return { success: false, error: error.message };
  }
}

/**
 * Post to X.com using OAuth 2.0
 */
export async function postToXWithOAuth(
  accessToken: string,
  content: string
): Promise<{ success: boolean; tweetId?: string; error?: string }> {
  try {
    const response = await fetch('https://api.twitter.com/2/tweets', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${accessToken}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        text: content,
      }),
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`Failed to post tweet: ${error}`);
    }

    const data = await response.json();

    return {
      success: true,
      tweetId: data.data.id,
    };
  } catch (error: any) {
    console.error('Error posting to X:', error);
    return {
      success: false,
      error: error.message,
    };
  }
}

