import { storage } from "./storage";
import { randomUUID } from "crypto";

export async function saveToLibrary(options: {
  userId: string;
  type: "image" | "video" | "text"; 
  dataBase64?: string;
  url?: string;
  meta?: any;
}) {
  const { userId, type: kind, url, dataBase64, meta } = options;

  // Save to content library
  const libraryItem = {
    userId,
    type: kind === 'text' ? 'image' : kind, // Store text as image type for compatibility
    url: url || (kind === 'text' ? '' : `/generated/${randomUUID()}.${kind === 'video' ? 'mp4' : 'png'}`),
    caption: meta?.caption || meta?.content || meta?.prompt || '',
    metadata: {
      ...meta,
      contentType: kind // Store actual type in metadata
    },
    tags: meta?.hashtags || [],
    businessName: meta?.businessName,
    productName: meta?.productName,
    platform: meta?.platform
  };

  return storage.createContentLibraryItem(libraryItem);
}

server/libraryRoutes.ts.content:
import { Router, Request, Response } from 'express';
import { z } from 'zod';
import { requireAuth } from './auth';
import type { IStorage } from './storage';

const getLibrarySchema = z.object({
  kind: z.enum(['image', 'video', 'text']).optional(),
  campaign: z.string().optional(),
  startDate: z.string().optional(),
  endDate: z.string().optional(),
  platform: z.string().optional(),
});

export function createLibraryRoutes(storage: IStorage) {
  const router = Router();

  // GET /api/library - Get library items with filters
  router.get('/api/library', requireAuth, async (req: Request, res: Response) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ error: 'Unauthorized' });
      }

      const filters = getLibrarySchema.parse(req.query);

      // Get all content library items for the user
      const items = await storage.getContentLibraryByUserId(userId);

      // Apply filters
      let filteredItems = items;

      if (filters.kind) {
        // For text filter, return empty array (text is not saved to library)
        if (filters.kind === 'text') {
          return res.json([]);
        }
        filteredItems = filteredItems.filter(item => item.type === filters.kind);
      }

      if (filters.platform) {
        filteredItems = filteredItems.filter(item => item.platform === filters.platform);
      }

      if (filters.startDate) {
        const startDate = new Date(filters.startDate);
        filteredItems = filteredItems.filter(item => {
          if (!item.createdAt) return false;
          return new Date(item.createdAt) >= startDate;
        });
      }

      if (filters.endDate) {
        const endDate = new Date(filters.endDate);
        filteredItems = filteredItems.filter(item => {
          if (!item.createdAt) return false;
          return new Date(item.createdAt) <= endDate;
        });
      }

      // Sort by creation date, newest first
      filteredItems.sort((a, b) => {
        const aTime = a.createdAt ? new Date(a.createdAt).getTime() : 0;
        const bTime = b.createdAt ? new Date(b.createdAt).getTime() : 0;
        return bTime - aTime;
      });

      res.json(filteredItems);
    } catch (error) {
      console.error('Get library error:', error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: 'Invalid query parameters', details: error.errors });
      }
      res.status(500).json({ error: 'Failed to fetch library' });
    }
  });

  // DELETE /api/library/:id - Delete a library item
  router.delete('/api/library/:id', requireAuth, async (req: Request, res: Response) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ error: 'Unauthorized' });
      }

      const { id } = req.params;

      // Get the item to verify ownership
      const items = await storage.getContentLibraryByUserId(userId);
      const item = items.find(i => i.id === id);

      if (!item) {
        return res.status(404).json({ error: 'Library item not found' });
      }

      // Only allow deletion of own items
      const user = await storage.getUser(userId);
      if (item.userId !== userId && user?.role !== 'admin') {
        return res.status(403).json({ error: 'Forbidden' });
      }

      // Delete the item
      await storage.deleteContentLibraryItem(id);

      res.json({ success: true, message: 'Library item deleted' });
    } catch (error) {
      console.error('Delete library item error:', error);
      res.status(500).json({ error: 'Failed to delete library item' });
    }
  });

  // GET /api/library/:id - Get a single library item
  router.get('/api/library/:id', requireAuth, async (req: Request, res: Response) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ error: 'Unauthorized' });
      }

      const { id } = req.params;

      // Get the item
      const items = await storage.getContentLibraryByUserId(userId);
      const item = items.find(i => i.id === id);

      if (!item) {
        return res.status(404).json({ error: 'Library item not found' });
      }

      // Check access permissions
      const user = await storage.getUser(userId);
      if (item.userId !== userId && user?.role !== 'admin') {
        return res.status(403).json({ error: 'Forbidden' });
      }

      res.json(item);
    } catch (error) {
      console.error('Get library item error:', error);
      res.status(500).json({ error: 'Failed to fetch library item' });
    }
  });

  return router;
}

server/campaignRoutes.ts.content:
import { Router, Request, Response } from 'express';
import { z } from 'zod';
import { requireAuth } from './auth';
import type { IStorage } from './storage';
import { saveToLibrary } from './library';
import { generateHighQualityPost } from './content/quality';
import { PostType } from './content/templates';
import { Platform } from './content/config';
import { BrandProfile } from '@shared/schema';
import { generateImage } from './ai/image';

// Declare global campaign progress tracking
declare global {
  var campaignProgress: Record<string, {
    total: number;
    current: number;
    status: string;
    posts: any[];
    userId?: string;
  }>;
}

const generateCampaignSchema = z.object({
  prompt: z.string().min(1, "Campaign prompt/theme is required"),
  start_date: z.string().datetime(),
  cadence: z.enum(["2_per_day_7_days"]).default("2_per_day_7_days"),
  businessName: z.string().optional(),
  productName: z.string().optional(),
  targetAudience: z.string().optional(),
  brandTone: z.string().optional(),
  keyMessages: z.array(z.string()).optional(),
  callToAction: z.string().optional(),
});

const applyCampaignScheduleSchema = z.object({
  morningSlot: z.string().regex(/^\d{2}:\d{2}$/).default("10:00"),
  afternoonSlot: z.string().regex(/^\d{2}:\d{2}$/).default("16:00"),
  timezone: z.string().default("UTC"),
});

export function createCampaignRoutes(storage: IStorage) {
  const router = Router();

  // Helper function to generate campaign posts in the background
  async function generateCampaignPostsAsync(
    campaignId: string,
    userId: string,
    params: any,
    brandProfile: BrandProfile,
    storage: IStorage
  ) {
    const progressKey = `campaign_progress_${campaignId}`;
    const posts = [];
    const startDate = new Date(params.start_date);
    const morningHour = 10;
    const afternoonHour = 16;

    const postTypeRotation: PostType[] = [
      'promo', 'tutorial', 'testimonial', 'promo', 
      'faq', 'announcement', 'promo',
      'testimonial', 'tutorial', 'promo',
      'seasonal', 'faq', 'promo', 'event'
    ];

    const platforms: Platform[] = ['instagram', 'facebook', 'x'];
    let postIndex = 0;
    const priorCaptions: string[] = [];
    const delay = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));

    try {
      for (let day = 0; day < 7; day++) {
        for (let slot = 0; slot < 2; slot++) {
          const scheduledDate = new Date(startDate);
          scheduledDate.setDate(startDate.getDate() + day);
          scheduledDate.setHours(slot === 0 ? morningHour : afternoonHour, 0, 0, 0);

          const postType = postTypeRotation[postIndex % postTypeRotation.length];
          const platform = platforms[postIndex % platforms.length];

          // Generate content
          let content = '';
          let hashtags: string[] = [];

          try {
            const result = await generateHighQualityPost({
              platform,
              postType,
              brand: brandProfile,
              campaignTheme: params.prompt,
              product: params.productName,
              desiredTone: brandProfile.voice,
              callToAction: params.callToAction,
              priorCaptions
            });

            if (result.ok) {
              content = result.best.caption;
              hashtags = result.best.hashtags;
              if (result.best.cta) {
                content += `\n\n${result.best.cta}`;
              }
              priorCaptions.push(result.best.caption);
            } else {
              content = `${params.prompt} - Post ${postIndex + 1}`;
              hashtags = ['#business', '#growth'];
            }
          } catch (err) {
            console.error('Quality generation failed:', err);
            content = `${params.prompt} - Post ${postIndex + 1}`;
            hashtags = ['#business', '#growth'];
          }

          if (hashtags.length > 0) {
            content += '\n\n' + hashtags.join(' ');
          }

          // Update progress
          global.campaignProgress[progressKey].current = postIndex;
          global.campaignProgress[progressKey].status = `Generating image ${postIndex + 1} of 14...`;

          // Add delay between images
          if (postIndex > 0) {
            await delay(3000);
          }

          // Generate image
          let imageUrl: string | undefined;
          try {
            const imagePrompt = `Professional social media image for ${params.businessName || 'business'}: ${content.substring(0, 80)}. Style: modern, clean, engaging.`;
            const imageResult = await generateImage({
              prompt: imagePrompt,
              aspectRatio: platform === 'instagram' ? '1:1' : '16:9',
              model: 'auto'
            });
            imageUrl = imageResult.url;

            if (userId && imageUrl) {
              await saveToLibrary({
                userId,
                type: 'image',
                url: imageUrl,
                meta: {
                  prompt: imagePrompt,
                  caption: content,
                  platform,
                  campaignId,
                  postType,
                  aspectRatio: imageResult.aspectRatio
                }
              });
            }
          } catch (imageError) {
            console.error('Image generation failed:', imageError);
          }

          const post = await storage.createPost({
            userId,
            campaignId,
            content,
            platform,
            platforms: [platform],
            status: 'draft',
            scheduledFor: scheduledDate,
            aiGenerated: true,
            imageUrl,
            metadata: {
              day: day + 1,
              slot: slot + 1,
              campaignPost: true,
              postType,
              qualityGenerated: true,
              hasImage: !!imageUrl
            },
          });

          posts.push(post);
          global.campaignProgress[progressKey].posts.push(post);
          global.campaignProgress[progressKey].current = postIndex + 1;
          global.campaignProgress[progressKey].status = `Created post ${postIndex + 1} of 14`;

          postIndex++;
        }
      }

      // Update campaign as complete
      await storage.updateCampaign(campaignId, {
        generationProgress: 100,
        status: 'review',
      });

      global.campaignProgress[progressKey].status = 'Complete';

      // Keep progress for 5 minutes after completion
      setTimeout(() => {
        delete global.campaignProgress[progressKey];
      }, 5 * 60 * 1000);

    } catch (error) {
      console.error('Background generation error:', error);
      global.campaignProgress[progressKey].status = 'Failed';
      await storage.updateCampaign(campaignId, {
        status: 'failed',
      });
    }
  }

  // POST /api/campaigns/generate - Start campaign generation
  router.post('/api/campaigns/generate', requireAuth, async (req: Request, res: Response) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ error: 'Unauthorized' });
      }

      const params = generateCampaignSchema.parse(req.body);

      // Check if user has enough credits for campaign generation
      // 14 posts Ã— (5 credits for image + 1 credit for text) = 84 credits total
      const CAMPAIGN_CREDIT_COST = 84;
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(401).json({ error: 'User not found' });
      }

      // Check if user has sufficient credits
      if ((user.credits ?? 0) < CAMPAIGN_CREDIT_COST) {
        return res.status(402).json({ 
          error: 'Insufficient credits for campaign generation',
          message: `Campaign generation requires ${CAMPAIGN_CREDIT_COST} credits (14 posts with images). You have ${user.credits ?? 0} credits.`,
          required: CAMPAIGN_CREDIT_COST,
          have: user.credits ?? 0
        });
      }

      // Deduct credits upfront for the entire campaign
      await storage.updateUser(userId, {
        credits: Math.max(0, (user.credits ?? 0) - CAMPAIGN_CREDIT_COST),
        totalCreditsUsed: (user.totalCreditsUsed ?? 0) + CAMPAIGN_CREDIT_COST
      });

      // Get brand profile for better content generation
      let brandProfile = await storage.getBrandProfile(userId);

      // If no brand profile exists, create a basic one from campaign params
      if (!brandProfile) {
        brandProfile = {
          id: 'temp',
          userId,
          brandName: params.businessName || 'Business',
          voice: (params.brandTone as any) || 'friendly',
          targetAudience: params.targetAudience || 'General audience',
          products: params.productName ? [params.productName] : [],
          valueProps: [],
          bannedPhrases: [],
          requiredDisclaimers: [],
          preferredCTAs: params.callToAction ? [params.callToAction] : ['Learn More'],
          keywords: params.keyMessages || [],
          createdAt: new Date(),
          updatedAt: new Date()
        };
      }

      // Create the campaign
      const campaign = await storage.createCampaign({
        userId,
        name: `Campaign: ${params.prompt}`,
        description: `Auto-generated campaign for: ${params.prompt}`,
        platform: 'multi',
        businessName: params.businessName || 'Business',
        productName: params.productName || '',
        targetAudience: params.targetAudience || 'General audience',
        campaignGoals: 'Engagement and brand awareness',
        brandTone: params.brandTone || 'professional',
        keyMessages: params.keyMessages || [],
        platforms: ['instagram', 'facebook', 'twitter'],
        visualStyle: 'modern',
        colorScheme: 'brand colors',
        callToAction: params.callToAction || 'Learn more',
        status: 'draft',
        startDate: new Date(params.start_date),
        endDate: new Date(new Date(params.start_date).getTime() + 7 * 24 * 60 * 60 * 1000), // 7 days later
        postsPerDay: 2,
        totalPosts: 14,
        generationProgress: 0,
      });

      // Initialize progress tracking
      const progressKey = `campaign_progress_${campaign.id}`;
      if (!global.campaignProgress) {
        global.campaignProgress = {};
      }
      global.campaignProgress[progressKey] = {
        total: 14,
        current: 0,
        status: 'Starting generation...',
        posts: [],
        userId, // Store userId for ownership check
      };

      // Start generation in the background (non-blocking)
      setImmediate(async () => {
        await generateCampaignPostsAsync(
          campaign.id,
          userId,
          params,
          brandProfile,
          storage
        );
      });

      // Return immediately with campaign ID for polling
      res.json({
        campaignId: campaign.id,
        message: 'Campaign generation started. Poll /api/campaigns/:id/progress for updates.',
        campaign,
      });
    } catch (error) {
      console.error('Generate campaign error:', error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: 'Invalid request data', details: error.errors });
      }
      res.status(500).json({ error: 'Failed to generate campaign' });
    }
  });

  // PUT /api/campaigns/:id/apply-schedule - Adjust post schedules
  router.put('/api/campaigns/:id/apply-schedule', requireAuth, async (req: Request, res: Response) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ error: 'Unauthorized' });
      }

      const { id } = req.params;
      const schedule = applyCampaignScheduleSchema.parse(req.body);

      // Get the campaign
      const campaign = await storage.getCampaign(id);
      if (!campaign) {
        return res.status(404).json({ error: 'Campaign not found' });
      }

      // Verify ownership
      const user = await storage.getUser(userId);
      if (campaign.userId !== userId && user?.role !== 'admin') {
        return res.status(403).json({ error: 'Forbidden' });
      }

      // Get all posts for this campaign
      const posts = await storage.getPostsByCampaignId(id);

      // Parse time slots
      const [morningHour, morningMinute] = schedule.morningSlot.split(':').map(Number);
      const [afternoonHour, afternoonMinute] = schedule.afternoonSlot.split(':').map(Number);

      // Check for overlaps with existing scheduled posts
      const updatedPosts = [];
      for (const post of posts) {
        if (post.scheduledFor) {
          const scheduledDate = new Date(post.scheduledFor);
          const metadata = post.metadata as any;

          if (metadata?.slot === 1) {
            // Morning slot
            scheduledDate.setHours(morningHour, morningMinute, 0, 0);
          } else if (metadata?.slot === 2) {
            // Afternoon slot
            scheduledDate.setHours(afternoonHour, afternoonMinute, 0, 0);
          }

          // Check for conflicts with other scheduled posts
          const conflictingPost = await storage.getScheduledPostAtTime(userId, scheduledDate);
          if (conflictingPost && conflictingPost.id !== post.id) {
            return res.status(422).json({ 
              error: 'Schedule conflict', 
              message: `Post at ${scheduledDate.toISOString()} conflicts with existing scheduled post` 
            });
          }

          // Update the post schedule
          await storage.updatePost(post.id, {
            scheduledFor: scheduledDate,
          });

          updatedPosts.push({ ...post, scheduledFor: scheduledDate });
        }
      }

      res.json({
        campaignId: id,
        updatedCount: updatedPosts.length,
        posts: updatedPosts,
      });
    } catch (error) {
      console.error('Apply schedule error:', error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: 'Invalid request data', details: error.errors });
      }
      res.status(500).json({ error: 'Failed to apply schedule' });
    }
  });

  // GET /api/campaigns/:id/posts - Get all posts for a campaign
  router.get('/api/campaigns/:id/posts', requireAuth, async (req: Request, res: Response) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ error: 'Unauthorized' });
      }

      const { id } = req.params;

      // Get the campaign to verify access
      const campaign = await storage.getCampaign(id);
      if (!campaign) {
        return res.status(404).json({ error: 'Campaign not found' });
      }

      // Verify access
      const user = await storage.getUser(userId);
      if (campaign.userId !== userId && user?.role !== 'admin') {
        return res.status(403).json({ error: 'Forbidden' });
      }

      // Get all posts for this campaign
      const posts = await storage.getPostsByCampaignId(id);

      // Sort by scheduled date
      posts.sort((a, b) => {
        if (!a.scheduledFor || !b.scheduledFor) return 0;
        return new Date(a.scheduledFor).getTime() - new Date(b.scheduledFor).getTime();
      });

      res.json(posts);
    } catch (error) {
      console.error('Get campaign posts error:', error);
      res.status(500).json({ error: 'Failed to fetch campaign posts' });
    }
  });

  // GET /api/campaigns/:id/progress - Get campaign generation progress
  router.get('/api/campaigns/:id/progress', requireAuth, async (req: Request, res: Response) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ error: 'Unauthorized' });
      }

      const { id } = req.params;
      const progressKey = `campaign_progress_${id}`;

      // Check if progress exists and verify ownership
      if (global.campaignProgress?.[progressKey]) {
        const progress = global.campaignProgress[progressKey];

        // Security: Verify user owns this campaign progress
        const user = await storage.getUser(userId);
        if (progress.userId !== userId && user?.role !== 'admin') {
          return res.status(403).json({ error: 'Forbidden' });
        }

        // Don't send userId in response
        const { userId: _, ...safeProgress } = progress;
        res.json(safeProgress);
      } else {
        // Check if campaign exists and is complete
        const campaign = await storage.getCampaign(id);
        if (campaign && campaign.generationProgress === 100) {
          res.json({
            total: 14,
            current: 14,
            status: 'Complete',
            posts: []
          });
        } else {
          res.json({
            total: 14,
            current: 0,
            status: 'Not started',
            posts: []
          });
        }
      }
    } catch (error) {
      console.error('Get progress error:', error);
      res.status(500).json({ error: 'Failed to get progress' });
    }
  });

  return router;
}

server/content/config.ts.content:
// Platform + quality knobs. Tweak without code changes.
export type Platform = "x"|"instagram"|"facebook"|"linkedin"|"tiktok"|"youtubeShorts";
export type PostType = "promo"|"announcement"|"tutorial"|"testimonial"|"faq"|"event"|"seasonal";

export interface PlatformConstraints {
  maxChars: number;
  maxHashtags: number;
  allowedRatios: Array<"1:1"|"4:5"|"16:9"|"9:16">;
  readabilityMaxGrade: number; // â‰ˆ Flesch-Kincaid target
}

export const PLATFORM_CONSTRAINTS: Record<Platform, PlatformConstraints> = {
  x:              { maxChars: 260, maxHashtags: 4, allowedRatios: ["1:1","16:9"], readabilityMaxGrade: 8 },
  instagram:      { maxChars: 2200, maxHashtags: 5, allowedRatios: ["1:1","4:5","9:16"], readabilityMaxGrade: 8 },
  facebook:       { maxChars: 2000, maxHashtags: 5, allowedRatios: ["1:1","16:9"], readabilityMaxGrade: 8 },
  linkedin:       { maxChars: 3000, maxHashtags: 5, allowedRatios: ["1:1","16:9"], readabilityMaxGrade: 10 },
  tiktok:         { maxChars: 2200, maxHashtags: 5, allowedRatios: ["9:16"], readabilityMaxGrade: 7 },
  youtubeShorts:  { maxChars: 150,  maxHashtags: 3, allowedRatios: ["9:16"], readabilityMaxGrade: 7 },
};

export const DEFAULT_TONES = ["friendly","bold","professional","playful"] as const;
export type Tone = typeof DEFAULT_TONES[number];

export interface BrandProfile {
  brandName: string;
  voice?: Tone;
  targetAudience?: string;
  products?: string[];
  valueProps?: string[];
  bannedPhrases?: string[];
  requiredDisclaimers?: string[];
  preferredCTAs?: string[];
  keywords?: string[];
}

export const HASHTAG_BLOCKLIST = [
  /follow4follow/i, /like4like/i, /nsfw/i, /giveaway/i, /lottery/i, /crypto.*pump/i
];

export const READABILITY_DEFAULT = 8;

export function ratioFromPlatform(p: Platform): "1:1"|"4:5"|"16:9"|"9:16" {
  return PLATFORM_CONSTRAINTS[p].allowedRatios[0];
}

server/content/promptBuilders.ts.content:
import { BrandProfile, Platform, PLATFORM_CONSTRAINTS, Tone } from "./config";
import { templateFor, PostType } from "./templates";

export interface BuiltPrompt {
  system: string;
  user: string;
  constraints: { maxChars: number; maxHashtags: number; readabilityMaxGrade: number; };
}

export function buildPrompt(opts: {
  platform: Platform;
  postType: PostType;
  brand: BrandProfile;
  campaignTheme?: string;
  product?: string;
  desiredTone?: Tone;
  callToAction?: string;
}) : BuiltPrompt {
  const pc = PLATFORM_CONSTRAINTS[opts.platform];
  const tone = opts.desiredTone || opts.brand.voice || "friendly";
  const cta = opts.callToAction || (opts.brand.preferredCTAs?.[0] ?? "Learn more");
  const base = templateFor(opts.postType, opts.brand);

  const system = `You are a senior social media copywriter.
Write in a ${tone} voice for ${opts.brand.brandName || "the brand"}.
Honor platform constraints and avoid spammy language.`;

  const user = [
    base,
    opts.campaignTheme ? `Theme: ${opts.campaignTheme}.` : "",
    opts.product ? `Focus product/service: ${opts.product}.` : "",
    opts.brand.valueProps?.length ? `Value props: ${opts.brand.valueProps.join(", ")}.` : "",
    `Target audience: ${opts.brand.targetAudience || "small business owners"}.`,
    `Include 1-sentence hook, value, and CTA: "${cta}".`,
    `Add 3â€“5 niche hashtags relevant to the topic (no generic tags).`,
    `MAX ${pc.maxChars} chars. Reading grade â‰¤ ${pc.readabilityMaxGrade}.`,
  ].filter(Boolean).join("\n");

  return { system, user, constraints: {
    maxChars: pc.maxChars,
    maxHashtags: pc.maxHashtags,
    readabilityMaxGrade: pc.readabilityMaxGrade
  }};
}

server/content/quality.ts.content:
import { buildPrompt } from "./promptBuilders";
import { validateContent, Candidate } from "./validators";
import { BrandProfile, Platform, PlatformConstraints } from "./config";
import { PostType } from "./templates";
import { GoogleGenerativeAI } from "@google/generative-ai";
import { moderateContent } from "./moderation";

interface GenOpts {
  platform: Platform;
  postType: PostType;
  brand: BrandProfile;
  campaignTheme?: string;
  product?: string;
  desiredTone?: string;
  callToAction?: string;
  priorCaptions?: string[];
}

interface Score {
  overall: number;
  clarity: number;
  value: number;
  specificity: number;
  brandVoice: number;
  platformFit: number;
  actionability: number;
  feedback: string[];
}

export async function generateHighQualityPost(opts: GenOpts): Promise<{
  ok: true;
  best: Candidate;
  candidates: Candidate[];
  scores: Score[];
} | {
  ok: false;
  error: string;
  reasons: string[];
  coaching: string[];
}> {
  // 1) Build structured prompt
  const { system, user, constraints } = buildPrompt({
    platform: opts.platform,
    postType: opts.postType,
    brand: opts.brand,
    campaignTheme: opts.campaignTheme,
    product: opts.product,
    desiredTone: opts.desiredTone as any,
    callToAction: opts.callToAction
  });

  // 2) Generate N candidates with mild diversity
  const candidates = await generateCandidates(system, user);

  // 3) Critique & refine top 2
  const withScores = await Promise.all(
    candidates.map(async c => ({ c, score: await critique(c, opts.platform) }))
  );
  withScores.sort((a,b) => b.score.overall - a.score.overall);

  const refinedTop2 = await Promise.all(
    withScores.slice(0,2).map(async ({c,score}) => ({
      refined: await refine(c, score.feedback),
      preScore: score
    }))
  );
  const rescored = await Promise.all(
    refinedTop2.map(async (r) => ({ c: r.refined, score: await critique(r.refined, opts.platform) }))
  );
  rescored.sort((a,b) => b.score.overall - a.score.overall);

  // 4) Validate winner
  const winner = normalizeCandidate(rescored[0].c);
  const v = validateContent(
    winner,
    { 
      maxChars: constraints.maxChars, 
      maxHashtags: constraints.maxHashtags, 
      allowedRatios: ["1:1"], 
      readabilityMaxGrade: constraints.readabilityMaxGrade 
    },
    opts.priorCaptions || []
  );

  if (!v.ok) {
    // one auto-fix attempt: shorten & tighten
    const fixed = await tighten(winner, v.reasons);
    const v2 = validateContent(fixed, { 
      maxChars: constraints.maxChars, 
      maxHashtags: constraints.maxHashtags, 
      allowedRatios: ["1:1"], 
      readabilityMaxGrade: constraints.readabilityMaxGrade 
    }, opts.priorCaptions || []);

    if (!v2.ok) {
      return { 
        ok: false, 
        error: "validation_failed", 
        reasons: v2.reasons, 
        coaching: v2.coaching 
      };
    }

    // Safety check on fixed content
    const safety = await moderateContent(
      fixed.caption + '\n' + fixed.hashtags.join(' '), 
      opts.platform
    );

    if (safety.decision === "block") {
      return {
        ok: false,
        error: "content_policy_violation",
        reasons: safety.reasons,
        coaching: safety.coaching || []
      };
    }

    return { 
      ok: true, 
      best: fixed, 
      candidates: candidates.map(normalizeCandidate), 
      scores: rescored.map(s=>s.score),
      requiresReview: safety.decision === "review"
    };
  }

  // 5) Safety moderation
  const fullContent = winner.caption + '\n' + winner.hashtags.join(' ');
  const safety = await moderateContent(fullContent, opts.platform);

  if (safety.decision === "block") {
    // Try safe rewrite if available
    if (safety.safeRewrite) {
      const safeCandidate = normalizeCandidate(safety.safeRewrite);
      return {
        ok: true,
        best: safeCandidate,
        candidates: candidates.map(normalizeCandidate),
        scores: rescored.map(s=>s.score),
        wasRewritten: true,
        requiresReview: true
      };
    }

    return {
      ok: false,
      error: "content_policy_violation",
      reasons: safety.reasons,
      coaching: safety.coaching || []
    };
  }

  return { 
    ok: true, 
    best: winner, 
    candidates: candidates.map(normalizeCandidate), 
    scores: rescored.map(s=>s.score),
    requiresReview: safety.decision === "review"
  };
}

// --- helpers ---

async function generateCandidates(system: string, user: string): Promise<string[]> {
  const temps = [0.6, 0.7, 0.8];
  const outs: string[] = [];

  const ai = process.env.GEMINI_API_KEY 
    ? new GoogleGenerativeAI(process.env.GEMINI_API_KEY)
    : null;

  if (!ai) {
    // Fallback to simple generation
    return [
      `ðŸš€ Transform your business with smart automation! Our AI-powered tools help you save 5+ hours weekly on repetitive tasks. Start your free trial today and see results in 24 hours.\n\n#SmartAutomation #BusinessGrowth #Productivity #AITools #TimeSaver\n\nCTA: Start Free Trial`,
      `ðŸ’¡ Did you know? 73% of businesses using AI report increased productivity. Join thousands who've already automated their workflow. Get instant access to our platform.\n\n#BusinessAutomation #ProductivityHack #AIBusiness #WorkSmarter #TechSolution\n\nCTA: Get Started Now`,
      `ðŸ“ˆ Ready to scale? Our platform helped 500+ businesses grow 3x faster. Discover how AI can transform your operations. Limited spots available this month!\n\n#BusinessScaling #GrowthStrategy #AITransformation #StartupSuccess #Innovation\n\nCTA: Book Your Demo`
    ];
  }

  for (const t of temps) {
    try {
      const model = ai.getGenerativeModel({ model: "gemini-1.5-flash" });
      const result = await model.generateContent({
        contents: [{ role: "user", parts: [{ text: [system, user].join("\n\n") }] }],
        generationConfig: { temperature: t, maxOutputTokens: 250 }
      });
      const response = result.response;
      const text = response.text();
      outs.push(clean(text));
    } catch (error) {
      console.error("Generation error:", error);
      // Add fallback
      outs.push(`Discover how our solution can help your business grow. Contact us today!\n\n#Business #Growth #Solution\n\nCTA: Learn More`);
    }
  }
  return outs;
}

function normalizeCandidate(text: string): Candidate {
  const lines = text.split('\n').filter(Boolean);
  const hashtags = extractHashtags(text);
  const cta = extractCTA(text);
  const caption = lines.find(l => !l.startsWith('#') && !l.startsWith('CTA:')) || text;

  return { caption: cleanCaption(caption), hashtags, cta };
}

function extractHashtags(text: string): string[] {
  const matches = text.match(/#[\w]+/g) || [];
  return matches.slice(0, 5).map(tag => tag.toLowerCase());
}

function extractCTA(text: string): string {
  const ctaMatch = text.match(/CTA:\s*(.+)$/im);
  if (ctaMatch) return ctaMatch[1].trim();

  // Try to infer from common patterns
  const patterns = [
    /(?:call|contact|visit|shop|book|start|get|try|learn|discover|explore)\s+(?:now|today|here)/gi,
    /(?:link in bio|swipe up|tap here|click here)/gi
  ];

  for (const pattern of patterns) {
    const match = text.match(pattern);
    if (match) return match[0];
  }

  return "Learn more";
}

function cleanCaption(text: string): string {
  return text
    .replace(/#[\w]+/g, '')
    .replace(/CTA:.*/i, '')
    .replace(/\n{3,}/g, '\n\n')
    .trim();
}

async function critique(candidate: string, platform: Platform): Promise<Score> {
  // Simple scoring heuristic
  const c = normalizeCandidate(candidate);
  const scores = {
    clarity: scoreClarity(c.caption),
    value: scoreValue(c.caption),
    specificity: scoreSpecificity(c.caption),
    brandVoice: 0.7, // Would need brand analysis
    platformFit: scorePlatformFit(c, platform),
    actionability: c.cta ? 0.8 : 0.3
  };

  const overall = Object.values(scores).reduce((a,b) => a+b, 0) / Object.keys(scores).length;

  const feedback: string[] = [];
  if (scores.clarity < 0.6) feedback.push("Simplify sentence structure");
  if (scores.value < 0.6) feedback.push("Add specific benefit or proof");
  if (scores.actionability < 0.6) feedback.push("Include clear call-to-action");

  return { overall, ...scores, feedback };
}

function scoreClarity(text: string): number {
  const avgWordLength = text.split(/\s+/).reduce((sum, word) => sum + word.length, 0) / text.split(/\s+/).length;
  const sentenceCount = (text.match(/[.!?]/g) || []).length || 1;
  const wordsPerSentence = text.split(/\s+/).length / sentenceCount;

  // Lower is clearer
  const clarityScore = 1 - Math.min(1, (avgWordLength / 10 + wordsPerSentence / 30) / 2);
  return Math.max(0.3, clarityScore);
}

function scoreValue(text: string): number {
  const valueWords = /save|increase|boost|improve|transform|grow|reduce|eliminate|achieve|unlock/gi;
  const matches = (text.match(valueWords) || []).length;
  return Math.min(1, 0.5 + matches * 0.2);
}

function scoreSpecificity(text: string): number {
  const specificWords = /\d+|percent|hours|minutes|days|weeks|months|dollars|customers|users|businesses/gi;
  const matches = (text.match(specificWords) || []).length;
  return Math.min(1, 0.5 + matches * 0.25);
}

function scorePlatformFit(c: Candidate, platform: Platform): number {
  const constraints = PLATFORM_CONSTRAINTS[platform];
  let score = 1.0;

  if (c.caption.length > constraints.maxChars) score -= 0.3;
  if (c.hashtags.length > constraints.maxHashtags) score -= 0.2;
  if (c.hashtags.length < 3) score -= 0.2;

  return Math.max(0.3, score);
}

async function refine(candidate: string, feedback: string[]): Promise<string> {
  if (feedback.length === 0) return candidate;

  const c = normalizeCandidate(candidate);
  let refined = c.caption;

  for (const f of feedback) {
    if (f.includes("Simplify")) {
      refined = simplifyText(refined);
    }
    if (f.includes("specific benefit")) {
      refined = addSpecificity(refined);
    }
    if (f.includes("call-to-action") && !c.cta) {
      refined += "\n\nCTA: Get Started Today";
    }
  }

  return `${refined}\n\n${c.hashtags.join(' ')}\n\n${c.cta ? `CTA: ${c.cta}` : ''}`;
}

function simplifyText(text: string): string {
  return text
    .replace(/utilize/gi, 'use')
    .replace(/implement/gi, 'start')
    .replace(/leverage/gi, 'use')
    .replace(/optimize/gi, 'improve');
}

function addSpecificity(text: string): string {
  if (!text.includes('%') && !text.match(/\d+/)) {
    return text.replace('improve', 'improve by 40%')
      .replace('save time', 'save 5+ hours weekly')
      .replace('grow', 'grow 3x faster');
  }
  return text;
}

async function tighten(c: Candidate, reasons: string[]): Promise<Candidate> {
  let fixed = { ...c };

  if (reasons.includes("caption.too_long")) {
    // Remove filler words and shorten
    fixed.caption = fixed.caption
      .replace(/\b(very|really|just|actually|basically|literally)\b/gi, '')
      .replace(/\s+/g, ' ')
      .trim();

    // If still too long, truncate smartly at sentence boundary
    const constraints = PLATFORM_CONSTRAINTS["x"]; // Use shortest as example
    if (fixed.caption.length > constraints.maxChars) {
      const sentences = fixed.caption.split(/[.!?]/);
      fixed.caption = sentences[0] + '.';
    }
  }

  if (reasons.includes("hashtags.count")) {
    // Ensure 3-5 hashtags
    if (fixed.hashtags.length < 3) {
      fixed.hashtags.push('#innovation', '#businessgrowth', '#efficiency');
    }
    if (fixed.hashtags.length > 5) {
      fixed.hashtags = fixed.hashtags.slice(0, 5);
    }
  }

  if (reasons.includes("cta.missing")) {
    fixed.cta = "Learn More";
  }

  return fixed;
}

function clean(text: string): string {
  return text.trim().replace(/\n{3,}/g, '\n\n');
}

import { PLATFORM_CONSTRAINTS } from "./config";

server/content/templates.ts.content:
import { BrandProfile, Platform } from "./config";

export type PostType = "promo"|"announcement"|"tutorial"|"testimonial"|"faq"|"event"|"seasonal";

export function templateFor(type: PostType, brand: BrandProfile) {
  const name = brand.brandName || "your brand";
  switch (type) {
    case "promo":
      return `Write a concise promotional post that highlights a single clear benefit of ${name}.
Use a punchy first sentence (the hook), one concrete proof (stat, result, mini-example), and a direct CTA.`;
    case "announcement":
      return `Announce a new feature or offer from ${name}. Lead with what's new, why it matters, and how to try it.`;
    case "tutorial":
      return `Explain a 3-step mini-tutorial related to ${name}'s product/service. Keep steps skimmable.`;
    case "testimonial":
      return `Transform this benefit into a short customer quote style (no real names). Emphasize outcome without fluff.`;
    case "faq":
      return `Answer one frequent customer question in 2â€“3 sentences with practical clarity.`;
    case "event":
      return `Promote an upcoming event for ${name}. Include date/time, value, and how to RSVP.`;
    case "seasonal":
      return `Tie a seasonal moment to ${name}'s value in a tasteful, timely way. Avoid clichÃ©s.`;
  }
}

export function artDirectionForImage(brand: BrandProfile, platform: Platform) {
  const palette = (brand.keywords || ["clean","bold","modern"]).join(", ");
  return `High-quality product/social image with brand-aligned palette (${palette}).
Subject centered, soft studio lighting, crisp edges, no watermark, no embedded text.
Composition fits platform ratio, export PNG.`;
}

export function storyboardForVideo(brand: BrandProfile) {
  return `8s storyboard:
0â€“2s HOOK: quick motion + bold overlay (â‰¤5 words).
2â€“6s VALUE: one benefit and a mini-proof.
6â€“8s CTA: brand end card with a single action.
No copyrighted music. Export MP4.`;
}

server/content/validators.ts.content:
import { PlatformConstraints, HASHTAG_BLOCKLIST } from "./config";

export interface ValidationResult {
  ok: boolean;
  reasons: string[];
  coaching: string[];
}

export interface Candidate {
  caption: string;
  hashtags: string[];
  cta?: string;
}

export function validateContent(
  c: Candidate,
  constraints: PlatformConstraints,
  priorCaptions: string[] = [],   // pass last N captions to avoid dupes
): ValidationResult {
  const reasons: string[] = [];
  const coaching: string[] = [];

  // 1) Basic structure
  if (!c.caption || c.caption.trim().length < 10) {
    reasons.push("caption.too_short");
    coaching.push("Add a clear value sentence + benefit.");
  }

  // 2) Length
  if (c.caption.length > constraints.maxChars) {
    reasons.push("caption.too_long");
    coaching.push(`Shorten to â‰¤ ${constraints.maxChars} characters.`);
  }

  // 3) CTA
  if (!c.cta || c.cta.trim().length < 3) {
    reasons.push("cta.missing");
    coaching.push("End with a direct action (e.g., 'Book now' / 'DM us').");
  }

  // 4) Hashtags (3â€“5; niche; no spam)
  const uniqueTags = Array.from(new Set((c.hashtags || []).map(normalizeTag)));
  if (uniqueTags.length < 3 || uniqueTags.length > constraints.maxHashtags) {
    reasons.push("hashtags.count");
    coaching.push(`Use ${Math.min(5, constraints.maxHashtags)} niche hashtags relevant to your audience.`);
  }
  if (uniqueTags.some(tag => HASHTAG_BLOCKLIST.some(rx => rx.test(tag)))) {
    reasons.push("hashtags.blocklisted");
    coaching.push("Replace generic/spammy hashtags with niche tags tied to your topic.");
  }

  // 5) Readability (very rough heuristic)
  const grade = readabilityGrade(c.caption);
  if (grade > constraints.readabilityMaxGrade) {
    reasons.push("readability.hard");
    coaching.push(`Use shorter sentences and concrete words (target grade â‰¤ ${constraints.readabilityMaxGrade}).`);
  }

  // 6) Near-duplicate check
  const isNearDup = priorCaptions.some(p => similarity(p, c.caption) > 0.92);
  if (isNearDup) {
    reasons.push("duplication.too_similar");
    coaching.push("Change the hook and the example to avoid repetition.");
  }

  return { ok: reasons.length === 0, reasons, coaching };
}

function normalizeTag(t: string) {
  const tag = t.trim().replace(/^#/, "");
  return `#${tag.toLowerCase()}`;
}

// Basic, fast similarity (cosine on char n-grams would be better; this is fine for guardrails)
function similarity(a: string, b: string): number {
  const set = (s: string) => new Set(s.toLowerCase().split(/\W+/).filter(Boolean));
  const A = set(a), B = set(b);
  const inter = [...A].filter(x => B.has(x)).length;
  const union = new Set([...A, ...B]).size;
  return union ? inter / union : 0;
}

export function readabilityGrade(text: string): number {
  const sents = Math.max(1, (text.match(/[.!?]/g) || []).length);
  const words = text.trim().split(/\s+/).length;
  const avg = words / sents;
  // cheap heuristic: higher avg words/sentence -> higher grade
  return Math.min(14, Math.max(1, Math.round(avg)));
}

