  test("X promo meets quality thresholds", async ({ request }) => {
    const r = await request.post(`${base}/api/content/generate`, {
      headers: { "x-user-id": "starter-user-1" },
      data: { 
        platform: "x", 
        postType: "promo", 
        tone: "friendly", 
