  test("Instagram tutorial has proper structure", async ({ request }) => {
    const r = await request.post(`${base}/api/content/generate`, {
      headers: { "x-user-id": "starter-user-1" },
      data: { 
        platform: "instagram", 
        postType: "tutorial", 
        tone: "friendly", 
        theme: "how to reheat pastries" 
      }
