import { Router } from "express";
import type { IStorage } from "./storage";
import { InsertBrandProfile } from "@shared/schema";

export function createBrandRoutes(storage: IStorage) {
  const router = Router();

  // Get brand profile for current user
  router.get("/api/brand/profile", async (req: any, res) => {
    try {
      if (!req.user?.id) {
        return res.status(401).json({ error: "Unauthorized" });
      }

      const profile = await storage.getBrandProfile(req.user.id);
      if (!profile) {
        // Return default profile structure if none exists
        return res.json({
          brandName: req.user.businessName || "",
          voice: "friendly",
          targetAudience: "",
          products: [],
          valueProps: [],
          bannedPhrases: [],
          requiredDisclaimers: [],
          preferredCTAs: ["Learn More", "Get Started", "Contact Us"],
          keywords: []
        });
      }

      res.json(profile);
    } catch (error) {
      console.error("Error fetching brand profile:", error);
      res.status(500).json({ error: "Failed to fetch brand profile" });
    }
  });

  // Create or update brand profile
  router.put("/api/brand/profile", async (req: any, res) => {
    try {
      if (!req.user?.id) {
        return res.status(401).json({ error: "Unauthorized" });
      }

      const {
        brandName,
        voice,
        targetAudience,
        products,
        valueProps,
        bannedPhrases,
        requiredDisclaimers,
        preferredCTAs,
        keywords
      } = req.body;

      // Check if profile exists
      const existing = await storage.getBrandProfile(req.user.id);

      if (existing) {
        // Update existing profile
        const updated = await storage.updateBrandProfile(req.user.id, {
          brandName,
          voice,
          targetAudience,
          products: products || [],
          valueProps: valueProps || [],
          bannedPhrases: bannedPhrases || [],
          requiredDisclaimers: requiredDisclaimers || [],
          preferredCTAs: preferredCTAs || ["Learn More"],
          keywords: keywords || []
        });
        return res.json(updated);
      } else {
        // Create new profile
        const newProfile: InsertBrandProfile = {
          userId: req.user.id,
          brandName: brandName || req.user.businessName || "My Brand",
          voice: voice || "friendly",
          targetAudience: targetAudience || "",
          products: products || [],
          valueProps: valueProps || [],
          bannedPhrases: bannedPhrases || [],
          requiredDisclaimers: requiredDisclaimers || [],
          preferredCTAs: preferredCTAs || ["Learn More"],
          keywords: keywords || []
        };

        const created = await storage.createBrandProfile(newProfile);
        res.json(created);
      }
    } catch (error) {
      console.error("Error updating brand profile:", error);
      res.status(500).json({ error: "Failed to update brand profile" });
    }
  });

  return router;
}

server/content/imageHeuristics.ts.content:
import sharp from "sharp";

export interface ImageQualityMetrics {
  sharpness: number;
  isBlankish: boolean;
  brightness: number;
  contrast: number;
  saturation: number;
}

export async function basicImageQualityScores(pngBytes: Buffer): Promise<ImageQualityMetrics> {
  try {
    // Heuristic: variance of Laplacian proxy via sharp (approx with edge magnitude)
    const img = sharp(pngBytes);
    const stats = await img.stats();

    // Use stddev as a cheap sharpness correlate (not perfect, but catches blanks/very blurry)
    const sharpness = stats.channels?.[0]?.stdev ?? 0;

    // Check if image is essentially blank
    const avgBrightness = stats.channels?.reduce((sum, ch) => sum + (ch.mean || 0), 0) / (stats.channels?.length || 1);
    const isBlankish = avgBrightness < 1 && sharpness < 1;

    // Additional metrics
    const brightness = avgBrightness;
    const contrast = Math.max(...(stats.channels?.map(ch => ch.stdev) || [0]));

    // Rough saturation estimate (difference between channels)
    let saturation = 0;
    if (stats.channels && stats.channels.length >= 3) {
      const channelMeans = stats.channels.slice(0, 3).map(ch => ch.mean);
      const maxMean = Math.max(...channelMeans);
      const minMean = Math.min(...channelMeans);
      saturation = maxMean - minMean;
    }

    return {
      sharpness,
      isBlankish,
      brightness,
      contrast,
      saturation
    };
  } catch (error) {
    console.error("Error analyzing image quality:", error);
    return {
      sharpness: 0,
      isBlankish: true,
      brightness: 0,
      contrast: 0,
      saturation: 0
    };
  }
}

export function isAcceptableImageQuality(metrics: ImageQualityMetrics): boolean {
  // Basic thresholds for acceptable image quality
  return (
    !metrics.isBlankish &&
    metrics.sharpness > 5 && // Not too blurry
    metrics.brightness > 10 && // Not too dark
    metrics.brightness < 245 && // Not overexposed
    metrics.contrast > 10 // Has some contrast
  );
}

server/content/moderation.ts.content:
import { GoogleGenAI } from "@google/genai";

export type ModerationDecision = "allow" | "review" | "block";

export interface ModerationResult {
  decision: ModerationDecision;
  reasons: string[];
  coaching?: string[];
  safeRewrite?: string;
  confidence: number;
}

// Platform-specific policy restrictions
const PLATFORM_POLICIES = {
  instagram: {
    prohibited: ["violence", "hate_speech", "adult_content", "misleading_claims", "regulated_goods"],
    sensitive: ["political", "medical_claims", "financial_advice"],
    maxHashtags: 30,
  },
  facebook: {
    prohibited: ["violence", "hate_speech", "adult_content", "misinformation", "spam"],
    sensitive: ["political", "medical_claims", "financial_products"],
    maxHashtags: 30,
  },
  x: {
    prohibited: ["violence", "hate_speech", "adult_content", "private_info"],
    sensitive: ["political", "misleading"],
    maxHashtags: 10,
  },
  linkedin: {
    prohibited: ["inappropriate_content", "harassment", "spam", "fake_profiles"],
    sensitive: ["controversial_topics", "unverified_claims"],
    maxHashtags: 5,
  },
  tiktok: {
    prohibited: ["dangerous_acts", "hate_speech", "adult_content", "harmful_misinformation"],
    sensitive: ["political", "medical_advice", "financial_advice"],
    maxHashtags: 100,
  }
};

// Known problematic terms that should trigger review
const REVIEW_TRIGGERS = [
  /\b(cure|treatment|heal|medical|doctor|prescription|fda|approved)\b/gi,
  /\b(guarantee|promise|assured|definitely|100%|risk-free)\b/gi,
  /\b(limited time|act now|don't wait|last chance|expires)\b/gi,
  /\b(make money|get rich|passive income|financial freedom)\b/gi,
  /\b(weight loss|diet|supplement|miracle|breakthrough)\b/gi,
];

// Terms that should be immediately blocked
const BLOCK_TRIGGERS = [
  /\b(kill|suicide|self-harm|violence|weapon)\b/gi,
  /\b(hate|racist|sexist|discriminate)\b/gi,
  /\b(nude|porn|sex|explicit)\b/gi,
  /\b(scam|fraud|illegal|stolen)\b/gi,
  /\b(drug|cocaine|marijuana|pills)\b/gi,
];

/**
 * Pre-generation safety check for prompts
 */
export async function checkPromptSafety(
  prompt: string,
  kind: "text" | "image" | "video"
): Promise<ModerationResult> {
  // Quick regex checks first
  for (const pattern of BLOCK_TRIGGERS) {
    if (pattern.test(prompt)) {
      return {
        decision: "block",
        reasons: ["prohibited_content"],
        coaching: ["This prompt contains prohibited terms. Please rephrase without harmful content."],
        confidence: 1.0
      };
    }
  }

  for (const pattern of REVIEW_TRIGGERS) {
    if (pattern.test(prompt)) {
      return {
        decision: "review",
        reasons: ["sensitive_content"],
        coaching: ["This content may require review. Consider rephrasing claims to be more factual."],
        confidence: 0.7
      };
    }
  }

  // For images/video, check for inappropriate visual requests
  if (kind !== "text") {
    const visualProblems = checkVisualPrompt(prompt);
    if (visualProblems.length > 0) {
      return {
        decision: "block",
        reasons: visualProblems,
        coaching: ["Visual content must be appropriate for all audiences."],
        confidence: 0.9
      };
    }
  }

  return {
    decision: "allow",
    reasons: [],
    confidence: 1.0
  };
}

/**
 * Post-generation content moderation
 */
export async function moderateContent(
  content: string,
  platform: keyof typeof PLATFORM_POLICIES,
  isAd: boolean = false
): Promise<ModerationResult> {
  const policy = PLATFORM_POLICIES[platform];

  // Check for prohibited content
  for (const pattern of BLOCK_TRIGGERS) {
    if (pattern.test(content)) {
      return {
        decision: "block",
        reasons: ["prohibited_content", "policy_violation"],
        coaching: [
          "Content contains prohibited terms.",
          "Please create content that's safe for all audiences."
        ],
        safeRewrite: generateSafeAlternative(content),
        confidence: 1.0
      };
    }
  }

  // Check for sensitive content that needs review
  let reviewReasons: string[] = [];
  for (const pattern of REVIEW_TRIGGERS) {
    if (pattern.test(content)) {
      reviewReasons.push("sensitive_content");
      break;
    }
  }

  // Ad-specific checks
  if (isAd) {
    if (/\b(guarantee|promise|100%|no risk)\b/gi.test(content)) {
      reviewReasons.push("unsubstantiated_claims");
    }
    if (!/\#ad|\#sponsored|\#partner/gi.test(content)) {
      reviewReasons.push("missing_disclosure");
    }
  }

  // Platform-specific hashtag limits
  const hashtags = (content.match(/#\w+/g) || []).length;
  if (hashtags > policy.maxHashtags) {
    reviewReasons.push("excessive_hashtags");
  }

  if (reviewReasons.length > 0) {
    return {
      decision: "review",
      reasons: reviewReasons,
      coaching: generateCoaching(reviewReasons),
      safeRewrite: isAd ? addRequiredDisclosures(content) : undefined,
      confidence: 0.6
    };
  }

  return {
    decision: "allow",
    reasons: [],
    confidence: 0.95
  };
}

/**
 * Pre-publish final safety gate
 */
export async function prePublishCheck(
  content: string,
  mediaUrls: string[],
  platform: keyof typeof PLATFORM_POLICIES
): Promise<ModerationResult> {
  // Run content moderation
  const contentCheck = await moderateContent(content, platform);
  if (contentCheck.decision === "block") {
    return contentCheck;
  }

  // Check if media needs review (would need actual image analysis in production)
  // For now, just flag if there are media items and content has sensitive topics
  if (mediaUrls.length > 0 && contentCheck.decision === "review") {
    return {
      ...contentCheck,
      coaching: [
        ...contentCheck.coaching || [],
        "Media content should be reviewed before publishing."
      ]
    };
  }

  return contentCheck;
}

// Helper functions

function checkVisualPrompt(prompt: string): string[] {
  const problems: string[] = [];

  const inappropriate = [
    /\b(nude|naked|explicit|sexual)\b/gi,
    /\b(violence|blood|gore|death)\b/gi,
    /\b(child|minor|kid).*\b(inappropriate|dangerous)\b/gi,
  ];

  for (const pattern of inappropriate) {
    if (pattern.test(prompt)) {
      problems.push("inappropriate_visual_request");
    }
  }

  return problems;
}

function generateSafeAlternative(content: string): string {
  // Simple safe rewrite - in production, use AI for better rewrites
  let safe = content;

  // Remove problematic words
  BLOCK_TRIGGERS.forEach(pattern => {
    safe = safe.replace(pattern, "[removed]");
  });

  // Remove excessive claims
  safe = safe.replace(/\b(guarantee|promise|100%|assured)\b/gi, "aim to");
  safe = safe.replace(/\b(cure|heal)\b/gi, "support");
  safe = safe.replace(/\b(proven|scientific fact)\b/gi, "researched");

  return safe;
}

function generateCoaching(reasons: string[]): string[] {
  const coaching: string[] = [];

  if (reasons.includes("sensitive_content")) {
    coaching.push("Content touches on sensitive topics. Ensure claims are factual and substantiated.");
  }
  if (reasons.includes("unsubstantiated_claims")) {
    coaching.push("Avoid absolute guarantees. Use 'may help' or 'designed to' instead.");
  }
  if (reasons.includes("missing_disclosure")) {
    coaching.push("Add #ad or #sponsored to comply with advertising guidelines.");
  }
  if (reasons.includes("excessive_hashtags")) {
    coaching.push("Reduce hashtag count to platform limits for better reach.");
  }

  return coaching;
}

function addRequiredDisclosures(content: string): string {
  if (!/#ad|#sponsored|#partner/gi.test(content)) {
    return content + "\n\n#ad #sponsored";
  }
  return content;
}

/**
 * Middleware for Express routes
 */
export function requireSafePrompt(kind: "text" | "image" | "video") {
  return async (req: any, res: any, next: any) => {
    const prompt = req.body.prompt || req.body.content || "";

    if (!prompt) {
      return next();
    }

    const safety = await checkPromptSafety(prompt, kind);

    if (safety.decision === "block") {
      return res.status(422).json({
        error: "Content policy violation",
        reasons: safety.reasons,
        coaching: safety.coaching
      });
    }

    if (safety.decision === "review") {
      // Add flag for review but allow generation
      req.body.requiresReview = true;
      req.body.reviewReasons = safety.reasons;
    }

    next();
  };
}

server/content/qualityEvaluator.ts.content:
import { validateContent } from "./validators";
import { PLATFORM_CONSTRAINTS } from "./config";
import { GoogleGenAI } from "@google/genai";

export type EvalScore = { 
  overall: number; 
  subs: {
    hook: number;
    value: number;
    fit: number;
    voice: number;
    action: number;
  } 
};

const ai = process.env.GEMINI_API_KEY 
  ? new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY })
  : null;

export async function scoreCaption(
  platform: keyof typeof PLATFORM_CONSTRAINTS, 
  caption: string
): Promise<EvalScore> {
  // Deterministic-ish critique at low temperature; fall back to neutral 7s if model unavailable.
  try {
    if (!ai) {
      throw new Error("AI not configured");
    }

    const prompt = `Score this social media caption for ${platform} on these criteria (1-10 scale):
- Hook: How engaging is the opening?
- Value: Does it provide clear value to the reader?
- Fit: How well does it fit ${platform} platform conventions?
- Voice: How consistent and authentic is the brand voice?
- Action: How clear and compelling is the call-to-action?

Return ONLY valid JSON in this exact format: {"hook":n,"value":n,"fit":n,"voice":n,"action":n}

Caption:
${caption}`;

    const model = ai.generativeModel({ model: "gemini-2.0-flash-exp" });
    const result = await model.generateContent({
      contents: [{ role: "user", parts: [{ text: prompt }] }],
      generationConfig: {
        temperature: 0.0,
        maxOutputTokens: 100,
        topP: 0.1,
      }
    });

    const text = result.response.text();
    // Extract JSON from response
    const jsonMatch = text.match(/\{[\s\S]*?\}/);
    if (!jsonMatch) throw new Error("No JSON found in response");

    const j = JSON.parse(jsonMatch[0]);
    const subs = { 
      hook: Math.min(10, Math.max(1, +j.hook || 7)), 
      value: Math.min(10, Math.max(1, +j.value || 7)), 
      fit: Math.min(10, Math.max(1, +j.fit || 7)), 
      voice: Math.min(10, Math.max(1, +j.voice || 7)), 
      action: Math.min(10, Math.max(1, +j.action || 7))
    };
    const overall = Math.round((subs.hook + subs.value + subs.fit + subs.voice + subs.action) / 5);
    return { overall, subs };
  } catch (e) {
    // Fallback to neutral scores if AI unavailable
    const subs = { hook: 7, value: 7, fit: 7, voice: 7, action: 7 };
    return { overall: 7, subs };
  }
}

export async function evaluateContentQuality(
  platform: keyof typeof PLATFORM_CONSTRAINTS,
  content: {
    caption: string;
    hashtags: string[];
    cta?: string;
  }
): Promise<{
  score: EvalScore;
  validation: ReturnType<typeof validateContent>;
  passes: boolean;
}> {
  const constraints = PLATFORM_CONSTRAINTS[platform];

  // Validate structure
  const validation = validateContent(
    content,
    {
      maxChars: constraints.maxChars,
      maxHashtags: constraints.maxHashtags,
      allowedRatios: ["1:1"],
      readabilityMaxGrade: constraints.readabilityMaxGrade
    },
    []
  );

  // Score quality
  const fullCaption = content.cta 
    ? `${content.caption}\n\n${content.cta}` 
    : content.caption;
  const score = await scoreCaption(platform, fullCaption);

  // Check if passes thresholds
  const passes = validation.ok && score.overall >= 7;

  return { score, validation, passes };
}

server/feedbackRoutes.ts.content:
import { Router } from "express";
import type { IStorage } from "./storage";
import { InsertContentFeedback } from "@shared/schema";

export function createFeedbackRoutes(storage: IStorage) {
  const router = Router();

  // Submit feedback for content
  router.post("/api/feedback", async (req: any, res) => {
    try {
      if (!req.user?.id) {
        return res.status(401).json({ error: "Unauthorized" });
      }

      const {
        contentId,
        contentType, // post, ai_suggestion, campaign_post
        feedback, // thumbs_up, thumbs_down
        reasons, // Array of reason codes
        qualityScore,
        platform,
        postType,
        metadata
      } = req.body;

      if (!contentId || !contentType || !feedback) {
        return res.status(400).json({ error: "Missing required fields" });
      }

      if (!['thumbs_up', 'thumbs_down'].includes(feedback)) {
        return res.status(400).json({ error: "Invalid feedback type" });
      }

      const feedbackRecord: InsertContentFeedback = {
        userId: req.user.id,
        contentId,
        contentType,
        feedback,
        reasons: reasons || [],
        qualityScore,
        platform,
        postType,
        metadata
      };

      await storage.createContentFeedback(feedbackRecord);

      // Update brand profile preferences based on feedback
      if (feedback === 'thumbs_up' && metadata?.cta) {
        // User liked this CTA, bump its priority
        const profile = await storage.getBrandProfile(req.user.id);
        if (profile) {
          const ctas = profile.preferredCTAs || [];
          if (!ctas.includes(metadata.cta)) {
            ctas.unshift(metadata.cta); // Add to front
            await storage.updateBrandProfile(req.user.id, {
              preferredCTAs: ctas.slice(0, 10) // Keep top 10
            });
          }
        }
      }

      res.json({ success: true, message: "Feedback recorded" });
    } catch (error) {
      console.error("Error recording feedback:", error);
      res.status(500).json({ error: "Failed to record feedback" });
    }
  });

  // Get feedback statistics for user
  router.get("/api/feedback/stats", async (req: any, res) => {
    try {
      if (!req.user?.id) {
        return res.status(401).json({ error: "Unauthorized" });
      }

      const feedback = await storage.getContentFeedbackByUserId(req.user.id);

      const stats = {
        total: feedback.length,
        thumbsUp: feedback.filter(f => f.feedback === 'thumbs_up').length,
        thumbsDown: feedback.filter(f => f.feedback === 'thumbs_down').length,
        avgQualityScore: feedback.reduce((sum, f) => sum + (f.qualityScore || 0), 0) / (feedback.length || 1),
        topReasons: getTopReasons(feedback),
        platformBreakdown: getPlatformBreakdown(feedback)
      };

      res.json(stats);
    } catch (error) {
      console.error("Error fetching feedback stats:", error);
      res.status(500).json({ error: "Failed to fetch feedback statistics" });
    }
  });

  return router;
}

function getTopReasons(feedback: any[]): Record<string, number> {
  const reasons: Record<string, number> = {};

  feedback.forEach(f => {
    if (f.reasons && Array.isArray(f.reasons)) {
      f.reasons.forEach((reason: string) => {
        reasons[reason] = (reasons[reason] || 0) + 1;
      });
    }
  });

  // Sort and return top 5
  return Object.fromEntries(
    Object.entries(reasons)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 5)
  );
}

function getPlatformBreakdown(feedback: any[]): Record<string, { up: number; down: number }> {
  const platforms: Record<string, { up: number; down: number }> = {};

  feedback.forEach(f => {
    const platform = f.platform || 'unknown';
    if (!platforms[platform]) {
      platforms[platform] = { up: 0, down: 0 };
    }

    if (f.feedback === 'thumbs_up') {
      platforms[platform].up++;
    } else {
      platforms[platform].down++;
    }
  });

  return platforms;
}

