import { Router } from "express";
import { TRIAL, TrialVariant } from "../config/trial";
import { storage } from "./storage";

export const trialRouter = Router();

function requireAuth(req:any,res:any,next:any){
  // Check for session-based auth (app auth)
  if (req.session?.userId) {
    req.user = { id: req.session.userId };
    return next();
  }
  // Check for Replit auth
  if (req.user?.claims?.sub) {
    req.user = { id: req.user.claims.sub };
    return next();
  }
  // Check if user object has id directly (from session auth middleware)
  if (req.user?.id) {
    return next();
  }
  return res.status(401).json({ error:"AUTH_REQUIRED" });
}

trialRouter.get("/status", requireAuth, async (req:any,res:any)=>{
  const u = await storage.getUser(req.user.id);
  res.json({
    variant: u?.trialVariant,
    startedAt: u?.trialStartedAt,
    endsAt: u?.trialEndsAt,
    imagesRemaining: u?.trialImagesRemaining ?? 0,
    videosRemaining: u?.trialVideosRemaining ?? 0,
    emailVerified: !!u?.emailVerified,
    cardOnFile: !!u?.cardOnFile
  });
});

trialRouter.post("/select", requireAuth, async (req:any,res:any)=>{
  const variant = String(req.body.variant || TRIAL.variant) as TrialVariant;
  if(!TRIAL.variants[variant]) return res.status(400).json({ error:"BAD_VARIANT" });
  const v = TRIAL.variants[variant];
  const now = new Date();
  const end = new Date(now.getTime() + v.days*24*3600*1000);

  // Update user with trial details and clear needsTrialSelection flag
  await storage.updateUser(req.user.id, {
    trialVariant: variant,
    trialStartedAt: now,
    trialEndsAt: end,
    trialImagesRemaining: v.images,
    trialVideosRemaining: v.videos,
    needsTrialSelection: false, // Clear the flag
    tier: "free_trial", // Set proper tier
    credits: v.credits || 50, // Set initial credits
  });

  res.json({ ok:true, variant, endsAt: end.toISOString() });
});

server/scheduleRoutes.ts.content:
import { Router } from "express";
import { z } from "zod";
import { storage } from "./storage";
import dayjs from "dayjs";
import utc from "dayjs/plugin/utc.js";
import timezone from "dayjs/plugin/timezone.js";

dayjs.extend(utc);
dayjs.extend(timezone);

export function createScheduleRoutes(storage: any): Router {
  const router = Router();

  // Get scheduled posts within date range
  router.get("/api/schedule", async (req, res) => {
    try {
      const { from, to, tz = "UTC" } = req.query;

      if (!from || !to) {
        return res.status(400).json({ error: "from and to dates are required" });
      }

      // Convert dates to UTC for database query
      const fromDate = dayjs.tz(from as string, tz as string).utc().toDate();
      const toDate = dayjs.tz(to as string, tz as string).utc().toDate();

      // Get all posts scheduled in this range
      const posts = await storage.getScheduledPosts({
        from: fromDate,
        to: toDate,
        userId: req.user?.claims?.sub || req.user?.id || "demo-user"
      });

      // Transform posts to calendar events
      const events = posts.map((post: any) => ({
        id: post.id,
        postId: post.id,
        title: post.title || post.content?.substring(0, 50),
        caption: post.content,
        scheduledAt: post.scheduledFor,
        endsAt: dayjs(post.scheduledFor).add(30, "minutes").toISOString(),
        platform: post.platforms?.[0] || post.platform || "instagram",
        status: post.status,
        mediaUrls: [post.imageUrl, post.videoUrl].filter(Boolean),
        tags: post.tags || [],
        needsApproval: post.status === "pending_approval" || post.requiresApproval
      }));

      res.json({ events });
    } catch (error) {
      console.error("Error fetching schedule:", error);
      res.status(500).json({ error: "Failed to fetch schedule" });
    }
  });

  // Create scheduled post from draft
  router.post("/api/schedule", async (req, res) => {
    try {
      const schema = z.object({
        draftId: z.string().optional(),
        platform: z.string(),
        scheduledAt: z.string(),
        caption: z.string(),
        mediaUrls: z.array(z.string()).optional(),
        tags: z.array(z.string()).optional()
      });

      const data = schema.parse(req.body);
      const userId = req.user?.claims?.sub || req.user?.id || "demo-user";

      // Check for conflicts
      const conflicts = await storage.checkScheduleConflicts({
        userId,
        platform: data.platform,
        scheduledAt: new Date(data.scheduledAt),
        duration: 30 // minutes
      });

      if (conflicts.length > 0) {
        // Suggest alternative time (15 minutes later)
        const suggestedTime = dayjs(data.scheduledAt).add(15, "minutes").toISOString();
        return res.status(409).json({
          error: "Time conflict detected",
          conflicts,
          suggestion: `Try scheduling at ${dayjs(suggestedTime).format("h:mm A")}`
        });
      }

      // Create or update the post
      let post;
      if (data.draftId) {
        // Update existing draft to scheduled
        post = await storage.updatePost(data.draftId, {
          status: "scheduled",
          scheduledFor: data.scheduledAt,
          platforms: [data.platform],
          content: data.caption,
          tags: data.tags
        });
      } else {
        // Create new scheduled post
        post = await storage.createPost({
          userId,
          content: data.caption,
          platforms: [data.platform],
          status: "scheduled",
          scheduledFor: data.scheduledAt,
          imageUrl: data.mediaUrls?.[0],
          videoUrl: data.mediaUrls?.find(url => url.match(/\.(mp4|mov|avi|webm)/i)),
          tags: data.tags,
          createdAt: new Date(),
          updatedAt: new Date()
        });
      }

      res.json({ 
        success: true, 
        postId: post.id,
        scheduledAt: data.scheduledAt 
      });
    } catch (error) {
      console.error("Error creating scheduled post:", error);
      res.status(500).json({ error: "Failed to schedule post" });
    }
  });

  // Update scheduled post
  router.put("/api/schedule/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const updates = req.body;
      const userId = req.user?.claims?.sub || req.user?.id || "demo-user";

      // If rescheduling, check for conflicts
      if (updates.scheduledAt) {
        const post = await storage.getPost(id);
        if (!post) {
          return res.status(404).json({ error: "Post not found" });
        }

        const conflicts = await storage.checkScheduleConflicts({
          userId,
          platform: post.platforms?.[0] || "instagram",
          scheduledAt: new Date(updates.scheduledAt),
          duration: 30,
          excludeId: id
        });

        if (conflicts.length > 0) {
          const suggestedTime = dayjs(updates.scheduledAt).add(15, "minutes").toISOString();
          return res.status(409).json({
            error: "Time conflict detected",
            conflicts,
            suggestion: `Try ${dayjs(suggestedTime).format("h:mm A")}`
          });
        }

        updates.scheduledFor = updates.scheduledAt;
        delete updates.scheduledAt;
      }

      // Update post
      const post = await storage.updatePost(id, {
        ...updates,
        updatedAt: new Date()
      });

      res.json({ success: true, post });
    } catch (error) {
      console.error("Error updating scheduled post:", error);
      res.status(500).json({ error: "Failed to update scheduled post" });
    }
  });

  // Delete/unschedule post
  router.delete("/api/schedule/:id", async (req, res) => {
    try {
      const { id } = req.params;

      // Update to draft status instead of deleting
      await storage.updatePost(id, {
        status: "draft",
        scheduledFor: null,
        updatedAt: new Date()
      });

      res.json({ success: true });
    } catch (error) {
      console.error("Error unscheduling post:", error);
      res.status(500).json({ error: "Failed to unschedule post" });
    }
  });

  // Publish post immediately
  router.post("/api/schedule/:id/publish", async (req, res) => {
    try {
      const { id } = req.params;

      const post = await storage.getPost(id);
      if (!post) {
        return res.status(404).json({ error: "Post not found" });
      }

      // TODO: Actually publish to platform APIs
      // For now, just update status
      await storage.updatePost(id, {
        status: "published",
        publishedAt: new Date(),
        updatedAt: new Date()
      });

      res.json({ success: true, publishedAt: new Date() });
    } catch (error) {
      console.error("Error publishing post:", error);
      res.status(500).json({ error: "Failed to publish post" });
    }
  });

  // Get draft posts for dragging
  router.get("/api/posts/draft", async (req, res) => {
    try {
      const userId = req.user?.claims?.sub || req.user?.id || "demo-user";

      const drafts = await storage.getPosts({
        userId,
        status: "draft"
      });

      const formattedDrafts = drafts.map((post: any) => ({
        id: post.id,
        title: post.title,
        content: post.content,
        caption: post.content,
        platform: post.platforms?.[0] || "instagram",
        mediaUrls: [post.imageUrl, post.videoUrl].filter(Boolean),
        hasImage: !!post.imageUrl,
        hasVideo: !!post.videoUrl,
        tags: post.tags || [],
        createdAt: post.createdAt
      }));

      res.json({ items: formattedDrafts });
    } catch (error) {
      console.error("Error fetching drafts:", error);
      res.status(500).json({ error: "Failed to fetch drafts" });
    }
  });

  return router;
}

scripts/check-health.js.content:
// scripts/check-health.js
import fs from "node:fs";
import path from "node:path";

const ART_DIR = process.argv[2];
if (!ART_DIR) {
  console.error("Usage: node scripts/check-health.js <artifact-dir>");
  process.exit(1);
}

const base = process.env.E2E_BASE_URL || "http://localhost:5000";
const out = { base, ts: new Date().toISOString(), health: null, ready: null };

console.log(`Checking health at ${base}...`);

try {
  // Check /health endpoint
  const healthResponse = await fetch(`${base}/health`);
  if (healthResponse.status !== 200) {
    throw new Error(`Health check failed with status ${healthResponse.status}`);
  }
  const healthData = await healthResponse.json();
  out.health = { status: 200, data: healthData };
  console.log("âœ“ Health check passed");

  // Check /ready endpoint
  const readyResponse = await fetch(`${base}/ready`);
  const readyData = await readyResponse.json();
  out.ready = { status: readyResponse.status, data: readyData };

  if (readyResponse.status !== 200) {
    console.error("âœ— Ready check failed");
    fs.mkdirSync(path.join(ART_DIR, "data"), { recursive: true });
    fs.writeFileSync(path.join(ART_DIR, "data", "health.json"), JSON.stringify(out, null, 2));
    throw new Error(`Ready check failed with status ${readyResponse.status}`);
  }
  console.log("âœ“ Ready check passed");

  // Save successful health check data
  fs.mkdirSync(path.join(ART_DIR, "data"), { recursive: true });
  fs.writeFileSync(path.join(ART_DIR, "data", "health.json"), JSON.stringify(out, null, 2));

  console.log("âœ“ All health checks passed");
  process.exit(0);
} catch (error) {
  console.error("Health check error:", error.message);
  // Save error data
  fs.mkdirSync(path.join(ART_DIR, "data"), { recursive: true });
  fs.writeFileSync(path.join(ART_DIR, "data", "health-error.json"), JSON.stringify({
    ...out,
    error: error.message
  }, null, 2));
  process.exit(1);
}

