scripts/run-int-tests.js.content:
// scripts/run-int-tests.js
import fs from "node:fs";
import path from "node:path";
import prettyBytes from "pretty-bytes";

const ART_DIR = process.argv[2];
if (!ART_DIR) {
  console.error("Usage: node scripts/run-int-tests.js <artifact-dir>");
  process.exit(1);
}

const base = process.env.E2E_BASE_URL || "http://localhost:5000";
const headers = { "Content-Type": "application/json" };

async function login(email, password) {
  console.log(`Logging in as ${email}...`);
  const r = await fetch(`${base}/api/auth/login`, { 
    method: "POST", 
    headers, 
    body: JSON.stringify({ email, password })
  });

  if (!r.ok) {
    const error = await r.text();
    throw new Error(`Login failed for ${email}: ${error}`);
  }

  const cookie = r.headers.get("set-cookie");
  const user = await r.json();
  console.log(`âœ“ Logged in as ${user.email} (${user.subscriptionTier || 'trial'})`);
  return { cookie, user };
}

function hWithCookie(cookie) { 
  return { ...headers, cookie }; 
}

async function getUser(cookie) {
  const r = await fetch(`${base}/api/user`, { headers: hWithCookie(cookie) });
  if (!r.ok) throw new Error("Failed to get user");
  return await r.json();
}

async function generateContent(cookie, prompt, type = "text") {
  console.log(`Generating ${type} content...`);
  const endpoint = type === "image" ? "/api/ai/generate-content" : "/api/ai/generate";
  const body = type === "image" 
    ? { prompt, platform: "instagram", generateImage: true, temperature: 0 }
    : { prompt, platform: "instagram", temperature: 0 };

  const r = await fetch(`${base}${endpoint}`, {
    method: "POST",
    headers: hWithCookie(cookie),
    body: JSON.stringify(body)
  });

  if (!r.ok) {
    const error = await r.text();
    throw new Error(`${type} generation failed: ${error}`);
  }

  return await r.json();
}

async function getContentLibrary(cookie, type = null) {
  const url = type ? `${base}/api/library?type=${type}` : `${base}/api/library`;
  const r = await fetch(url, { headers: hWithCookie(cookie) });
  if (!r.ok) throw new Error("Failed to get content library");
  return await r.json();
}

async function createCampaign(cookie, data) {
  console.log("Creating campaign...");
  const campaignData = {
    ...data,
    platform: data.platform || "multi",
    businessName: data.businessName || "Test Business",
    targetAudience: data.targetAudience || "Tech professionals aged 25-45",
    campaignGoals: data.campaignGoals || "Increase brand awareness and engagement",
    brandTone: data.brandTone || "professional",
    visualStyle: data.visualStyle || "modern",
    callToAction: data.callToAction || "Learn more"
  };

  const r = await fetch(`${base}/api/campaigns`, {
    method: "POST",
    headers: hWithCookie(cookie),
    body: JSON.stringify(campaignData)
  });

  if (!r.ok) {
    const error = await r.text();
    throw new Error(`Campaign creation failed: ${error}`);
  }

  return await r.json();
}

(async () => {
  const results = {
    timestamp: new Date().toISOString(),
    tests: []
  };

  try {
    // Test 1: Login with test users (create if needed)
    const testEmail = process.env.TEST_ENTERPRISE_EMAIL || "test-enterprise@myaimediamgr.com";
    const testPassword = process.env.TEST_ENTERPRISE_PASSWORD || "Test123!@#";

    let auth;
    try {
      auth = await login(testEmail, testPassword);
    } catch (loginError) {
      console.log("Test user not found, creating...");
      // Try to create the user first
      const signupResponse = await fetch(`${base}/api/auth/signup`, {
        method: "POST",
        headers,
        body: JSON.stringify({
          email: testEmail,
          password: testPassword,
          username: "test-enterprise",
          businessName: "Test Enterprise Co"
        })
      });

      if (!signupResponse.ok) {
        throw new Error(`Failed to create test user: ${await signupResponse.text()}`);
      }

      // Now login
      auth = await login(testEmail, testPassword);
    }

    results.tests.push({ name: "auth", status: "PASS", user: auth.user.email });

    // Test 2: Get initial user state
    const userBefore = await getUser(auth.cookie);
    const creditsBefore = userBefore.credits || 0;
    const trialImagesBefore = userBefore.trialImagesRemaining || 0;
    console.log(`Credits: ${creditsBefore}, Trial Images: ${trialImagesBefore}`);
    results.tests.push({ 
      name: "user-state-before", 
      status: "PASS", 
      credits: creditsBefore,
      trialImages: trialImagesBefore
    });

    // Test 3: Generate text content
    const textContent = await generateContent(auth.cookie, "Test post for a bakery", "text");
    if (!textContent.content || textContent.content.length < 10) {
      throw new Error("Text generation returned empty or too short content");
    }
    console.log(`âœ“ Generated text: ${textContent.content.substring(0, 50)}...`);
    results.tests.push({ 
      name: "text-generation", 
      status: "PASS", 
      length: textContent.content.length 
    });

    // Test 4: Generate image content (if API key available)
    let imageResult = null;
    if (process.env.GEMINI_API_KEY || process.env.VERTEX_AI_PROJECT) {
      try {
        const imageContent = await generateContent(auth.cookie, "Neon gradient abstract background", "image");
        if (imageContent.imageUrl) {
          imageResult = { url: imageContent.imageUrl, id: imageContent.id };
          console.log(`âœ“ Generated image: ${imageContent.imageUrl}`);
          results.tests.push({ 
            name: "image-generation", 
            status: "PASS", 
            imageId: imageContent.id 
          });
        }
      } catch (imgError) {
        console.warn("Image generation skipped:", imgError.message);
        results.tests.push({ 
          name: "image-generation", 
          status: "SKIP", 
          reason: "No AI service configured" 
        });
      }
    } else {
      results.tests.push({ 
        name: "image-generation", 
        status: "SKIP", 
        reason: "No API keys" 
      });
    }

    // Test 5: Check content library
    await new Promise(resolve => setTimeout(resolve, 1000)); // Wait for async save
    const library = await getContentLibrary(auth.cookie);
    const hasItems = library.items && library.items.length > 0;
    console.log(`âœ“ Content library has ${library.items?.length || 0} items`);
    results.tests.push({ 
      name: "content-library", 
      status: hasItems ? "PASS" : "WARN",
      itemCount: library.items?.length || 0
    });

    // Test 6: Create a campaign
    try {
      const campaign = await createCampaign(auth.cookie, {
        name: "Test Campaign " + Date.now(),
        description: "Integration test campaign",
        platforms: ["instagram"],
        startDate: new Date().toISOString(),
        endDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString(),
        postsPerDay: 2,
        tone: "professional",
        includeHashtags: true
      });

      console.log(`âœ“ Created campaign: ${campaign.id}`);
      results.tests.push({ 
        name: "campaign-creation", 
        status: "PASS",
        campaignId: campaign.id,
        postCount: campaign.posts?.length || 0
      });
    } catch (campError) {
      console.warn("Campaign creation failed:", campError.message);
      results.tests.push({ 
        name: "campaign-creation", 
        status: "FAIL",
        error: campError.message
      });
    }

    // Test 7: Check credits after operations
    const userAfter = await getUser(auth.cookie);
    const creditsAfter = userAfter.credits || 0;
    const trialImagesAfter = userAfter.trialImagesRemaining || 0;
    console.log(`Credits after: ${creditsAfter}, Trial Images after: ${trialImagesAfter}`);

    results.tests.push({ 
      name: "user-state-after", 
      status: "PASS",
      credits: creditsAfter,
      trialImages: trialImagesAfter,
      creditsUsed: creditsBefore - creditsAfter,
      trialImagesUsed: trialImagesBefore - trialImagesAfter
    });

    // Save results
    results.status = results.tests.every(t => t.status === "PASS" || t.status === "SKIP") ? "PASS" : "FAIL";
    fs.mkdirSync(path.join(ART_DIR, "data"), { recursive: true });
    fs.writeFileSync(
      path.join(ART_DIR, "data", "integration.json"),
      JSON.stringify(results, null, 2)
    );

    console.log(`\nâœ“ Integration tests: ${results.status}`);
    process.exit(results.status === "PASS" ? 0 : 1);

  } catch (error) {
    console.error("Integration test error:", error);
    results.error = error.message;
    results.status = "FAIL";

    fs.mkdirSync(path.join(ART_DIR, "data"), { recursive: true });
    fs.writeFileSync(
      path.join(ART_DIR, "data", "integration-error.json"),
      JSON.stringify(results, null, 2)
    );

    process.exit(1);
  }
})();

scripts/test-runner.js.content:
// scripts/test-runner.js
/* eslint-disable no-console */
import { execSync } from "node:child_process";
import fs from "node:fs";
import path from "node:path";
import dayjs from "dayjs";

const PHASE = process.argv.includes("--phase=int") ? "int"
           : process.argv.includes("--phase=e2e") ? "e2e" : "full";

const RUN_ID = dayjs().format("YYYYMMDD-HHmmss");
const ROOT = path.resolve(".");
const ART_DIR = path.join(ROOT, "test-artifacts", RUN_ID);
const LOG_DIR = path.join(ART_DIR, "logs");
const DATA_DIR = path.join(ART_DIR, "data");
const IMG_DIR = path.join(ART_DIR, "images");
const VID_DIR = path.join(ART_DIR, "videos");
const SCR_DIR = path.join(ART_DIR, "screens");

for (const d of [ART_DIR, LOG_DIR, DATA_DIR, IMG_DIR, VID_DIR, SCR_DIR]) fs.mkdirSync(d, { recursive: true });

function run(cmd, env = {}) {
  console.log(`\n$ ${cmd}`);
  try {
    execSync(cmd, { stdio: "inherit", env: { ...process.env, ...env } });
  } catch (error) {
    console.error(`Command failed: ${cmd}`);
    throw error;
  }
}

function writeJson(p, obj) {
  fs.writeFileSync(p, JSON.stringify(obj, null, 2));
}

async function buildReport(results, outDir) {
  const dataDir = path.join(outDir, "data");
  const imgDir = path.join(outDir, "images");
  const vidDir = path.join(outDir, "videos");
  const scrDir = path.join(outDir, "screens");
  const list = (dir, exts) => fs.existsSync(dir) ? fs.readdirSync(dir).filter(f => exts.some(e => f.endsWith(e))) : [];

  const imgs = list(imgDir, [".png",".jpg",".jpeg"]);
  const vids = list(vidDir, [".mp4",".webm"]);
  const scrs = list(scrDir, [".png"]);

  const html = `<!doctype html>
<html><head>
<meta charset="utf-8"/>
<title>Test Report ${results.runId}</title>
<style>
 body{background:#0a0a0b;color:#e5e7eb;font-family:ui-sans-serif,system-ui}
 .chip{display:inline-block;padding:4px 8px;border-radius:999px;margin-right:6px}
 .pass{background:#065f46} .fail{background:#7f1d1d}
 .grid{display:grid;grid-template-columns:repeat(auto-fill,minmax(220px,1fr));gap:12px}
 .card{background:#111114;border:1px solid #1f2937;border-radius:12px;padding:10px}
 video, img{width:100%;border-radius:8px}
 a{color:#a78bfa}
</style></head>
<body>
<h1>Run ${results.runId} â€” <span class="chip ${results.status==="PASS"?"pass":"fail"}">${results.status}</span></h1>
<h2>Phases</h2>
${results.phases.map(p=>`<span class="chip ${p.status==="PASS"?"pass":"fail"}">${p.name}: ${p.status}</span>`).join("")}

<h2>UI Screenshots</h2>
<div class="grid">${scrs.map(f=>`<div class="card"><img src="./screens/${f}"/><div>${f}</div></div>`).join("")}</div>

<h2>Generated Images</h2>
<div class="grid">${imgs.map(f=>`<div class="card"><img src="./images/${f}"/><div>${f}</div></div>`).join("")}</div>

<h2>Generated Videos</h2>
<div class="grid">${vids.map(f=>`<div class="card"><video controls src="./videos/${f}"></video><div>${f}</div></div>`).join("")}</div>

<h2>Data</h2>
<ul>
  ${(fs.existsSync(dataDir)?fs.readdirSync(dataDir):[]).map(f=>`<li><a href="./data/${f}">${f}</a></li>`).join("")}
</ul>
</body></html>`;
  fs.writeFileSync(path.join(outDir, "report.html"), html);
}

(async () => {
  const results = { runId: RUN_ID, startedAt: new Date().toISOString(), phases: [], status: "PENDING" };

  // Health gates
  try {
    run(`node scripts/check-health.js ${ART_DIR}`);
    results.phases.push({ name: "health", status: "PASS" });
  } catch (error) {
    console.error("Health check failed:", error.message);
    results.phases.push({ name: "health", status: "FAIL" });
    results.status = "FAIL";
    await buildReport(results, ART_DIR);
    process.exit(1);
  }

  // Unit (optional)
  if (PHASE === "full") {
    try {
      // Using vitest if available, otherwise skip
      if (fs.existsSync(path.join(ROOT, "node_modules", ".bin", "vitest"))) {
        run(`npm run test:unit`);
        results.phases.push({ name: "unit", status: "PASS" });
      } else {
        console.log("Skipping unit tests (vitest not installed)");
        results.phases.push({ name: "unit", status: "SKIP" });
      }
    } catch (error) {
      console.error("Unit tests failed:", error.message);
      results.phases.push({ name: "unit", status: "FAIL" });
      results.status = "FAIL";
    }
  }

  // Integration API checks
  if (PHASE === "full" || PHASE === "int") {
    try {
      run(`node scripts/run-int-tests.js ${ART_DIR}`);
      results.phases.push({ name: "integration", status: "PASS" });
    } catch (error) {
      console.error("Integration tests failed:", error.message);
      results.phases.push({ name: "integration", status: "FAIL" });
      results.status = "FAIL";
    }
  }

  // E2E (artifact producing)
  if (PHASE === "full" || PHASE === "e2e") {
    try {
      // Check if playwright config exists
      const configPath = path.join(ROOT, "playwright.config.ts");
      if (fs.existsSync(configPath)) {
        run(`npx playwright test --config=${configPath} --reporter=list`, {
          ART_DIR, IMG_DIR, VID_DIR, SCR_DIR, DATA_DIR
        });
      } else {
        // Run with default config
        run(`npx playwright test --reporter=list`, {
          ART_DIR, IMG_DIR, VID_DIR, SCR_DIR, DATA_DIR
        });
      }
      results.phases.push({ name: "e2e", status: "PASS" });
    } catch (error) {
      console.error("E2E tests failed:", error.message);
      results.phases.push({ name: "e2e", status: "FAIL" });
      results.status = "FAIL";
    }
  }

  results.status = results.status === "PENDING" ? "PASS" : results.status;
  results.endedAt = new Date().toISOString();
  writeJson(path.join(DATA_DIR, "results.json"), results);
  await buildReport(results, ART_DIR);
  console.log(`\nâœ… Test Run Complete: ${results.status}`);
  console.log(`ðŸ“ Artifacts: ${ART_DIR}`);
  console.log(`ðŸ“Š Report: ${path.join(ART_DIR, "report.html")}`);

  if (results.status !== "PASS") {
    console.log(`\nâŒ Tests failed. Review the report for details.`);
    process.exit(1);
  }
})();

