import { GoogleGenAI } from "@google/genai";
import * as fs from "fs/promises";
import path from "path";
import { randomUUID } from "crypto";

// Initialize Gemini client with the provided API key
const geminiApiKey = process.env.GEMINI_API_KEY;
if (!geminiApiKey) {
  throw new Error("GEMINI_API_KEY is required for Veo 3 video generation");
}

const ai = new GoogleGenAI({ apiKey: geminiApiKey });

// Correct Veo 3 model IDs from Google's documentation
const VEO_MODELS = {
  fast: "veo-3.0-fast-generate-001",
  standard: "veo-3.0-generate-001"
};

export interface GeminiVideoOptions {
  prompt: string;
  duration?: number;
  aspectRatio?: string;
  model?: "veo-3-fast" | "veo-3";
}

export async function generateVideoWithVeo3(opts: GeminiVideoOptions): Promise<Buffer> {
  try {
    console.log('Starting Veo 3 video generation...');

    // Select the correct model ID
    const modelId = opts.model === "veo-3-fast" ? VEO_MODELS.fast : VEO_MODELS.standard;
    console.log(`Using model: ${modelId}`);

    // Start video generation with correct model ID
    let op = await ai.models.generateVideos({
      model: modelId,
      prompt: opts.prompt,
      config: {
        aspectRatio: opts.aspectRatio || "16:9",
        resolution: "720p"
      }
    });

    console.log('Video generation started, polling for completion...');

    // Poll until the operation is done (max 3 minutes)
    const maxAttempts = 18; // 18 * 10 seconds = 3 minutes
    let attempts = 0;

    while (!op.done && attempts < maxAttempts) {
      await new Promise(resolve => setTimeout(resolve, 10000)); // Wait 10 seconds
      op = await ai.operations.getVideosOperation({ operation: op });
      attempts++;
      console.log(`Polling attempt ${attempts}/${maxAttempts}, done: ${op.done}`);
    }

    if (!op.done) {
      throw new Error("Video generation timed out after 3 minutes");
    }

    if (!op.response?.generatedVideos?.length) {
      throw new Error("Veo 3 returned no video");
    }

    // Get the generated video file
    const videoFile = op.response.generatedVideos[0].video;
    if (!videoFile) {
      throw new Error("No video file in response");
    }
    console.log('Video generated successfully, downloading...');

    // Download the video file using the files API
    let videoBuffer: Buffer;

    try {
      console.log('Downloading video file:', videoFile);

      // The GenAI SDK download method doesn't actually download the file content
      // Instead, we need to use the file's URI with proper authentication
      if ((videoFile as any).uri || (videoFile as any).url) {
        let fileUri = (videoFile as any).uri || (videoFile as any).url;
        console.log('Original video URI:', fileUri);

        // The URI already has the download endpoint, we need to add the API key as a query parameter
        const apiKey = process.env.GEMINI_API_KEY || process.env.GOOGLE_API_KEY;
        const separator = fileUri.includes('?') ? '&' : '?';
        fileUri = `${fileUri}${separator}key=${apiKey}`;

        console.log('Fetching video from URI with API key...');
        const response = await fetch(fileUri);
        if (!response.ok) {
          const errorText = await response.text();
          console.error('Download error response:', errorText);
          throw new Error(`Failed to fetch video: ${response.statusText} (${response.status})`);
        }

        const arrayBuffer = await response.arrayBuffer();
        videoBuffer = Buffer.from(arrayBuffer);
        console.log(`Downloaded video successfully, size: ${videoBuffer.length} bytes`);
      } else {
        // If no URI is available, throw an error
        throw new Error("Video file has no URI for download. File details: " + JSON.stringify(videoFile));
      }

      console.log('Veo 3 video generation completed successfully');
      return videoBuffer;
    } catch (downloadError: any) {
      console.error('Failed to download video:', downloadError);
      throw downloadError;
    }

  } catch (error: any) {
    console.error('Veo 3 generation error:', error);

    // If it's a model not found error and we were trying fast, fallback to standard
    if (opts.model === "veo-3-fast" && error.message?.includes('not found')) {
      console.log('Falling back to standard Veo 3 model...');
      return generateVideoWithVeo3({
        ...opts,
        model: "veo-3"
      });
    }

    throw error;
  }
}

export async function generateVideoWithGemini(prompt: string, duration: number = 8, aspectRatio: string = "16:9"): Promise<Buffer> {
  return generateVideoWithVeo3({
    prompt,
    duration,
    aspectRatio,
    model: "veo-3-fast" // Default to fast model
  });
}

client/src/pages/auth.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useToast } from "@/hooks/use-toast";
import { Loader2, Mail, Chrome, AlertCircle } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";

// Validation schemas
const loginSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(8, "Password must be at least 8 characters"),
});

const signupSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  username: z.string().min(3, "Username must be at least 3 characters").max(30, "Username must be less than 30 characters"),
  password: z.string().min(8, "Password must be at least 8 characters"),
  confirmPassword: z.string(),
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  businessName: z.string().optional(),
  referralCode: z.string().optional(),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type LoginFormData = z.infer<typeof loginSchema>;
type SignupFormData = z.infer<typeof signupSchema>;

export default function Auth() {
  const [location, setLocation] = useLocation();
  const { toast } = useToast();
  const [tab, setTab] = useState<"login" | "signup">("login");
  const [referralCode, setReferralCode] = useState<string>("");
  const [referrerName, setReferrerName] = useState<string>("");
  const [oauthError, setOauthError] = useState<string>("");

  // Parse return URL, referral code, and OAuth errors from query params
  const params = new URLSearchParams(location.split('?')[1] || '');
  const returnUrl = params.get('return') ? decodeURIComponent(params.get('return')!) : '/';
  const refCode = params.get('ref') || '';
  const errorParam = params.get('error') || '';
  const errorDetails = params.get('details') || '';

  // Handle OAuth errors and validate referral code on component mount
  useEffect(() => {
    // Handle OAuth errors
    if (errorParam) {
      let errorMessage = 'Authentication failed. Please try again.';

      switch(errorParam) {
        case 'oauth_failed':
          errorMessage = 'Google authentication failed. Please try again.';
          break;
        case 'no_user_object':
          errorMessage = 'Failed to retrieve user information from Google. Please try again.';
          break;
        case 'session_save_failed':
        case 'session_failed':
          errorMessage = 'Failed to create session. Please try again or use email/password login.';
          break;
        case 'csrf_state_mismatch':
          errorMessage = 'Security verification failed. Please try again.';
          break;
        case 'google_oauth_error':
          errorMessage = `Google authentication error: ${errorDetails || 'Unknown error'}`;
          break;
        case 'google_auth_failed':
          errorMessage = 'Google authentication was cancelled or failed. Please try again.';
          break;
        case 'passport_auth_failed':
          errorMessage = `Authentication failed: ${errorDetails || 'Please try again'}`;
          break;
        case 'callback_exception':
          errorMessage = 'An error occurred during authentication. Please try again.';
          break;
        case 'no_user':
          errorMessage = 'Authentication successful but user not found. Please sign up first.';
          break;
        case 'invalid_user':
          errorMessage = 'Invalid user data received from Google. Please try again.';
          break;
      }

      setOauthError(errorMessage);
      toast({
        title: "Authentication Error",
        description: errorMessage,
        variant: "destructive",
      });
    }

    // Handle referral code
    if (refCode) {
      setReferralCode(refCode);
      setTab('signup'); // Switch to signup tab if referral code is present

      // Validate the referral code
      fetch(`/api/referral/validate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ referralCode: refCode }),
      })
        .then(res => res.json())
        .then(data => {
          if (data.valid) {
            setReferrerName(data.referrerName || "");
            toast({
              title: "Referral Applied!",
              description: `You were referred by ${data.referrerName}. You'll get 25 bonus credits when you sign up!`,
            });
          }
        })
        .catch(() => {});
    }
  }, [refCode, errorParam, errorDetails, toast]);

  // Login form
  const loginForm = useForm<LoginFormData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  // Signup form
  const signupForm = useForm<SignupFormData>({
    resolver: zodResolver(signupSchema),
    defaultValues: {
      email: "",
      username: "",
      password: "",
      confirmPassword: "",
      firstName: "",
      lastName: "",
      businessName: "",
      referralCode: refCode || "",
    },
  });

  // Login mutation
  const loginMutation = useMutation({
    mutationFn: async (data: LoginFormData) => {
      const response = await apiRequest("POST", "/api/auth/login", data);
      return await response.json();
    },
    onSuccess: (data) => {
      if (data.requiresVerification) {
        // Redirect to verification page
        localStorage.setItem("verificationEmail", data.email);
        toast({
          title: "Email verification required",
          description: "Please verify your email to access your account.",
        });
        setLocation(`/verify-email?email=${encodeURIComponent(data.email)}`);
      } else {
        queryClient.invalidateQueries({ queryKey: ["/api/user"] });
        queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
        toast({
          title: "Welcome back!",
          description: "You have successfully logged in.",
        });
        setLocation(returnUrl);
      }
    },
    onError: (error: any) => {
      const errorData = error.response?.data || error;

      if (errorData.requiresVerification) {
        // Email not verified - redirect to verification
        localStorage.setItem("verificationEmail", errorData.email);
        toast({
          title: "Email verification required",
          description: errorData.message || "Please verify your email before logging in.",
        });
        setLocation(`/verify-email?email=${encodeURIComponent(errorData.email)}`);
      } else {
        toast({
          title: "Login failed",
          description: errorData.message || "Invalid email or password",
          variant: "destructive",
        });
      }
    },
  });

  // Signup mutation
  const signupMutation = useMutation({
    mutationFn: async (data: SignupFormData) => {
      const { confirmPassword, ...signupData } = data;
      const response = await apiRequest("POST", "/api/auth/signup", signupData);
      const result = await response.json();

      // Process referral if code was provided
      if (signupData.referralCode && result.userId) {
        try {
          await apiRequest("POST", "/api/referral/process", {
            referralCode: signupData.referralCode,
            newUserId: result.userId,
          });
        } catch (error) {
          console.error("Failed to process referral:", error);
        }
      }

      return result;
    },
    onSuccess: (data) => {
      if (data.requiresVerification) {
        // New account needs email verification
        localStorage.setItem("verificationEmail", data.email);
        toast({
          title: "Account created!",
          description: data.message || "Please check your email for verification code.",
        });
        setLocation(`/verify-email?email=${encodeURIComponent(data.email)}`);
      } else {
        queryClient.invalidateQueries({ queryKey: ["/api/user"] });
        queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
        toast({
          title: "Account created!",
          description: "Welcome to MyAI MediaMgr. Your free trial has started.",
        });
        // New accounts go to trial selection, unless they have a specific return URL for checkout
        setLocation(returnUrl.includes('/checkout') ? returnUrl : "/");
      }
    },
    onError: (error: any) => {
      toast({
        title: "Registration failed",
        description: error.message || "Failed to create account",
        variant: "destructive",
      });
    },
  });

  const handleGoogleLogin = () => {
