          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6 mt-6">
          {/* Current Trial Status */}
          <Card className="border-green-500/50 bg-green-50/10">
            <CardHeader>
              <CardTitle className="text-lg flex items-center gap-2">
                <CheckCircle className="w-5 h-5 text-green-500" />
