                </li>
              </ul>
              <Button
                onClick={() => upgradeToCardTrial.mutate()}
                disabled={processingUpgrade}
                className="bg-purple-600 hover:bg-purple-700"
              >
                <CreditCard className="w-4 h-4 mr-2" />
