            </CardContent>
          </Card>

          {/* Subscription Plans */}
          <div>
            <h3 className="text-lg font-semibold mb-4">Ready to go beyond the trial?</h3>
            <div className="grid md:grid-cols-3 gap-4">
              {subscriptionTiers.map((tier) => (
                <Card 
                  key={tier.id} 
                  className={tier.popular ? "border-blue-500 relative" : ""}
