import { Router } from "express";
import { storage } from "./storage";
import { z } from "zod";
import bcrypt from "bcryptjs";
import { requireAuth } from "./auth";
// Stripe import removed - will use stripeRoutes instead
import type { Request, Response } from "express";

const router = Router();

// User ID helper
function getUserId(req: any): string | null {
  if (req.session?.userId) return req.session.userId;
  if (req.user?.id) return req.user.id;
  if (req.user?.claims?.sub) return req.user.claims.sub;
  return null;
}

// Get user billing history
router.get("/billing-history", requireAuth, async (req: Request, res: Response) => {
  try {
    const userId = getUserId(req);
    if (!userId) {
      return res.status(401).json({ message: "Not authenticated" });
    }

    const transactions = await storage.getCreditTransactionsByUserId(userId);

    // Format transactions for display
    const formattedTransactions = transactions.map(tx => ({
      id: tx.id,
      description: tx.description || `${tx.type === 'purchase' ? 'Purchased' : 'Used'} ${Math.abs(tx.amount)} credits`,
      amount: tx.type === 'purchase' ? (Math.abs(tx.amount) / 10).toFixed(2) : 0, // Assuming $0.10 per credit
      status: 'completed',
      createdAt: tx.createdAt
    }));

    res.json(formattedTransactions);
  } catch (error: any) {
    console.error("Error fetching billing history:", error);
    res.status(500).json({ message: error.message });
  }
});

// Change password
router.patch("/password", requireAuth, async (req: Request, res: Response) => {
  try {
    const userId = getUserId(req);
    if (!userId) {
      return res.status(401).json({ message: "Not authenticated" });
    }

    const { currentPassword, newPassword } = z.object({
      currentPassword: z.string().min(1),
      newPassword: z.string().min(8)
    }).parse(req.body);

    const user = await storage.getUser(userId);
    if (!user) {
      return res.status(404).json({ message: "User not found" });
    }

    // Verify current password
    if (!user.password) {
      return res.status(400).json({ message: "Password authentication not enabled for this account" });
    }

    const isValid = await bcrypt.compare(currentPassword, user.password);
    if (!isValid) {
      return res.status(401).json({ message: "Current password is incorrect" });
    }

    // Hash new password
    const hashedPassword = await bcrypt.hash(newPassword, 10);

    // Update password
    await storage.updateUser(userId, { password: hashedPassword });

    res.json({ message: "Password changed successfully" });
  } catch (error: any) {
    console.error("Error changing password:", error);
    if (error instanceof z.ZodError) {
      return res.status(400).json({ message: "Invalid input", errors: error.errors });
    }
    res.status(500).json({ message: error.message });
  }
});

// Change email
router.patch("/email", requireAuth, async (req: Request, res: Response) => {
  try {
    const userId = getUserId(req);
    if (!userId) {
      return res.status(401).json({ message: "Not authenticated" });
    }

    const { newEmail } = z.object({
      newEmail: z.string().email()
    }).parse(req.body);

    const user = await storage.getUser(userId);
    if (!user) {
      return res.status(404).json({ message: "User not found" });
    }

    // Check if email is already in use
    const existingUser = await storage.getUserByEmail(newEmail);
    if (existingUser && existingUser.id !== userId) {
      return res.status(400).json({ message: "Email address is already in use" });
    }

    // Update email (in production, you'd want to send verification email first)
    await storage.updateUser(userId, { 
      email: newEmail,
      emailVerified: false // Reset verification status
    });

    // In production, send verification email here

    res.json({ message: "Email updated successfully. Please check your email for verification." });
  } catch (error: any) {
    console.error("Error changing email:", error);
    if (error instanceof z.ZodError) {
      return res.status(400).json({ message: "Invalid email address" });
    }
    res.status(500).json({ message: error.message });
  }
});

// Delete account
router.delete("/account", requireAuth, async (req: Request, res: Response) => {
  try {
    const userId = getUserId(req);
    if (!userId) {
      return res.status(401).json({ message: "Not authenticated" });
    }

    const user = await storage.getUser(userId);
    if (!user) {
      return res.status(404).json({ message: "User not found" });
    }

    // Cancel any active subscriptions with Stripe
    if (user.stripeSubscriptionId) {
      try {
        await stripe.subscriptions.cancel(user.stripeSubscriptionId);
      } catch (stripeError) {
        console.error("Error cancelling Stripe subscription:", stripeError);
      }
    }

    // Soft delete the account
    await storage.updateUser(userId, {
      accountStatus: "deleted",
      subscriptionStatus: "cancelled",
      deletedAt: new Date()
    });

    // Destroy the session
    if (req.session) {
      req.session.destroy((err) => {
        if (err) {
          console.error("Error destroying session:", err);
        }
      });
    }

    res.json({ message: "Account deleted successfully" });
  } catch (error: any) {
    console.error("Error deleting account:", error);
    res.status(500).json({ message: error.message });
  }
});

export default router;

server/verificationRoutes.ts.content:
import { Router } from 'express';
import { z } from 'zod';
import { storage } from './storage';
import {
  generateVerificationCode,
  hashVerificationCode,
  verifyCode,
  sendVerificationEmail,
  sendWelcomeEmail,
  emailRateLimiter,
  verificationRateLimiter,
} from './emailService';

const router = Router();

// Validation schemas
const sendVerificationSchema = z.object({
  email: z.string().email(),
});

const verifyEmailSchema = z.object({
  email: z.string().email(),
  code: z.string().length(6).regex(/^\d{6}$/),
});

// Send verification code endpoint
router.post('/send-verification', async (req, res) => {
  try {
    const { email } = sendVerificationSchema.parse(req.body);

    // Check rate limiting for email sending
    if (!emailRateLimiter.isAllowed(email)) {
      const remainingMs = emailRateLimiter.getRemainingTime(email);
      const remainingMinutes = Math.ceil(remainingMs / 60000);
      return res.status(429).json({
        message: `Too many verification emails. Please wait ${remainingMinutes} minutes before requesting another code.`,
        retryAfter: remainingMs,
      });
    }

    // Find user by email
    const user = await storage.getUserByEmail(email);
    if (!user) {
      return res.status(404).json({ message: 'No account found with this email address' });
    }

    // Check if already verified
    if (user.emailVerified) {
      return res.status(400).json({ message: 'Email is already verified' });
    }

    // Generate new verification code
    const code = generateVerificationCode();
    const hashedCode = await hashVerificationCode(code);
    const expiry = new Date(Date.now() + 10 * 60 * 1000); // 10 minutes from now

    // Update user with new verification code
    await storage.updateUser(user.id, {
      emailVerificationCode: hashedCode,
      emailVerificationExpiry: expiry,
      emailVerificationAttempts: 0,
    });

    // Send verification email
    const emailSent = await sendVerificationEmail(email, code);

    if (!emailSent) {
      return res.status(500).json({ message: 'Failed to send verification email. Please try again.' });
    }

    res.json({
      message: 'Verification code sent to your email',
      expiresAt: expiry.toISOString(),
    });
  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ message: 'Invalid email format', errors: error.errors });
    }
    console.error('Send verification error:', error);
    res.status(500).json({ message: 'Failed to send verification code' });
  }
});

// Verify email with code endpoint
router.post('/verify-email', async (req, res) => {
  try {
    const { email, code } = verifyEmailSchema.parse(req.body);

    // Check rate limiting for verification attempts
    const rateLimitKey = `verify:${email}`;
    if (!verificationRateLimiter.isAllowed(rateLimitKey)) {
      const remainingMs = verificationRateLimiter.getRemainingTime(rateLimitKey);
      const remainingMinutes = Math.ceil(remainingMs / 60000);
      return res.status(429).json({
        message: `Too many verification attempts. Please wait ${remainingMinutes} minutes before trying again.`,
        retryAfter: remainingMs,
      });
    }

    // Find user by email
    const user = await storage.getUserByEmail(email);
    if (!user) {
      return res.status(404).json({ message: 'No account found with this email address' });
    }

    // Check if already verified
    if (user.emailVerified) {
      return res.status(400).json({ message: 'Email is already verified' });
    }

    // Check if verification code exists
    if (!user.emailVerificationCode || !user.emailVerificationExpiry) {
      return res.status(400).json({ 
        message: 'No verification code found. Please request a new code.',
        requiresNewCode: true,
      });
    }

    // Check if code has expired
    if (new Date() > user.emailVerificationExpiry) {
      return res.status(400).json({ 
        message: 'Verification code has expired. Please request a new code.',
        requiresNewCode: true,
      });
    }

    // Check attempt limit
    const attempts = (user.emailVerificationAttempts || 0) + 1;
    if (attempts > 5) {
      // Clear the code to force a new one
      await storage.updateUser(user.id, {
        emailVerificationCode: null,
        emailVerificationExpiry: null,
        emailVerificationAttempts: 0,
      });
      return res.status(400).json({ 
        message: 'Too many incorrect attempts. Please request a new code.',
        requiresNewCode: true,
      });
    }

    // Verify the code
    const isValid = await verifyCode(code, user.emailVerificationCode);

    if (!isValid) {
      // Increment attempt counter
      await storage.updateUser(user.id, {
        emailVerificationAttempts: attempts,
      });

      const remainingAttempts = 5 - attempts;
      return res.status(400).json({ 
        message: `Invalid verification code. ${remainingAttempts} attempts remaining.`,
        remainingAttempts,
      });
    }

    // Code is valid! Mark email as verified
    await storage.updateUser(user.id, {
      emailVerified: true,
      emailVerificationCode: null,
      emailVerificationExpiry: null,
      emailVerificationAttempts: 0,
    });

    // Reset rate limiters for this user
    emailRateLimiter.reset(email);
    verificationRateLimiter.reset(rateLimitKey);

    // Send welcome email
    await sendWelcomeEmail(email);

    // Create session if not already logged in
    if (!req.session?.userId) {
      req.session.userId = user.id;
      req.session.user = {
        id: user.id,
        email: user.email,
        username: user.username,
        businessName: user.businessName,
        role: user.role,
        tier: user.tier,
        isAdmin: user.isAdmin,
      };
    }

    res.json({
      message: 'Email verified successfully! Welcome to MyAI MediaMgr.',
      verified: true,
      user: {
        id: user.id,
        email: user.email,
        username: user.username,
      },
    });
  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ message: 'Invalid input format', errors: error.errors });
    }
    console.error('Verify email error:', error);
    res.status(500).json({ message: 'Failed to verify email' });
  }
});

// Check verification status endpoint
router.get('/verification-status', async (req, res) => {
  try {
    const email = req.query.email as string;

    if (!email) {
      return res.status(400).json({ message: 'Email parameter is required' });
    }

    const user = await storage.getUserByEmail(email);
    if (!user) {
      return res.status(404).json({ message: 'No account found with this email address' });
    }

    const hasActiveCode = !!(
      user.emailVerificationCode && 
      user.emailVerificationExpiry && 
      new Date() < user.emailVerificationExpiry
    );

    res.json({
      emailVerified: user.emailVerified,
      hasActiveCode,
      expiresAt: hasActiveCode ? user.emailVerificationExpiry : null,
    });
  } catch (error) {
    console.error('Check verification status error:', error);
    res.status(500).json({ message: 'Failed to check verification status' });
  }
});

export default router;

