import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { CheckCircle2, XCircle, Loader2, AlertCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export default function CheckoutReturn() {
  const [location, setLocation] = useLocation();
  const { toast } = useToast();

  // Parse session ID from URL
  const params = new URLSearchParams(location.split('?')[1] || '');
  const sessionId = params.get("session_id");

  const [sessionStatus, setSessionStatus] = useState<string>("");
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string>("");

  useEffect(() => {
    const checkSession = async () => {
      if (!sessionId) {
        setError("No session ID found");
        setIsLoading(false);
        return;
      }

      try {
        const data = await apiRequest({
          url: `/api/billing/session-status/${sessionId}`,
          method: "GET"
        });

        setSessionStatus(data.status);

        if (data.status === "complete") {
          // Payment successful - invalidate user data to refresh subscription status
          queryClient.invalidateQueries({ queryKey: ["/api/user"] });

          toast({
            title: "Payment Successful!",
            description: "Your subscription has been activated.",
          });

          // Redirect to dashboard after 3 seconds
          setTimeout(() => {
            setLocation("/");
          }, 3000);
        } else if (data.status === "open") {
          // Payment still processing or failed
          setError("Payment was not completed. Please try again.");
        }
      } catch (err: any) {
        console.error("Error checking session:", err);
        setError("Unable to verify payment status");
      } finally {
        setIsLoading(false);
      }
    };

    checkSession();
  }, [sessionId, setLocation, toast]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-950 via-purple-950 to-pink-950 flex items-center justify-center">
        <Card className="max-w-md w-full">
          <CardContent className="p-8">
            <div className="flex flex-col items-center space-y-4">
              <Loader2 className="h-12 w-12 animate-spin text-purple-500" />
              <p className="text-lg text-white">Verifying your payment...</p>
              <p className="text-sm text-gray-400">Please wait while we confirm your transaction</p>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-950 via-purple-950 to-pink-950 flex items-center justify-center">
        <Card className="max-w-md w-full">
          <CardContent className="p-8">
            <div className="flex flex-col items-center space-y-4">
              <XCircle className="h-12 w-12 text-red-500" />
              <h2 className="text-xl font-semibold text-white">Payment Issue</h2>
              <p className="text-center text-gray-400">{error}</p>
              <div className="flex gap-3 mt-4">
                <Button 
                  onClick={() => setLocation("/checkout")}
                  variant="outline"
                >
                  Try Again
                </Button>
                <Button 
                  onClick={() => setLocation("/")}
                >
                  Go to Dashboard
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (sessionStatus === "complete") {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-950 via-purple-950 to-pink-950 flex items-center justify-center">
        <Card className="max-w-md w-full">
          <CardContent className="p-8">
            <div className="flex flex-col items-center space-y-4">
              <CheckCircle2 className="h-16 w-16 text-green-500" />
              <h2 className="text-2xl font-bold text-white">Payment Successful!</h2>
              <p className="text-center text-gray-300">
                Thank you for your subscription. Your account has been upgraded successfully.
              </p>
              <p className="text-sm text-gray-400">
                Redirecting to dashboard in a few seconds...
              </p>
              <Button 
                onClick={() => setLocation("/")}
                className="mt-4 bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700"
              >
                Go to Dashboard Now
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Fallback for unknown status
  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-950 via-purple-950 to-pink-950 flex items-center justify-center">
      <Card className="max-w-md w-full">
        <CardContent className="p-8">
          <div className="flex flex-col items-center space-y-4">
            <AlertCircle className="h-12 w-12 text-yellow-500" />
            <h2 className="text-xl font-semibold text-white">Payment Status Unknown</h2>
            <p className="text-center text-gray-400">
              We couldn't determine the status of your payment. Please check your email for confirmation.
            </p>
            <Button 
              onClick={() => setLocation("/")}
              className="mt-4"
            >
              Go to Dashboard
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

server/stripe-webhook.ts.content:
import { Router, type Request, type Response } from "express";
import express from "express";
import Stripe from "stripe";
import { storage } from "./storage";

if (!process.env.STRIPE_SECRET_KEY) {
  throw new Error('Missing required Stripe secret: STRIPE_SECRET_KEY');
}

const stripe = new Stripe(process.env.STRIPE_SECRET_KEY, {
  apiVersion: "2024-11-20.acacia" as Stripe.LatestApiVersion,
  typescript: true,
});

const router = Router();

// Define pricing for plans (in cents)
const PLAN_PRICES = {
  starter: {
    price: 1900, // $19
    name: "Starter Plan",
    description: "190 credits per month",
    credits: 190
  },
  professional: {
    price: 4900, // $49
    name: "Professional Plan", 
    description: "500 credits per month",
    credits: 500
  },
  business: {
    price: 19900, // $199
    name: "Business Plan",
    description: "2000 credits per month",
    credits: 2000
  }
};

// IMPORTANT: This webhook handler MUST use express.raw() for body parsing
// and MUST be registered BEFORE any express.json() middleware
router.post("/webhook", 
  express.raw({ type: "application/json" }), 
  async (req, res) => {
    const sig = req.headers['stripe-signature'] as string;
    const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;

    if (!webhookSecret) {
      console.error("STRIPE_WEBHOOK_SECRET not configured");
      // Still return 200 to prevent retries
      return res.sendStatus(200);
    }

    let event: Stripe.Event;

    try {
      // Verify the webhook signature using the raw body
      event = stripe.webhooks.constructEvent(req.body, sig, webhookSecret);
    } catch (err: any) {
      console.error(`Webhook signature verification failed: ${err.message}`);
      return res.status(400).send(`Webhook Error: ${err.message}`);
    }

    // Handle the event
    try {
      switch (event.type) {
        case 'checkout.session.completed': {
          const session = event.data.object as Stripe.Checkout.Session;

          // Fulfill the purchase
          const userId = session.metadata?.userId;
          const type = session.metadata?.type;
          const credits = parseInt(session.metadata?.credits || '0');
          const planId = session.metadata?.planId;

          if (userId) {
            const user = await storage.getUser(userId);
            if (user) {
              if (type === 'credits') {
                // One-time credit purchase
                await storage.updateUser(userId, {
                  credits: (user.credits || 0) + credits
                });

                // Log the transaction
                await storage.createCreditTransaction({
                  userId,
                  amount: credits,
                  type: 'purchase',
                  description: `Purchased ${credits} credits`,
                  stripeSessionId: session.id
                });

                console.log(`âœ… Fulfilled credit purchase for user ${userId}: ${credits} credits`);
              } else if (planId) {
                // Subscription purchase
                const subscriptionId = session.subscription as string;

                // Unlock account if it was locked due to trial expiration
                await storage.updateUser(userId, {
                  tier: planId as any,
                  stripeSubscriptionId: subscriptionId,
                  credits: (user.credits || 0) + credits,
                  monthlyCredits: credits,
                  subscriptionStatus: 'active',
                  isLocked: false, // Unlock the account
                  accountStatus: 'active', // Reactivate the account
                  pausedAt: null,
                  pausedReason: null,
                  isPaid: true
                });

                // Log the transaction
                await storage.createCreditTransaction({
                  userId,
                  amount: credits,
                  type: 'purchase',
                  description: `${PLAN_PRICES[planId as keyof typeof PLAN_PRICES].name} subscription`,
                  stripeSessionId: session.id
                });

                // Send notification about account unlock
                await storage.createNotification({
                  userId,
                  title: "Welcome to Your Subscription!",
                  message: `Your account has been activated with the ${PLAN_PRICES[planId as keyof typeof PLAN_PRICES].name}. You now have ${credits} credits available.`,
                  type: 'success'
                });

                console.log(`âœ… Activated subscription and unlocked account for user ${userId}: ${planId} plan`);
              }
            }
          }
          break;
        }

        case 'invoice.paid': {
          const invoice = event.data.object as Stripe.Invoice;
          console.log(`âœ… Invoice paid: ${invoice.id}`);

          // Handle successful recurring payment - refresh monthly credits
          const subscriptionId = typeof (invoice as any).subscription === 'string' ? (invoice as any).subscription : undefined;
          const customerId = typeof invoice.customer === 'string' ? invoice.customer : undefined;

          if (subscriptionId) {
            // Find user by customer ID
            const users = await storage.getAllUsers();
            const user = users.find(u => u.stripeCustomerId === customerId);

            if (user && user.monthlyCredits) {
              // Refresh monthly credits on successful payment
              await storage.updateUser(user.id, {
                credits: (user.credits || 0) + user.monthlyCredits
              });

              await storage.createCreditTransaction({
                userId: user.id,
                amount: user.monthlyCredits,
                type: 'purchase',
                description: 'Monthly subscription credits',
                stripeSessionId: invoice.id
              });

              console.log(`âœ… Refreshed ${user.monthlyCredits} credits for user ${user.id}`);
            }
          }
          break;
        }

        case 'invoice.payment_failed': {
          const invoice = event.data.object as Stripe.Invoice;
          console.log(`âŒ Invoice payment failed: ${invoice.id}`);
          // Handle failed recurring payment
          break;
        }

        case 'customer.subscription.updated':
        case 'customer.subscription.deleted': {
          const subscription = event.data.object as Stripe.Subscription;
          const customerId = subscription.customer as string;

          // Find user by Stripe customer ID
          const users = await storage.getAllUsers();
          const user = users.find(u => u.stripeCustomerId === customerId);

          if (user) {
            if (event.type === 'customer.subscription.deleted') {
              // Subscription cancelled
              await storage.updateUser(user.id, {
                tier: 'free',
                stripeSubscriptionId: null,
                monthlyCredits: 0
              });
              console.log(`âŒ Subscription cancelled for user ${user.id}`);
            } else {
              console.log(`âœ… Subscription updated for user ${user.id}`);
            }
          }
          break;
        }

        default:
          console.log(`âš ï¸ Unhandled event type: ${event.type}`);
      }
    } catch (error) {
      console.error(`Error processing webhook event ${event.type}:`, error);
      // Still return 200 to prevent retries for processing errors
    }

    // Return 200 immediately to acknowledge receipt
    res.sendStatus(200);
  }
);

export default router;

