                  <TabsTrigger value="content" className="text-xs xl:text-sm px-1 xl:px-3">Content</TabsTrigger>
                  <TabsTrigger value="strategy" className="text-xs xl:text-sm px-1 xl:px-3">Strategy</TabsTrigger>
