import { useQuery } from "@tanstack/react-query";
import { Clock, AlertTriangle } from "lucide-react";
import { Link } from "wouter";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

interface TrialStatus {
  isTrialUser: boolean;
  trialEndDate: string | null;
  daysRemaining: number;
  hasExpired: boolean;
}

export function TrialCountdown() {
  // Get trial status
  const { data: trialStatus } = useQuery<TrialStatus>({
    queryKey: ["/api/user/trial-status"],
    refetchInterval: 60000, // Refresh every minute
  });

  // Don't show if not a trial user
  if (!trialStatus?.isTrialUser) {
    return null;
  }

  const { daysRemaining, hasExpired } = trialStatus;

  // Determine styling based on days remaining
  const isUrgent = daysRemaining <= 3;
  const isWarning = daysRemaining <= 7;

  if (hasExpired) {
    return (
      <Alert variant="destructive" className="mb-4">
        <AlertTriangle className="h-4 w-4" />
        <AlertDescription className="flex items-center justify-between">
          <span className="font-medium">
            Your free trial has expired. Upgrade now to continue using all features.
          </span>
          <Link href="/pricing">
            <Button size="sm" variant="outline" className="ml-4" data-testid="button-upgrade-expired">
              Upgrade Now
            </Button>
          </Link>
        </AlertDescription>
      </Alert>
    );
  }

  return (
    <div className={`
      flex items-center justify-between p-4 mb-4 rounded-lg border
      ${isUrgent ? 'bg-red-50 dark:bg-red-950/20 border-red-200 dark:border-red-800' : 
        isWarning ? 'bg-yellow-50 dark:bg-yellow-950/20 border-yellow-200 dark:border-yellow-800' : 
        'bg-blue-50 dark:bg-blue-950/20 border-blue-200 dark:border-blue-800'}
    `} data-testid="trial-countdown">
      <div className="flex items-center gap-3">
        <Clock className={`h-5 w-5 ${
          isUrgent ? 'text-red-600 dark:text-red-400' : 
          isWarning ? 'text-yellow-600 dark:text-yellow-400' : 
          'text-blue-600 dark:text-blue-400'
        }`} />

        <div className="flex items-center gap-2">
          <span className={`font-medium ${
            isUrgent ? 'text-red-900 dark:text-red-100' : 
            isWarning ? 'text-yellow-900 dark:text-yellow-100' : 
            'text-blue-900 dark:text-blue-100'
          }`}>
            Free Trial:
          </span>

          <Badge 
            variant={isUrgent ? "destructive" : isWarning ? "secondary" : "default"}
            className="font-bold"
            data-testid="badge-days-remaining"
          >
            {daysRemaining === 0 ? 'Last Day!' : 
             daysRemaining === 1 ? '1 Day Left' : 
             `${daysRemaining} Days Left`}
          </Badge>

          {isUrgent && (
            <span className="text-sm text-red-600 dark:text-red-400 font-medium animate-pulse">
              âš ï¸ Expiring Soon
            </span>
          )}
        </div>
      </div>

      <div className="flex items-center gap-2">
        <Link href="/pricing">
          <Button 
            variant={isUrgent ? "destructive" : "outline"} 
            size="sm"
            data-testid="button-upgrade-trial"
          >
            Upgrade Now
          </Button>
        </Link>
      </div>
    </div>
  );
}

// Compact version for header/navbar
export function TrialCountdownCompact() {
  const { data: trialStatus } = useQuery<TrialStatus>({
    queryKey: ["/api/user/trial-status"],
    refetchInterval: 60000,
  });

  if (!trialStatus?.isTrialUser || trialStatus.hasExpired) {
    return null;
  }

  const { daysRemaining } = trialStatus;
  const isUrgent = daysRemaining <= 3;

  return (
    <Link href="/pricing">
      <Badge 
        variant={isUrgent ? "destructive" : "secondary"}
        className="cursor-pointer hover:opacity-80 transition-opacity"
        data-testid="badge-trial-compact"
      >
        <Clock className="h-3 w-3 mr-1" />
        {daysRemaining === 0 ? 'Trial ends today' : `${daysRemaining} days left`}
      </Badge>
    </Link>
  );
}

server/referralRoutes.ts.content:
import { Router, Request, Response } from "express";
import { storage } from "./storage";
import { requireAuth } from "./auth";
import { z } from "zod";

const router = Router();

// Helper function to get user ID from request
function getUserId(req: any): string | null {
  return req.session?.userId || req.user?.claims?.sub || req.user?.id || null;
}

// Get user's referral information
router.get("/me", requireAuth, async (req: Request, res: Response) => {
  try {
    const userId = getUserId(req);
    if (!userId) {
      return res.status(401).json({ message: "Authentication required" });
    }

    const user = await storage.getUser(userId);
    if (!user) {
      return res.status(404).json({ message: "User not found" });
    }

    // Generate referral code if user doesn't have one
    let userWithCode = user;
    if (!user.referralCode) {
      userWithCode = await storage.generateReferralCode(userId) || user;
    }

    // Get referral stats
    const stats = await storage.getReferralStats(userId);

    // Get recent referrals
    const recentReferrals = await storage.getReferralsByReferrer(userId);

    const baseUrl = process.env.BASE_URL || `${req.protocol}://${req.get('host')}`;
    const referralLink = `${baseUrl}/register?ref=${userWithCode.referralCode}`;

    res.json({
      referralCode: userWithCode.referralCode,
      referralLink,
      stats,
      recentReferrals: recentReferrals.slice(0, 10), // Limit to 10 recent referrals
    });
  } catch (error: any) {
    console.error("Error fetching referral data:", error);
    res.status(500).json({ message: "Failed to fetch referral data" });
  }
});

// Generate new referral code (if user doesn't have one)
router.post("/generate-code", requireAuth, async (req: Request, res: Response) => {
  try {
    const userId = getUserId(req);
    if (!userId) {
      return res.status(401).json({ message: "Authentication required" });
    }

    const user = await storage.generateReferralCode(userId);
    if (!user) {
      return res.status(404).json({ message: "User not found" });
    }

    const baseUrl = process.env.BASE_URL || `${req.protocol}://${req.get('host')}`;
    const referralLink = `${baseUrl}/register?ref=${user.referralCode}`;

    res.json({
      referralCode: user.referralCode,
      referralLink,
    });
  } catch (error: any) {
    console.error("Error generating referral code:", error);
    res.status(500).json({ message: "Failed to generate referral code" });
  }
});

// Validate referral code (used during registration)
const validateReferralSchema = z.object({
  referralCode: z.string().min(1, "Referral code is required"),
});

router.post("/validate", async (req: Request, res: Response) => {
  try {
    const { referralCode } = validateReferralSchema.parse(req.body);

    const referrer = await storage.getUserByReferralCode(referralCode);
    if (!referrer) {
      return res.status(404).json({ 
        message: "Invalid referral code",
        valid: false 
      });
    }

    res.json({
      valid: true,
      referrerName: referrer.fullName || referrer.username,
      referrerBusinessName: referrer.businessName,
    });
  } catch (error: any) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        message: "Invalid request", 
        errors: error.errors 
      });
    }
    console.error("Error validating referral code:", error);
    res.status(500).json({ message: "Failed to validate referral code" });
  }
});

// Process referral (called after successful registration)
const processReferralSchema = z.object({
  referralCode: z.string().min(1, "Referral code is required"),
  newUserId: z.string().min(1, "New user ID is required"),
});

router.post("/process", async (req: Request, res: Response) => {
  try {
    const { referralCode, newUserId } = processReferralSchema.parse(req.body);

    // Find referrer
    const referrer = await storage.getUserByReferralCode(referralCode);
    if (!referrer) {
      return res.status(404).json({ message: "Invalid referral code" });
    }

    // Prevent self-referrals
    if (referrer.id === newUserId) {
      return res.status(400).json({ message: "Cannot refer yourself" });
    }

    // Check if this user was already referred
    const existingReferral = await storage.getReferralsByUser(newUserId);
    if (existingReferral.length > 0) {
      return res.status(400).json({ message: "User already has a referrer" });
    }

    // Create referral record
    const referral = await storage.createReferral({
      referrerId: referrer.id,
      referredUserId: newUserId,
      referralCode,
      creditsEarned: 0,
      status: "pending",
    });

    // Award credits to referrer (100 credits)
    const referrerCredits = 100;
    await storage.updateUser(referrer.id, {
      credits: (referrer.credits || 0) + referrerCredits,
    });

    // Award welcome credits to new user (25 credits)
    const welcomeCredits = 25;
    await storage.updateUser(newUserId, {
      credits: welcomeCredits,
      referredBy: referralCode,
    });

    // Complete the referral
    await storage.completeReferral(referral.id, referrerCredits);

    // Create credit transactions for tracking
    await storage.createCreditTransaction({
      userId: referrer.id,
      amount: referrerCredits,
      type: "referral_bonus",
      description: "Referral bonus for successful referral",
    });

    await storage.createCreditTransaction({
      userId: newUserId,
      amount: welcomeCredits,
      type: "referral_welcome",
      description: "Welcome credits from referral signup",
    });

    res.json({
      message: "Referral processed successfully",
      referrerCreditsEarned: referrerCredits,
      welcomeCredits,
    });
  } catch (error: any) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        message: "Invalid request", 
        errors: error.errors 
      });
    }
    console.error("Error processing referral:", error);
    res.status(500).json({ message: "Failed to process referral" });
  }
});

// Get referral stats for admin/analytics
router.get("/stats", requireAuth, async (req: Request, res: Response) => {
  try {
    const userId = getUserId(req);
    if (!userId) {
      return res.status(401).json({ message: "Authentication required" });
    }

    const stats = await storage.getReferralStats(userId);
    res.json(stats);
  } catch (error: any) {
    console.error("Error fetching referral stats:", error);
    res.status(500).json({ message: "Failed to fetch referral stats" });
  }
});

export default router;

